INSERT INTO `tin_tuc` (`id_tin`, `tieu_de`, `danh_muc_id`, `hinh_anh`, `noi_dung`, `luot_xem`, `id_nguoi_dang`, `hien_thi`) VALUES
(3, 'Đến nhà cổ Bình Thủy – tham quan ngôi nhà cổ đẹp nhất xứ Tây Đô', 1, '1540407463.nha-co-binh-thuy1-min_1540371371.jpg', '<p>Cuối thế kỉ 18. V&ugrave;ng ph&iacute;a T&acirc;y Nam v&agrave;m s&ocirc;ng Cần Thơ ph&ugrave; sa nước ngọt tưới m&aacute;t quanh năm. Đ&acirc;y cũng l&agrave; nơi thu h&uacute;t nhiều người đổ về chọn đất an cư lập nghiệp. Trong đ&oacute; c&oacute; gia tộc Họ Dương.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Gia tộc Họ Dương đến đất Nam Bộ lập nghiệp, đ&atilde; sống qua nhiều thế hệ tại đ&acirc;y. Ở thế hệ thứ 3, &ocirc;ng Dương Văn Vị cũng l&agrave; người kh&aacute; gi&agrave;u c&oacute; của d&ograve;ng họ, đ&atilde; chọn v&ugrave;ng đất gần v&agrave;m rạch B&igrave;nh Thủy l&agrave;m nơi dựng nh&agrave;.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy1-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Nh&agrave; cổ B&igrave;nh Thủy nh&igrave;n từ ch&iacute;nh diện</em></p>\r\n\r\n<p><br />\r\nL&uacute;c mới đầu, nh&agrave; được &ocirc;ng Vị dựng bằng gỗ v&agrave; lợp ng&oacute;i, d&ugrave;ng để thờ tổ ti&ecirc;n. M&atilde;i đến 30 năm sau đ&oacute; mới bắt đầu được t&ocirc;n tạo lại. Sau khi &ocirc;ng Vị mất, con trai &uacute;t của &ocirc;ng l&agrave; Dương Chấn Kỷ (l&uacute;c đ&oacute; l&agrave; một điền chủ rất gi&agrave;u c&oacute;) tiếp tục x&acirc;y dựng v&agrave; ho&agrave;n thiện ng&ocirc;i nh&agrave; v&agrave;o năm 1911.<br />\r\n&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Kỷ l&agrave; một thương gia rất th&iacute;ch t&igrave;m t&ograve;i c&aacute;i mới lạ, nhất l&agrave; về kiến tr&uacute;c v&agrave; nghệ thuật. Thời bấy giờ đang thịnh h&agrave;nh tr&agrave;o lưu T&acirc;y phương, &ocirc;ng liền cho người x&acirc;y dựng ng&ocirc;i nh&agrave; theo kiến tr&uacute;c ch&acirc;u &Acirc;u.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Những năm 1980, hậu duệ đời thứ 5 của gia tộc họ Dương l&agrave; &ocirc;ng Dương Văn Ng&ocirc;n c&oacute; th&uacute; chơi hoa kiểng. &Ocirc;ng sưu tầm kh&aacute; nhiều giống lan qu&yacute; v&agrave; hay tổ chức giao lưu, chơi lan. Do đ&oacute;, thời n&agrave;y ng&ocirc;i nh&agrave; c&ograve;n c&oacute; thể l&agrave; &ldquo;Vườn lan B&igrave;nh Thủy&rdquo;<br />\r\n&nbsp;</p>\r\n\r\n<h3>Du lịch Cần Thơ nhớ gh&eacute; thăm nh&agrave; cổ B&igrave;nh Thủy &ndash; nh&agrave; cổ đẹp nhất xứ T&acirc;y Đ&ocirc;<br />\r\n&nbsp;</h3>\r\n\r\n<p>Trong số 70 ng&ocirc;i nh&agrave; cổ ở Cần thơ th&igrave; nh&agrave; cổ B&igrave;nh Thủy l&agrave; đẹp nhất. Ng&ocirc;i nh&agrave; cho đến ngay vẫn giữ nguy&ecirc;n được sự bề thế v&agrave; đẹp đẽ như thời điểm mới x&acirc;y do được tr&ugrave;ng tu kĩ lưỡng. Thoạt nh&igrave;n kiến tr&uacute;c c&oacute; vẻ cổ k&iacute;nh, trang nghi&ecirc;m, nhưng đường n&eacute;t đi&ecirc;u khắc lại to&aacute;t n&ecirc;n vẻ ph&oacute;ng kho&aacute;ng như t&iacute;nh c&aacute;ch con người miền T&acirc;y.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Cổng r&agrave;o l&agrave;m bằng b&ecirc; t&ocirc;ng v&agrave; sắt ki&ecirc;n cố theo kiểu dinh thự Ph&aacute;p. Cổng phụ được x&acirc;y theo kiến tr&uacute;c &Aacute; Đ&ocirc;ng với 4 cột tr&ograve;n vững chắc.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-7-min.jpg\" style=\"height:532px; width:800px\" /></p>\r\n\r\n<p><em>Cổng lớn được x&acirc;y dựng như cổng một biệt thự thời nay<br />\r\n<img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-9-min.jpg\" style=\"height:533px; width:800px\" /></em></p>\r\n\r\n<p><em>Cổng phụ mang n&eacute;t kiến tr&uacute;c phương Đ&ocirc;ng</em></p>\r\n\r\n<p><br />\r\nNh&agrave; cổ nằm tr&ecirc;n một diện t&iacute;ch lớn khoảng 8.000 m&eacute;t vu&ocirc;ng, bao gồm cả vườn tược. Ri&ecirc;ng khu nh&agrave; được bao bọc bởi c&acirc;y cảnh được cắt tỉa chu đ&aacute;o, hoa nở rộ bốn m&ugrave;a. Khiến cho ng&ocirc;i nh&agrave; vừa mang n&eacute;t ho&agrave;i cổ k&iacute;nh đ&aacute;o vừa sống động, thơ mộng v&agrave; tươi mới.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy2-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Xung quanh nh&agrave; trồng kh&aacute; nhiều hoa v&agrave; c&acirc;y cảnh được cắt tỉa chu đ&aacute;o</em><br />\r\n<br />\r\n<br />\r\nTrước khi v&agrave;o được b&ecirc;n trong, du kh&aacute;ch sẽ nh&igrave;n thấy một cầu thang c&aacute;ch điệu h&igrave;nh c&aacute;nh cung tao nh&atilde;, trang tr&iacute; hoa văn tinh tế. Cầu thang n&agrave;y l&agrave; điểm nối từ khoảng s&acirc;n đến t&ograve;a nh&agrave;. Nh&agrave; c&oacute; 5 gian 2 m&aacute;i, chia th&agrave;nh nh&agrave; trước, giữa v&agrave; sau. Nền nh&agrave; được l&aacute;t gạch nhập khẩu từ Ph&aacute;, đ&oacute;ng trần platfond, tr&ecirc;n c&aacute;c cửa lớn đều được chạm khắc rất tinh tế.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-15-min.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><em>Cầu thang xoắn nối liền nh&agrave; với s&acirc;n vườn</em></p>\r\n\r\n<p><br />\r\nB&ecirc;n trong, nh&agrave; cổ B&igrave;nh Thủy nổi bật với 3 bộ b&agrave;n ghế cổ đẹp v&agrave; 1 sập gụ được trang tr&iacute; khảm ngọ trai tinh xảo, đường n&eacute;t hoa văn chạm khắc mềm mại, xinh đẹp. Ở gian giữa b&agrave;y b&agrave;n thờ tổ ti&ecirc;n đ&uacute;ng kiểu của người Nam Bộ. Gian thờ được sơn son thếp v&agrave;ng, giường thờ, sập gụ được chạm khắc tinh xảo Mai, lan, c&uacute;c, tr&uacute;c, Ph&uacute;c &ndash; Lộc &ndash; Thọ, long, l&acirc;n, quy, phụng&hellip;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy4-min.jpg\" style=\"height:531px; width:800px\" /></p>\r\n\r\n<p><em>Bộ b&agrave;n ghế cổ<br />\r\n<br />\r\n<strong><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-6-min.jpg\" style=\"height:534px; width:800px\" /></strong></em></p>\r\n\r\n<p><br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://tour.dulichvietnam.com.vn/du-lich-trong-nuoc/du-lich-mien-tay.html/63\"><strong>Tour lịch miền T&acirc;y gi&aacute; rẻ</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Nh&agrave; cổ B&igrave;nh Thủy</strong>&nbsp;c&ograve;n c&oacute; cả một kho đồ cổ đ&atilde; được g&igrave;n giữ suốt nhiều thế kỉ qua. Trong đ&oacute; c&oacute; hai bộ b&agrave;n ghế xuất xứ từ V&acirc;n Nam, Trung Quốc, mặt b&agrave;n l&agrave;m bằng đ&aacute; cẩm thạch, v&acirc;n xanh; bộ salon kiểu Ph&aacute;p đời Louis XV, cặp đ&egrave;n treo thế kỉ 19&hellip; Những hiện vật n&agrave;y cho ta biết được mức độ gi&agrave;u c&oacute; v&agrave; th&uacute; chơi đồ cao cấp của mấy đời gia đ&igrave;nh họ Dương đất B&igrave;nh Thủy, Cần Thơ.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-16-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Đồ cổ trong nh&agrave; cổ B&igrave;nh Thủy vẫn c&ograve;n đến ng&agrave;y nay</em><br />\r\n&nbsp;</p>\r\n\r\n<p>Khu vườn xung quanh nh&agrave; được trang tr&iacute; h&ograve;n non bộ, c&acirc;y cảnh được cắt tỉa xinh đẹp, tinh tế.&nbsp;Nh&agrave; cổ B&igrave;nh Thủy với kiến tr&uacute;c ho&agrave;i cổ độc đ&aacute;o đ&atilde; trở th&agrave;nh địa điểm quay phim cho c&aacute;c bộ phim như &ldquo;Người đẹp T&acirc;y Đ&ocirc;&rdquo;, &ldquo;Những nẻo đường xa&rdquo;, &ldquo;Nợ đời&rdquo;, &ldquo;Người t&igrave;nh&rdquo;.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Năm 2009, nh&agrave; cổ B&igrave;nh Thủy đ&atilde; được Bộ Văn h&oacute;a - Thể thao &amp; Du lịch c&ocirc;ng nhận l&agrave; Di t&iacute;ch kiến tr&uacute;c nghệ thuật cấp quốc gia.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Nh&agrave; cổ B&igrave;nh Thủy</strong>&nbsp;hiện nay l&agrave; một trong những điểm tham quan thu h&uacute;t du kh&aacute;ch khi du lịch Cần Thơ. Nh&agrave; cổ mở cửa tiếp đ&oacute;n du kh&aacute;ch hằng ng&agrave;y. Đến đ&acirc;y l&agrave; đến với kh&ocirc;ng gian thơ mộng, hơi ho&agrave;i cổ nhưng cũng rất ph&oacute;ng kho&aacute;ng. Giới trẻ cũng thường t&igrave;m đến đ&acirc;y để chụp ảnh lưu niệm bởi khung cảnh nơi đ&acirc;y l&ecirc;n ảnh rất đẹp.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-8-min.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><br />\r\nNếu c&oacute; dịp&nbsp;<strong>du lịch Cần Thơ</strong>&nbsp;hoặc&nbsp;<strong>du lịch miền T&acirc;y</strong>, đừng qu&ecirc;n gh&eacute; thăm nh&agrave; cổ B&igrave;nh Thủy &ndash; ng&ocirc;i nh&agrave; cổ đẹp nhất xứ T&acirc;y Đ&ocirc;. Du kh&aacute;ch c&oacute; thể đăng k&iacute; tour du lịch để được thiết kế lộ tr&igrave;nh tham quan l&iacute; tưởng nhất, tiết kiệm thời gian v&agrave; chi ph&iacute; nhất.&nbsp;</p>\r\n\r\n<p><strong><em>Cẩm Luyến</em></strong></p>\r\n', 3, 0, 1),
(4, 'Khám phá vẻ đẹp Côn Đảo dưới sắc trời mùa thu', 1, '1540407610.con-dao-20-min_1539855807.jpg', '<p><strong>C&ocirc;n Đảo</strong>&nbsp;(B&agrave; Rịa &ndash; Vũng T&agrave;u) l&agrave; một quần đảo bao gồm 16 h&ograve;n đảo lớn nhỏ, diện t&iacute;ch khoảng 76 km vu&ocirc;ng. C&ocirc;n Đảo nằm c&aacute;ch Vũng T&agrave;u 97 hải l&yacute; v&agrave; c&aacute;ch s&ocirc;ng Hậu 45 hải l&yacute;.<br />\r\nTầm th&aacute;ng 10 - 11 l&agrave; thời điểm C&ocirc;n Đảo bước v&agrave;o m&ugrave;a kh&ocirc;, cũng l&agrave; l&uacute;c đẹp nhất khi trời v&agrave;o thu. Đ&acirc;y ch&iacute;nh l&agrave; thời điểm l&iacute; tưởng để đi du lịch C&ocirc;n Đảo. Ngo&agrave;i biển &iacute;t nhiều sẽ c&oacute; s&oacute;ng lớn, nhưng điều n&agrave;y chẳng g&acirc;y ảnh hưởng g&igrave; mấy, nếu bạn đến c&aacute;c h&ograve;n đảo v&agrave; ở trong b&atilde;i biển.&nbsp;<br />\r\nC&ocirc;n Đảo qua thời gian vẫn giữ được n&eacute;t đẹp thi&ecirc;n nhi&ecirc;n hoang sơ, dịu d&agrave;ng. Khi &aacute;nh nắng trời thu chiếu rọi, nước biển chuyển sang ngọc b&iacute;ch đẹp đẽ. B&atilde;i c&aacute;t trải d&agrave;i mịn m&agrave;ng, b&atilde;i tắm sạch sẽ, nước trong veo. &Aacute;nh nắng m&ugrave;a thu cũng kh&ocirc;ng qu&aacute; gắt như nắng hạ, du kh&aacute;ch c&oacute; thể tắm biển tại b&atilde;i biển Đầm Trầu.&nbsp;</p>\r\n\r\n<p>Người ta đến C&ocirc;n Đảo đầu ti&ecirc;n l&agrave; để thăm Nh&agrave; t&ugrave; C&ocirc;n Đảo. Nơi địa ngục trần gian n&agrave;y đ&atilde; từng giam giữ v&agrave; tra tấn d&atilde; man những người t&ugrave; c&aacute;ch mạng, người y&ecirc;u nước. Ngo&agrave;i nh&agrave; t&ugrave; C&ocirc;n Đảo c&ograve;n c&oacute; nghĩa trang H&agrave;ng Dương cũng thu h&uacute;t nhiều du kh&aacute;ch đến tưởng nhớ v&agrave; b&agrave;y tỏ l&ograve;ng biết ơn với c&aacute;c chiến sĩ c&aacute;ch mạng ng&agrave;y xưa.&nbsp;</p>\r\n\r\n<p>V&agrave;o m&ugrave;a n&agrave;y, nếu muốn c&oacute; một chuyến đi tham quan nhẹ nh&agrave;ng cũng đừng ngại chọn C&ocirc;n Đảo. Sắc trời m&ugrave;a thu tạo ra cho nơi đ&acirc;y khung cảnh đẹp tuyệt điệu. Du kh&aacute;ch c&oacute; thể tham quan c&aacute;c địa điểm như Dinh Ch&uacute;a Đảo, Hệ thống chuồng cọp, Miếu b&agrave; Phi Yến, B&atilde;i Nh&aacute;t, Cảng Bến Đầm, H&ograve;n B&agrave;, Ch&ugrave;a N&uacute;i Một, B&atilde;i Suối N&oacute;ng, H&ograve;n Bảy Cạnh, H&ograve;n T&agrave;i, H&ograve;n Cau, H&ograve;n Tre Lớn&hellip;</p>\r\n\r\n<p>Nếu đ&atilde; du lịch đến C&ocirc;n Đảo v&agrave;o m&ugrave;a n&agrave;y, h&atilde;y thử trải nghiệm lặn ngắm san h&ocirc; một lần nh&eacute;. Cũng đừng qu&ecirc;n thưởng thức qua những m&oacute;n ăn hấp dẫn của nơi n&agrave;y. C&aacute; thu một nắng, ch&aacute;o h&agrave;u, cua mặt trăng, mắm nhum, t&ocirc;m h&ugrave;m đỏ&hellip;đều l&agrave; đặc sản C&ocirc;n Đảo độc đ&aacute;o, hấp dẫn</p>\r\n\r\n<p>Kh&aacute;c với c&aacute;c khu du lịch đ&ocirc;ng đ&uacute;c, ồn &agrave;o kh&aacute;c,&nbsp;<strong>C&ocirc;n Đảo</strong>&nbsp;cho đến nay vẫn giữ được vẹn nguy&ecirc;n n&eacute;t đẹp tự nhi&ecirc;n đ&aacute;ng tr&acirc;n qu&yacute;. Người d&acirc;n nơi đ&acirc;y nhiệt t&igrave;nh, th&acirc;n thiện, ch&acirc;n chất, dễ mến. Kh&ocirc;ng kh&iacute; th&igrave; lu&ocirc;n thanh b&igrave;nh y&ecirc;n ả. Nếu như đ&atilde; qu&aacute; mỏi mệt với những bon chen nơi th&agrave;nh phố, h&atilde;y đến C&ocirc;n Đảo m&ugrave;a thu để nghỉ ngơi nh&eacute;</p>\r\n', 4, 0, 1),
(5, 'Khu nghỉ dưỡng thân thiện với thiên nhiên nhất châu Á', 1, '1540412990.22-10-201857-740388268-w500-5136-1540206579_180x108.png', '<p>Đến nay, InterContinental Danang Sun Peninsula Resort trở th&agrave;nh một minh chứng điển h&igrave;nh cho triết l&yacute; kiến tr&uacute;c n&agrave;y của &ocirc;ng.<strong>Mỗi ng&agrave;y một... xanh hơn</strong></p>\r\n\r\n<p>Bậc thầy kiến tr&uacute;c Bill Bensley từng chia sẻ cảm gi&aacute;c &ldquo;giống như được về nh&agrave;&rdquo; mỗi khi trở lại khu nghỉ dưỡng b&ecirc;n b&aacute;n đảo Sơn Tr&agrave;. Điều &ocirc;ng t&acirc;m đắc nhất ở &ldquo;ng&ocirc;i nh&agrave;&rdquo; n&agrave;y l&agrave; n&oacute; mỗi ng&agrave;y một xanh hơn.</p>\r\n\r\n<p>&ldquo;Ng&agrave;y ấy, t&ocirc;i đ&atilde; t&igrave;m c&aacute;ch hạn chế tối đa việc động chạm m&ocirc;i trường trong qu&aacute; tr&igrave;nh thi c&ocirc;ng. Giờ đ&acirc;y, mọi thứ đ&atilde; xanh tươi trở lại v&agrave; thậm ch&iacute; tr&ocirc;ng c&ograve;n tự nhi&ecirc;n hơn trước kia. T&ocirc;i thật sự rất y&ecirc;u nơi n&agrave;y, tất cả những g&igrave; t&ocirc;i l&agrave;m l&agrave; cố gắng x&acirc;y n&ecirc;n một c&ocirc;ng tr&igrave;nh cho ph&eacute;p mọi người sống c&ugrave;ng với thi&ecirc;n nhi&ecirc;n&rdquo;, Bill Bensley chia sẻ.</p>\r\n\r\n<p>Thật vậy, nối từ tầng Biển cả đến tầng Thi&ecirc;n đường của khu nghỉ dưỡng l&agrave; chiếc cầu thang gỗ uốn lượn quanh co như mọc giữa rừng gi&agrave;. Bill Bensley đ&atilde; thống nhất với chủ đầu tư khu nghỉ dưỡng n&agrave;y - Tập đo&agrave;n Sun Group, rằng tr&ecirc;n con đường đi của c&aacute;i cầu thang gỗ đ&oacute;, kh&ocirc;ng c&oacute; chuyện chặt c&acirc;y l&agrave;m đường, chỉ c&oacute; thể l&agrave; uốn đường để nhường c&acirc;y. Nguy&ecirc;n tắc đ&oacute; được t&ocirc;n trọng tối đa, từ l&uacute;c x&acirc;y dựng đến khi chỉnh trang để đ&oacute;n đo&agrave;n thượng kh&aacute;ch APEC năm 2017.</p>\r\n\r\n<p>Cũng từ cầu thang gỗ, du kh&aacute;ch sẽ thấy những c&aacute;nh rừng nguy&ecirc;n sinh, ngọn đồi huyền thoại, b&atilde;i c&aacute;t trắng muốt ven theo đại dương xanh ngọc. Tất cả c&ograve;n đ&oacute;, vẻ đẹp nguy&ecirc;n vẹn của thi&ecirc;n tạo.</p>\r\n\r\n<p>Mỗi căn biệt thự trong khu nghỉ dưỡng được đặt men theo c&aacute;c ngọn đồi, h&agrave;i ho&agrave; với n&uacute;i rừng. Du kh&aacute;ch vừa c&oacute; thể đứng ở ban c&ocirc;ng ngắm nh&igrave;n khung cảnh kỳ vĩ của b&aacute;n đảo Sơn Tr&agrave;, vừa nghe tiếng g&aacute;y của g&agrave; mặt đỏ, xem những ch&uacute; s&oacute;c nhảy nh&oacute;t, v&agrave; theo d&otilde;i lo&agrave;i voọc ch&agrave; v&aacute; ch&acirc;n n&acirc;u qu&yacute; hiếm t&igrave;m kiếm l&aacute; non tr&ecirc;n t&aacute;n c&acirc;y gần đ&oacute;. &ldquo;Nghỉ dưỡng giữa rừng nguy&ecirc;n sinh&rdquo;, đ&oacute; l&agrave; trải nghiệm đang chờ đ&oacute;n du kh&aacute;ch tại InterContinental Danang Sun Peninsula Resort.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/10/22/22-10-201856-276787815-w500-6557-1540206579.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Voọc ch&agrave; v&aacute; ch&acirc;n n&acirc;u nổi tiếng ở b&aacute;n đảo Sơn Tr&agrave;.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Năm 2018, &ldquo;khu nghỉ dưỡng sang trọng bậc nhất thế giới&rdquo; n&agrave;y lại tiếp tục được World Travel Awards khu vực ch&acirc;u &Aacute;, ch&acirc;u Đại Dương trao tặng c&ugrave;ng l&uacute;c 5 giải thưởng, trong đ&oacute; c&oacute; danh hiệu &ldquo;Khu nghỉ dưỡng th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n nhất ch&acirc;u &Aacute;&quot;.</p>\r\n\r\n<p><strong>Tầm nh&igrave;n cho tương lai</strong></p>\r\n\r\n<p>Khu nghỉ c&oacute; ri&ecirc;ng một nh&agrave; động vật học l&agrave; điều hiếm c&oacute; kh&ocirc;ng chỉ ở Việt Nam m&agrave; tr&ecirc;n cả thế giới. Tuy nhi&ecirc;n, đ&oacute; l&agrave; điều đ&oacute; đ&atilde; trở th&agrave;nh hiện thực khi James Morrison l&agrave; nh&agrave; động vật học của ri&ecirc;ng InterContinental Danang Sun Peninsula Resort.</p>\r\n\r\n<p>James Morrison bắt đầu l&agrave;m việc trong lĩnh vực bảo tồn từ năm 2011 tại Vườn quốc gia Kruger, Nam Phi. Đam m&ecirc; bảo tồn thi&ecirc;n nhi&ecirc;n, &ocirc;ng tới Việt Nam để giảng dạy v&agrave; nghi&ecirc;n cứu m&ocirc;i trường tại Đại học Quốc gia H&agrave; Nội. Sau nhiều h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; v&agrave; nghi&ecirc;n cứu c&aacute;c c&ocirc;ng vi&ecirc;n quốc gia ở Myanmar, Bangladesh, Nepal... &ocirc;ng quyết định trở về Việt Nam, trong vai tr&ograve; Trưởng ph&ograve;ng Bảo tồn, C&ocirc;ng vi&ecirc;n quốc gia P&ugrave; M&aacute;t cho tổ chức Bảo vệ Động vật hoang d&atilde; của Việt Nam v&agrave; hiện l&agrave;m việc cho resort ở Sơn Tr&agrave;.</p>\r\n\r\n<p>&ldquo;Chỉ c&oacute; một v&agrave;i nơi ở Việt Nam sở hữu đa dạng sinh học phong ph&uacute; như b&aacute;n đảo Sơn Tr&agrave;&rdquo;, James chia sẻ, &ldquo;Đ&oacute; l&agrave; l&yacute; do việc bảo vệ hệ sinh th&aacute;i l&agrave; rất quan trọng&quot;. Theo đ&oacute;, &ocirc;ng đảm nhận việc nghi&ecirc;n cứu, tư vấn v&agrave; chia sẻ kiến thức về động vật hoang d&atilde; của khu resort v&agrave; c&aacute;c hệ sinh th&aacute;i xung quanh, để giảm thiểu t&aacute;c động đến m&ocirc;i trường tự nhi&ecirc;n bằng c&aacute;ch thực hiện c&aacute;c hoạt động kinh doanh một c&aacute;ch c&oacute; tr&aacute;ch nhiệm. &quot;C&ugrave;ng nhau, ch&uacute;ng ta c&oacute; thể x&acirc;y dựng sự ph&aacute;t triển bền vững kh&ocirc;ng chỉ cho khu dưỡng m&agrave; c&ograve;n cho to&agrave;n bộ v&ugrave;ng sinh th&aacute;i - đ&oacute; l&agrave; tầm nh&igrave;n cho tương lai&rdquo;, James khẳng định.</p>\r\n\r\n<p>Để từng bước thực hiện sứ mệnh của m&igrave;nh tại khu nghỉ dưỡng, James Morrison v&agrave; đồng sự ở resort đ&atilde; c&oacute; một loạt hoạt động, từ n&acirc;ng cấp hệ thống đ&egrave;n LED để giảm đến 75% năng lượng ti&ecirc;u thụ nhằm giảm lượng kh&iacute; thải ra m&ocirc;i trường, đến d&agrave;nh 100% lợi nhuận b&aacute;n đồ lưu niệm tại khu nghỉ được quy&ecirc;n g&oacute;p cho một tổ chức phi ch&iacute;nh phủ nhằm bảo vệ voọc ch&acirc;n n&acirc;u.</p>\r\n\r\n<p>Du kh&aacute;ch đến đ&acirc;y cũng sẽ được James Morrison v&agrave; c&aacute;c nh&acirc;n vi&ecirc;n chia sẻ kiến thức về động vật hoang d&atilde;, như một c&aacute;ch truyền th&ocirc;ng về &yacute; thức bảo vệ m&ocirc;i trường sống. Theo chủ đầu tư, c&aacute;c hoạt động nhằm từng bước đưa resort trở th&agrave;nh một biểu tượng mới của nghỉ dưỡng xanh, xứng đ&aacute;ng với danh hiệu Khu nghỉ dưỡng th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n nhất ch&acirc;u &Aacute; 2018.</p>\r\n', 7, 0, 1),
(6, 'Độc đáo lễ hội Ok Om Bok của người Khmer', 1, '1540414790.le-hoi-ok-om-bok-4-min_1540290643.jpg', '<p>Truyền thuyết&nbsp;<strong>người Khmer&nbsp;</strong>cho rằng thỏ ch&iacute;nh l&agrave; tiền kiếp của Phật Th&iacute;ch Ca. Xưa kia sống ở bờ s&ocirc;ng Hằng. Một h&ocirc;m, thần Sakah giả l&agrave;m ăn xin thử l&ograve;ng thỏ. Kh&ocirc;ng c&oacute; g&igrave; cho thần n&ecirc;n thỏ đốt lửa, tự nhảy v&agrave;o để mời người ăn thịt m&igrave;nh. Lửa bỗng dưng tắt v&agrave; người ăn xin biến mất, thần Sakah hiện ra. Thần muốn khen ngợi l&ograve;ng hy sinh của thỏ n&ecirc;n vẽ h&igrave;nh thỏ l&ecirc;n Mặt trăng. Trong t&iacute;n ngưỡng của người Khmer, lễ c&uacute;ng trăng l&agrave; để tưởng nhớ tiền kiếp của đức Phật Th&iacute;ch Ca. Đồng thời cũng l&agrave; dịp tạ ơn thần mặt trăng đ&atilde; bảo vệ m&ugrave;a m&agrave;ng suốt một năm qua.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-2-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<h2><br />\r\nLễ c&uacute;ng mặt trăng Ok Om Bok</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&agrave;i ng&agrave;y trước ng&agrave;y rằm th&aacute;ng 10 &acirc;m lịch, người Khmer đ&atilde; bắt đầu chuẩn bị mọi thứ để l&agrave;m lễ. Đến ng&agrave;y, người Khmer sẽ b&agrave;y biện b&agrave;n c&uacute;ng trong s&acirc;n nh&agrave;, s&acirc;n ch&ugrave;a để quan s&aacute;t mặt trăng.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Trước khi trăng l&ecirc;n, người ta cắm hai thanh tre l&ecirc;n mặt đất, c&aacute;ch nhau 3 m&eacute;t, g&aacute;c ngang một thanh tre kh&aacute;c như h&igrave;nh c&aacute;i cổng. Đặt một c&aacute;i b&agrave;n dưới cổng đ&oacute;. Tr&ecirc;n b&agrave;n b&agrave;y cốm dẹp, khoai lan, khoai m&ocirc;n, dừa tươi, chuối, b&aacute;nh kẹo, tr&agrave;&hellip; Cứ mỗi lần r&oacute;t tr&agrave; l&agrave; họ lại một lần khấn v&aacute;i đức Phật.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-5-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>M&acirc;m c&uacute;ng đầy đủ mọi thứ của người Khmer</em><br />\r\n<br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://www.dulichvietnam.com.vn/tra-vinh/\" target=\"_blank\"><strong>Cẩm nang du lịch Tr&agrave; Vinh</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p>Khi trăng l&ecirc;n đỉnh đầu, người lớn tuổi được người Khmer tin tưởng sẽ đại diện ra c&uacute;ng tạ mặt trăng. R&oacute;t tr&agrave;, thắp nhang rồi khấn nguyện. Trong qu&aacute; tr&igrave;nh c&uacute;ng, trẻ con sẽ tập trung lại chờ được ăn b&aacute;nh. C&uacute;ng xong, người ta sẽ ph&aacute;t b&aacute;nh cho c&aacute;c em nhỏ.&nbsp; Người lớn sẽ đ&uacute;t b&aacute;nh, kẹo cho c&aacute;c em cho đến khi đầy miệng, sau đ&oacute; vỗ lưng c&aacute;c em v&agrave; hỏi c&aacute;c em về ước muốn của m&igrave;nh. Người khmer tin rằng, ước muốn của c&aacute;c em nhỏ cũng l&agrave; động lực của người lớn trong năm tới.<br />\r\n<br />\r\n<img alt=\"lễ hội Ok Om Bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-1-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Người l&agrave;m lễ đ&uacute;t đồ ăn cho trẻ em v&agrave; hỏi c&aacute;c em về ước muốn của m&igrave;nh</em><br />\r\n&nbsp;</p>\r\n\r\n<h2>Trong dịp lễ n&agrave;y c&oacute; 2 sự kiện l&agrave; thả đ&egrave;n gi&oacute; v&agrave; đua ghe Ngo</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thả đ&egrave;n gi&oacute;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&egrave;n gi&oacute; được l&agrave;m từ tre, giấy quyến v&agrave; d&acirc;y kẽm. Đ&aacute;y đ&egrave;n để trống c&oacute; lớp g&ograve;n b&ecirc;n trong. L&uacute;c thả th&igrave; đốt lớp g&ograve;n, nhiều người c&ugrave;ng n&acirc;ng đ&egrave;n l&ecirc;n cao. Nhiệt độ l&agrave;m giấy căng phồng, tạo ra lực đẩy để đ&egrave;n bay l&ecirc;n. Đ&egrave;n gi&oacute; bay l&ecirc;n trời mang theo nguyện ước của người Khmer.&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-4-min.jpg\" style=\"height:500px; width:800px\" /><br />\r\n&nbsp;</p>\r\n\r\n<h3>Đua ghe Ngo</h3>\r\n\r\n<p><br />\r\nĐua ghe Ngo l&agrave; sự kiện được chờ đợi thứ hai của lễ hội Ok Om Bok. Ghe Ngo l&agrave; t&ecirc;n của một loại thuyền độc mộc. Ghe d&agrave;i 25 - 30 m&eacute;t, rộng khoảng 1 - 1,4 m&eacute;t. Tr&ecirc;n ghe đ&oacute;ng nhiều thanh gỗ g&aacute;c ngang vừa cho hai tay bơi ngồi theo cặp. Mỗi ghe đua c&oacute; thể chở từ 40 - 60 người ch&egrave;o. Người lớn tuổi, c&oacute; chức sắc, được nể trọng sẽ điều khiển nhịp ch&egrave;o v&agrave; ngồi ở mũi ghe. Người cầm l&aacute;i ngồi ở đu&ocirc;i ghe. Mỗi ghe Ngo t&ocirc; vẽ những biểu tượng như cọp, rồng, sư tử&hellip; Trước khi đua, ghe Ngo lu&ocirc;n được tu bổ, hạ thủy tập dượt, l&agrave;m lễ c&uacute;ng b&aacute;i trước rất kĩ c&agrave;ng. C&uacute;ng b&aacute;i trước l&agrave; để cầu c&aacute;c vị thần ph&ugrave; hộ cho qu&aacute; tr&igrave;nh diễn ra cuộc đua được thuận lợi, an to&agrave;n.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Đến trưa ng&agrave;y rằm, người d&acirc;n ra hai b&ecirc;n bờ s&ocirc;ng h&aacute;o hức chờ đợi cuộc đua bắt đầu. Sau hồi c&ograve;i hiệu lệnh xuất ph&aacute;t, từng đội ghe đua nhau ch&egrave;o trong tiếng trống, tiếng loa, tiếng reo h&ograve; của người cỗ vũ.&nbsp;<br />\r\n<br />\r\n<img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Cuộc đua ghe Ngo v&igrave; danh dự của mỗi phum sốc Khmer diễn ra s&ocirc;i nổi</em><br />\r\n<br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://tour.dulichvietnam.com.vn/du-lich-trong-nuoc/du-lich-mien-tay.html/63\" target=\"_blank\"><strong>Tour du lịch miền T&acirc;y</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p>Ghe Ngo l&agrave; một sản phẩm văn h&oacute;a độc đ&aacute;o của người Khmer Nam Bộ. Ghe cũng l&agrave; biểu tượng của sự no ấm, sung t&uacute;c, l&agrave; đại diện cho mỗi phum s&oacute;c, x&atilde;, huyện. Do đ&oacute;, cuộc đua ghe thường diễn ra quyết liệt kh&ocirc;ng chỉ v&igrave; giải thưởng, m&agrave; c&ograve;n v&igrave; danh dự v&agrave; vinh quang của phum s&oacute;c. Đua ghe Ngo cũng l&agrave; m&ocirc;n thể thao hấp dẫn v&agrave; h&agrave;o hứng.&nbsp;<br />\r\n<br />\r\nNếu du kh&aacute;ch&nbsp;<strong>du lịch miền T&acirc;y Nam Bộ</strong>&nbsp;trong dịp n&agrave;y, được h&ograve;a m&igrave;nh v&agrave;o lễ hội Ok Om Bok, sẽ cảm thấy trải nghiệm n&agrave;y v&ocirc; c&ugrave;ng đ&aacute;ng gi&aacute;. Kh&ocirc;ng kh&iacute; rộn r&agrave;ng, vui nhộn của lễ hỗi sẽ khiến bạn qu&ecirc;n hết mọi phiền muộn, h&ograve;a m&igrave;nh v&agrave;o lễ hội mỗi năm chỉ c&oacute; một lần n&agrave;y.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Lễ hội Ok Om Bok&nbsp;</strong>mỗi năm chỉ c&oacute; một lần, mang &yacute; nghĩa v&ocirc; c&ugrave;ng s&acirc;u sắc trong đời sống tinh thần người Khmer. Thể hiện kh&aacute;t vọng v&agrave; t&acirc;m hồn người Khmer, thể hiện sự biết ơn của người Khmer với đấng bề tr&ecirc;n. Việc giữ truyền thống tổ chức lễ hội Ok Om Bok cũng g&oacute;p phần bảo tồn, ph&aacute;t huy n&eacute;t đẹp văn h&oacute;a gi&agrave;u t&iacute;nh nh&acirc;n văn của người Khmer Nam Bộ. Kh&ocirc;ng chỉ vậy, lễ hội c&ograve;n l&agrave; một sản phẩm du lịch thu h&uacute;t du kh&aacute;ch đến với v&ugrave;ng đất n&agrave;y.&nbsp;</p>\r\n', 6, 0, 1),
(12, 'Đà Nẵng sắp mở tuyến phố đi bộ kết hợp chợ đêm', 1, '1540527062.du-lich-da-nang_1540365374.jpg', '<p>Ng&agrave;y 23/10, Sở Du lịch TP Đ&agrave; Nẵng th&ocirc;ng tin về việc tr&igrave;nh UBND th&agrave;nh phố xem x&eacute;t mở tuyến phố đi bộ tr&ecirc;n đường H&ugrave;ng Vương. Tuyến đường sầm uất thường xuy&ecirc;n đ&oacute;n lượng kh&aacute;ch lớn đến tham quan v&agrave; mua sắm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đường H&ugrave;ng Vương song song với nhiều con đường s&ocirc;i động của Đ&agrave; Nẵng như đường L&ecirc; Duẩn, Nguyễn Văn Linh. Đ&acirc;y được xem l&agrave; mạch giao th&ocirc;ng xuy&ecirc;n suốt, kết nối quận Hải Ch&acirc;u - trung t&acirc;m kinh tế, ch&iacute;nh trị, văn h&oacute;a của to&agrave;n th&agrave;nh phố với c&aacute;c khu vực l&acirc;n cận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuyến giao th&ocirc;ng n&agrave;y cũng gắn liền với c&aacute;c hoạt động thương mại. Chợ H&agrave;n v&agrave; chợ Cồn l&agrave; hai khu chợ nhộn nhịp bậc nhất nơi đ&acirc;y. Ngo&agrave;i ra, c&aacute;c c&ocirc;ng tr&igrave;nh hiện đại như si&ecirc;u thị Big C, nh&agrave; h&aacute;t Trưng Vương v&agrave; quảng trường cũng nằm trong khu vực.</p>\r\n\r\n<p><img alt=\"Chợ Cồn, thiên đường ăn vặt nổi tiếng của Đà Nẵng. Ảnh: Kiểu Sơn.\" src=\"https://www.dulichvietnam.com.vn/data/image/0hoi-cho-du-lich/tau%20thong%20nhat/cho-con-da-nang.jpg\" style=\"height:480px; width:800px\" /><br />\r\nChợ Cồn, thi&ecirc;n đường ăn vặt nổi tiếng của Đ&agrave; Nẵng. Ảnh: Kiểu Sơn.<br />\r\n&nbsp;</p>\r\n\r\n<p>Theo Sở Du lịch Đ&agrave; Nẵng, những khu xung quanh như phố ẩm thực đ&ecirc;m tr&ecirc;n đường Phạm Hồng Th&aacute;i, &ldquo;con đường c&agrave; ph&ecirc;&rdquo; Y&ecirc;n B&aacute;i hay nh&agrave; thờ ch&iacute;nh t&ograve;a Đ&agrave; Nẵng tr&ecirc;n đường Trần Ph&uacute; cũng sẽ hỗ trợ cho việc h&igrave;nh th&agrave;nh khu phố đi bộ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu th&aacute;ng 9 năm nay, th&agrave;nh phố Đ&agrave; Nẵng cũng đ&atilde; mở chợ đ&ecirc;m kết hợp phố đi bộ tại đoạn đường L&yacute; Nam Đế - Mai Hắc Đế thuộc phường An Hải T&acirc;y, quận Sơn Tr&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo l&atilde;nh đạo Sở Du lịch Đ&agrave; Nẵng, l&acirc;u nay, hoạt động vui chơi giải tr&iacute; về đ&ecirc;m của Đ&agrave; Nẵng c&ograve;n hạn chế. Th&agrave;nh phố sẽ ưu ti&ecirc;n bố tr&iacute; c&aacute;c h&agrave;ng h&oacute;a, sản phẩm của địa phương, xen kẽ c&aacute;c dịch vụ ăn uống, giải tr&iacute; ở khu phố đi bộ, chợ v&agrave;o buổi tối.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong 10 th&aacute;ng năm 2018, tổng kh&aacute;ch du lịch đến Đ&agrave; Nẵng ước đạt hơn 6,8 triệu lượt, tăng 21,5% so với c&ugrave;ng kỳ 2017. Trong đ&oacute;, lượng kh&aacute;ch quốc tế hơn 2,5 triệu lượt, tăng 31% so với c&ugrave;ng kỳ 2017. Số kh&aacute;ch nội địa hơn 4,3 triệu lượt, tăng 16,7% so với c&ugrave;ng kỳ 2017. Tổng thu du lịch ước đạt 23.661 tỷ đồng, tăng 43% so với c&ugrave;ng kỳ năm 2017.</p>\r\n', 1, 1, 1),
(14, 'Đàn ông khi đi du lịch một mình cần chuẩn bị những gì?', 4, '1541401160.1909131015-Travel 220x145.jpg', '<p>Lần đầu ti&ecirc;n đi nước ngo&agrave;i một m&igrave;nh, t&ocirc;i chưa biết chuẩn bị đồ đạc như thế n&agrave;o l&agrave; hợp l&yacute;.</p>\r\n\r\n<p>T&ocirc;i năm nay 28 tuổi, đang sống c&ugrave;ng bố mẹ tại H&agrave; Nội. Sắp tới, t&ocirc;i c&oacute; chuyến du lịch Th&aacute;i Lan 5 ng&agrave;y. Đ&acirc;y l&agrave; lần đầu ti&ecirc;n t&ocirc;i ra nước ngo&agrave;i v&agrave; đi du lịch một m&igrave;nh. Đến Bangkok, t&ocirc;i sẽ thăm một v&agrave;i người bạn tại đ&acirc;y. V&igrave; kh&ocirc;ng đi du lịch nhiều, t&ocirc;i kh&ocirc;ng biết c&aacute;ch chuẩn bị đồ đạc thế n&agrave;o l&agrave; hợp l&yacute; trong 5 ng&agrave;y. T&ocirc;i cũng kh&ocirc;ng biết c&aacute;ch xếp đồ như thế n&agrave;o l&agrave; th&ocirc;ng minh. Mong nhận được tư vấn của c&aacute;c bạn. Xin cảm ơn.</p>\r\n', 0, 0, 1),
(15, 'Vì sao không nên lót giấy lên toilet công cộng trên đường du lịch', 4, '1541401267.20161220170222494-1541046483-5971-1541047868.jpg', '<p>L&oacute;t khăn giấy l&ecirc;n bồn cầu kh&ocirc;ng phải giải ph&aacute;p hiệu quả để giảm thiểu nguy cơ l&acirc;y bệnh truyền nhiễm trong toilet c&ocirc;ng cộng.</p>\r\n\r\n<p>Nhiều người lu&ocirc;n cố gắng tr&aacute;nh toilet c&ocirc;ng cộng bằng mọi gi&aacute;, bởi kh&ocirc;ng ai muốn ngửi m&ugrave;i x&uacute; uế, những vết ố tr&ecirc;n tường trắng, bồn rửa tay dơ... Phổ biến nhất vẫn l&agrave; nỗi e ngại phải d&ugrave;ng bồn cầu trong toilet c&ocirc;ng cộng.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, khi di chuyển xa trong những chuyến du lịch, bạn buộc phải d&ugrave;ng toilet c&ocirc;ng cộng trong những trường hợp bất khả kh&aacute;ng. L&oacute;t khăn giấy k&iacute;n bồn cầu l&agrave; giải ph&aacute;p t&igrave;nh thế được nhiều người lựa chọn. Nhưng thực tế, lớp ngăn c&aacute;ch n&agrave;y khiến bạn tiếp x&uacute;c với nhiều vi khuẩn hơn.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Nhiều người có thói quen lót giấy vệ sinh phủ kín bệ bồn cầu. Ảnh: fxp.cz.\" src=\"https://i-dulich.vnecdn.net/2018/11/01/20161220170222494-1541046483-5971-1541047868.jpg\" />\r\n			<p>&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Nhiều người c&oacute; th&oacute;i quen l&oacute;t giấy vệ sinh phủ k&iacute;n bệ bồn cầu. Ảnh:<em>&nbsp;fxp.cz.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>L&yacute; do nằm ở cấu tạo của bồn cầu, bệ ngồi thường được l&agrave;m từ những vật liệu chống vi khuẩn l&acirc;y nhiễm. Tiến sĩ William Schaffer, một gi&aacute;o sư ng&agrave;nh y tế dự ph&ograve;ng, trả lời&nbsp;<em>Huffington Post</em>: &quot;Bệ ngồi toilet kh&ocirc;ng phải thứ l&acirc;y lan bất cứ bệnh truyền nhiễm n&agrave;o, bạn sẽ kh&ocirc;ng mắc phải bệnh g&igrave;&quot;.</p>\r\n\r\n<p>Trong khi đ&oacute;, giấy vệ sinh c&oacute; kết cấu dễ thấm h&uacute;t, khiến nơi n&agrave;y trở th&agrave;nh m&ocirc;i trường ho&agrave;n hảo cho vi khuẩn lưu lại, theo&nbsp;<em>Sun</em>. Những cuộn giấy thường được đặt s&aacute;t bồn cầu, trong tầm với để thuận tiện cho người sử dụng - song vị tr&iacute; n&agrave;y khiến ch&uacute;ng thu h&uacute;t nhiều vi khuẩn bị bắn ra sau mỗi lần xả nước.</p>\r\n\r\n<p>Nếu thực sự kh&ocirc;ng muốn tiếp x&uacute;c bồn cầu, bạn c&oacute; thể ngồi xổm c&aacute;ch xa n&oacute; - coi đ&oacute; như một b&agrave;i tập luyện cơ đ&ugrave;i trong toilet c&ocirc;ng cộng, hoặc t&igrave;m nơi c&oacute; bệ x&iacute; xổm.&nbsp;Điều quan trọng nhất gi&uacute;p bạn ph&ograve;ng trừ vi khuẩn c&oacute; hại ch&iacute;nh l&agrave; rửa tay sau mỗi lần d&ugrave;ng toilet, v&agrave; tr&aacute;nh chạm tay sạch l&ecirc;n những bề mặt như cửa ra v&agrave;o, tay nắm cửa...</p>\r\n', 0, 0, 1),
(16, '10 điều thú vị nhưng ít người biết về Paris', 4, '1541401306.2-Out-and-About-with-Mary-Kay-1540775375_680x0.jpg', '<p>Nếu d&agrave;nh mỗi ng&agrave;y để tham quan một nh&agrave; h&agrave;ng, qu&aacute;n ăn... ngo&agrave;i trời ở Kinh đ&ocirc; &aacute;nh s&aacute;ng, bạn sẽ mất 29,8 năm.</p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/2-Out-and-About-with-Mary-Kay-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>C&oacute; khoảng 180 điểm b&aacute;n c&agrave; ph&ecirc; espresso với gi&aacute; một euro (26.000 đồng). Thậm ch&iacute;, người Ph&aacute;p c&ograve;n c&oacute; cả bản đồ đ&aacute;nh dấu địa điểm b&aacute;n, gi&uacute;p du kh&aacute;ch dễ d&agrave;ng t&igrave;m kiếm.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/3-Robyn-Lee-Flickr-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>Nếu d&agrave;nh mỗi ng&agrave;y để thăm một qu&aacute;n ngo&agrave;i trời, bạn sẽ phải mất 29,8 năm. Th&agrave;nh phố c&oacute; gần 10.000 qu&aacute;n như thế v&agrave; kh&ocirc;ng ai chắc được trong thời gian tới c&oacute; bao nhi&ecirc;u h&agrave;ng kh&aacute;c được mở.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/9-Marco-Bermardini-Flickr-1540775376_680x0.jpg\" /></p>\r\n\r\n<p>Nếu bạn th&iacute;ch b&aacute;nh m&igrave; baguettes, th&agrave;nh phố n&agrave;y l&agrave; sự lựa chọn đ&uacute;ng đắn khi c&oacute; hơn 1.700 cửa h&agrave;ng.</p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/5-1540798764_680x0.jpg\" /></p>\r\n\r\n<p>Paris c&oacute; nhiều bar nhưng &iacute;t hơn cửa h&agrave;ng b&aacute;nh m&igrave;, với hơn 1.100 qu&aacute;n. Ảnh:&nbsp;<em>France.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/3-1540798502_680x0.jpg\" /></p>\r\n\r\n<p>Mọi c&aacute;i c&acirc;y đều được thu thập số liệu, đo đạc kỹ lưỡng. Trong đ&oacute;, điểm c&acirc;y số 0 của Ph&aacute;p được đ&aacute;nh dấu ở s&acirc;n trước nh&agrave; thờ Đức B&agrave;. Hiện th&agrave;nh phố c&oacute; &iacute;t nhất 484.000 c&acirc;y xanh. Ảnh:&nbsp;<em>News.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/5-Paris-Property-Group-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>Th&agrave;nh phố từng chỉ c&oacute; một biển b&aacute;o &quot;Dừng lại&quot;, đặt ở lối ra của c&ocirc;ng ty bất động sản tại quận 16. Hệ thống giao th&ocirc;ng Paris chủ yếu dựa v&agrave;o việc nhường đường cho người b&ecirc;n phải. Tuy nhi&ecirc;n, tấm biển b&aacute;o tr&ecirc;n thường xuy&ecirc;n bị đ&aacute;nh cắp. Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/7-Stephanie-Kilgast-Flickr-1540775376_680x0.jpg\" /></p>\r\n\r\n<p>C&oacute; 6.100 con phố ở thủ đ&ocirc; nước Ph&aacute;p. 10 con phố đẹp nhất cho du kh&aacute;ch đi bộ ngắm cảnh l&agrave;:&nbsp;Rue des Barres, Rue de l&#39;Abreuvoir, Cour du Commerce-Saint-Andr&eacute;, Rue Montorgueil, Rue Cr&eacute;mieux, Rue des Rosiers, Rue Lepic, Rue Saint-Antoine, Avenue Winston Churchill, Quai de Jemmapes.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/2-1540798391_680x0.jpg\" /></p>\r\n\r\n<p>Con phố ngắn nhất l&agrave; Rue des Degr&eacute;s, d&agrave;i 5,75 m, nằm ở quận 2. Ảnh:&nbsp;<em>Paris ladouce.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/6-Pictr73-Flickr-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>Chu&ocirc;ng ch&iacute;nh trong Nh&agrave; thờ Đức B&agrave; nặng 13 tấn. N&oacute; c&ograve;n được gọi với c&aacute;i t&ecirc;n Emmanuel.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/4-Pinterest-1540775375_600x0.jpg\" /></p>\r\n\r\n<p>Ch&iacute;nh quyền cho ph&eacute;p đặt 31 m&aacute;y b&aacute;n bao cao su tự động tr&ecirc;n đường phố. Trong trường hợp du kh&aacute;ch kh&ocirc;ng biết c&aacute;c địa điểm, bạn c&oacute; thể t&igrave;m kiếm ch&uacute;ng bằng bản đồ.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n', 0, 0, 1),
(17, '5 điểm gần Hà Nội đáng thử vào dịp cuối tuần cho các gia đình', 4, '1541401343.1-11-201859-w500-7581-1541123655.png', '<p>Chỉ mất 1-2 tiếng di chuyển, Tam Đảo, Thung Nai hay Hạ Long đang được c&aacute;c gia đ&igrave;nh lựa chọn cho những chuyến đi ngắn ng&agrave;y dịp cuối tuần.</p>\r\n\r\n<p>Với quỹ thời gian eo hẹp, bạn vẫn c&oacute; thể tận hưởng kỳ nghỉ cuối tuần xa H&agrave; Nội với c&aacute;c điểm đến dưới đ&acirc;y.</p>\r\n\r\n<p><strong>Tam Đảo, Vĩnh Ph&uacute;c</strong></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/11/02/1-11-201853-153967157-w500-1990-1541123652.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>C&aacute;c c&ocirc;ng tr&igrave;nh ở Tam Đảo ẩn hiện trong kh&oacute;i sương bồng bềnh.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Nằm c&aacute;ch H&agrave; Nội khoảng 80 km, Tam Đảo được biết đến với kh&iacute; hậu &ocirc;n h&ograve;a, hội tụ bốn m&ugrave;a trong ng&agrave;y. Buổi s&aacute;ng l&agrave; m&ugrave;a xu&acirc;n m&aacute;t l&agrave;nh, buổi trưa c&oacute; nắng như m&ugrave;a hạ v&agrave;ng rực rỡ, chiều bu&ocirc;ng ngọt dịu m&ugrave;a thu v&agrave; buổi tối mang c&aacute;i lạnh se sắt của m&ugrave;a đ&ocirc;ng.</p>\r\n\r\n<p>Từng được v&iacute; như khu nghỉ dưỡng bậc nhất Đ&ocirc;ng Dương do người Ph&aacute;p x&acirc;y dựng n&ecirc;n Tam Đảo hiện vẫn sở hữu c&ocirc;ng tr&igrave;nh mang &acirc;m hưởng kiến tr&uacute;c Paris. Đ&oacute; l&agrave; nh&agrave; thờ Tam Đảo được x&acirc;y dựng đầu thế kỷ 20 với kiến tr&uacute;c Gothic đặc trưng vừa t&ocirc;n nghi&ecirc;m nhưng vẫn to&aacute;t l&ecirc;n vẻ cổ k&iacute;nh ẩn hiện giữa rừng gi&agrave;.</p>\r\n\r\n<p>Chuyến h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Tam Đảo tiếp tục mở ra với th&aacute;c Bạc, nơi c&oacute; d&ograve;ng suối nhỏ trắng x&oacute;a, chảy xiết từ khe n&uacute;i cheo leo. Ai đam m&ecirc; kh&aacute;m ph&aacute; sẽ th&iacute;ch cảm x&uacute;c được ngắm trọn khung cảnh n&uacute;i rừng Tam Đảo h&ugrave;ng vĩ từ độ cao 1.375 m của th&aacute;p truyền h&igrave;nh. C&ograve;n ai th&iacute;ch y&ecirc;n tĩnh trong một miền đất đật Phật, n&ecirc;n đến Thiền viện T&acirc;y Thi&ecirc;n.</p>\r\n\r\n<p><strong>Hoa Lư, Ninh B&igrave;nh</strong></p>\r\n\r\n<p>Chỉ c&aacute;ch H&agrave; Nội chừng 100 km, quần thể danh thắng Tr&agrave;ng An (Ninh B&igrave;nh) cũng l&agrave; thi&ecirc;n đường du lịch hấp dẫn, cho c&aacute;c gia đ&igrave;nh đưa con c&aacute;i về với thi&ecirc;n nhi&ecirc;n kỳ vĩ, trải nghiệm di sản văn h&oacute;a thi&ecirc;n nhi&ecirc;n được UNESCO vinh danh.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/11/02/1-11-201838-w500-6054-1541123653.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>D&ograve;ng s&ocirc;ng đưa du kh&aacute;ch kh&aacute;m ph&aacute; Tam Cốc.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Khung cảnh ở đ&acirc;y hữu t&igrave;nh với những d&atilde;y n&uacute;i non tr&ugrave;ng điệp, d&ograve;ng suối xanh m&aacute;t v&agrave; c&aacute;nh đồng l&uacute;a v&agrave;ng &oacute;ng ả.</p>\r\n\r\n<p>Quanh Hoa Lư, ngo&agrave;i khu danh thắng Tr&agrave;ng An, bạn c&oacute; thể đi thăm c&aacute;c quần thể danh thắng ch&ugrave;a chiền như ch&ugrave;a B&aacute;i Đ&iacute;nh, đền thờ vua Đinh, vua L&ecirc; v&agrave; kh&aacute;m ph&aacute; cố đ&ocirc;. Nghỉ dưỡng tại c&aacute;c resort gần khu bảo tồn thi&ecirc;n nhi&ecirc;n đầm V&acirc;n Long để s&aacute;ng h&ocirc;m sau, bạn kh&aacute;m ph&aacute; nơi từng được chọn l&agrave;m phim trường&nbsp;<em>Kong: Skull Island,</em>&nbsp;rồi gh&eacute; khu suối nướng n&oacute;ng K&ecirc;nh G&agrave; ăn uống, nghỉ ngơi. Tắm suối nước n&oacute;ng l&agrave; những gợi &yacute; th&uacute; vị cho một h&agrave;nh tr&igrave;nh Hoa Lư trọn vẹn.</p>\r\n\r\n<p><strong>Thung Nai, H&ograve;a B&igrave;nh</strong></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/11/02/1-11-201818-w500-3115-1541123654.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Đến Thung Nai, du kh&aacute;ch chỉ mất hơn một giờ di chuyển từ H&agrave; Nội.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Được v&iacute; như Hạ Long tr&ecirc;n cạn, Thung Nai sở hữu khung cảnh sơn thủy hữu t&igrave;nh của m&acirc;y trời, n&uacute;i non v&agrave; hồ nước trong veo, m&aacute;t l&agrave;nh. Trải nghiệm th&uacute; vị nhất ở Thung Nai l&agrave; ngồi thuyền dạo quanh l&ograve;ng hồ. Thuyền xu&ocirc;i m&aacute;i tr&ecirc;n mặt nước xanh in b&oacute;ng n&uacute;i, m&acirc;y trời, gi&oacute; lộng m&aacute;t rượi. Bạn sẽ dễ d&agrave;ng nhận ra 3 h&ograve;n đảo lớn nhất Thung Nai l&agrave; đảo Dừa, đảo Cối xay gi&oacute;, đảo Xanh. Đ&acirc;y l&agrave; gi&acirc;y ph&uacute;t thư gi&atilde;n để bạn tận hưởng trọn vẹn cảm gi&aacute;c nhẹ nh&agrave;ng, &ecirc;m dịu giữa cảnh thi&ecirc;n nhi&ecirc;n hữu t&igrave;nh, thanh b&igrave;nh tr&agrave;n ngập m&agrave;u xanh.</p>\r\n\r\n<p><strong>Hồ N&uacute;i Cốc, Th&aacute;i Nguy&ecirc;n</strong></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/11/02/1-11-201841-w500-4826-1541123654.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Được gắn với huyền t&iacute;ch ch&agrave;ng Cốc v&agrave; n&agrave;ng C&ocirc;ng, hồ N&uacute;i Cốc cũng l&agrave; địa điểm hấp dẫn cho kỳ nghỉ cuối tuần với những ai muốn đưa cả nh&agrave; về miền trong l&agrave;nh, b&igrave;nh y&ecirc;n.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Để kh&aacute;m ph&aacute; trọn vẹn vẻ đẹp của hồ N&uacute;i Cốc bạn h&atilde;y lựa chọn một chiếc thuyền đi dạo quanh hồ, thưởng ngoạn khung cảnh thi&ecirc;n nhi&ecirc;n thơ mộng, thanh b&igrave;nh, nghe kể chuyện truyền thuyết thủy chung ch&agrave;ng Cốc &ndash; n&agrave;ng C&ocirc;ng, kh&aacute;m ph&aacute; ng&ocirc;i nh&agrave; hơn 200 tuổi với nhiều di vật qu&yacute; gi&aacute; tr&ecirc;n N&uacute;i C&aacute;i, thăm quần thể ch&ugrave;a Th&aacute;c V&agrave;ng...</p>\r\n\r\n<p>Ho&agrave;ng h&ocirc;n ở hồ n&uacute;i Cốc cũng l&agrave; trải nghiệm đ&aacute;ng thử khi &aacute;nh chiều v&agrave;ng ruộm bao phủ l&ecirc;n c&aacute;c đảo đ&aacute;: đảo nổi, đảo ch&igrave;m thoắt ẩn, thoắt hiện đầy kỳ th&uacute;, với nhiều t&ecirc;n gọi rất gần gũi: Đảo C&ograve;, đảo D&ecirc;, đảo Khỉ, đảo Khuynh Diệp, đảo Tr&aacute;m...</p>\r\n\r\n<p><strong>Cụm c&ocirc;ng vi&ecirc;n b&ecirc;n vịnh Hạ Long, Quảng Ninh</strong></p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/11/02/1-11-201859-w500-7581-1541123655.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Sau khi tuyến cao tốc Hạ Long &ndash; Hải Ph&ograve;ng đi v&agrave;o hoạt động, qu&atilde;ng đường từ H&agrave; Nội xuống Hạ Long được r&uacute;t ngắn chỉ c&ograve;n khoảng 1,5 giờ l&aacute;i xe.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Do thời gian di chuyển r&uacute;t ngắn, nhiều gia đ&igrave;nh đang coi Hạ Long l&agrave; bến đỗ mới mỗi dịp cuối tuần, để đưa con trẻ đi vui chơi giải tr&iacute;, tho&aacute;t khỏi sự chật hẹp v&agrave; thiếu chỗ vui chơi của H&agrave; Nội.</p>\r\n\r\n<p>Đi Hạ Long l&agrave; phải ra vịnh ngắm kỳ quan thế giới. Nhưng với nhiều gia đ&igrave;nh c&oacute; con nhỏ, thế giới của v&ocirc; số tr&ograve; chơi v&agrave; cảnh quan hấp dẫn ở c&ocirc;ng vi&ecirc;n Sun World Halong Complex tại trung t&acirc;m B&atilde;i Ch&aacute;y đang l&agrave; lựa chọn hợp l&yacute; hơn. C&aacute;c gia đ&igrave;nh c&oacute; thể chọn nghỉ ngơi tại nhiều kh&aacute;ch sạn gần c&ocirc;ng vi&ecirc;n, để trong hai ng&agrave;y trải nghiệm hết tổ hợp vui chơi giải tr&iacute; lớn nhất miền Bắc n&agrave;y.</p>\r\n\r\n<p>Tại đ&acirc;y c&oacute; rất c&aacute;c trải nghiệm ph&ugrave; hợp với nhiều lứa tuổi, từ lướt c&aacute;p treo Nữ Ho&agrave;ng ngắm nh&igrave;n to&agrave;n cảnh Vịnh Hạ Long từ tr&ecirc;n cao, dạo bước giữa vườn Nhật xanh mướt mải để lạc trong một thế giới bonsai, vườn Zen độc đ&aacute;o. Cả gia đ&igrave;nh c&oacute; thể c&ugrave;ng xem m&uacute;a rối nước, check in cầu Koi, gặp gỡ thần tượng tại bảo t&agrave;ng tượng s&aacute;p lớn nhất Việt Nam hay vui chơi tại thế giới tr&ograve; xu x&egrave;ng hiện đại tại tổ hợp vui chơi tr&ecirc;n đỉnh Ba Đ&egrave;o...</p>\r\n\r\n<p>Với gia đ&igrave;nh c&oacute; con tuổi teen, Dragon Park với nhiều tr&ograve; chơi cảm gi&aacute;c mạnh như &ldquo;Phi long thần tốc&rdquo;; &ldquo;T&ecirc; gi&aacute;c cuồng nộ&rdquo;, &ldquo;Theo dấu ch&acirc;n rồng&rdquo;... l&agrave; kh&ocirc;ng n&ecirc;n bỏ lỡ. Đặc biệt, gi&aacute; v&eacute; vui chơi c&ocirc;ng vi&ecirc;n Dragon Park từ nay đến hết 31/12/2018 giảm chỉ c&ograve;n 50.000 đồng.</p>\r\n', 0, 0, 1);
INSERT INTO `tin_tuc` (`id_tin`, `tieu_de`, `danh_muc_id`, `hinh_anh`, `noi_dung`, `luot_xem`, `id_nguoi_dang`, `hien_thi`) VALUES
(18, '5 biến tấu của món phở cho ngày se lạnh ở Hà Nội', 4, '1541401381.500px0-IMG-9052-copy-7450-1541169730.jpg', '<p>Ngo&agrave;i phở nước, bạn c&oacute; thể thưởng thức phở x&agrave;o hoặc phở chi&ecirc;n phồng, ăn cũng rất ngon v&agrave;o thời tiết se lạnh.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Phở nước truyền thống\" src=\"https://i-dulich.vnecdn.net/2018/11/02/500px0-IMG-9052-copy-7450-1541169730.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Phở nước truyền thống</strong></p>\r\n\r\n			<p>Phở l&agrave; m&oacute;n&nbsp;dễ ăn&nbsp;được b&aacute;n ở nhiều nơi, nhưng kh&ocirc;ng đ&acirc;u c&oacute; vị đặc trưng như ở H&agrave; Nội. B&aacute;nh phở được l&agrave;m từ gạo ngon, tr&aacute;ng th&agrave;nh lớp b&aacute;nh trắng mỏng, mềm, dai, rồi th&aacute;i sợi. B&aacute;t phở trở n&ecirc;n&nbsp;bắt mắt hơn nhờ&nbsp;h&agrave;nh hoa chẻ, h&agrave;nh l&aacute;. Nước d&ugrave;ng được hầm từ xương, tuỳ theo c&ocirc;ng thức của mỗi qu&aacute;n m&agrave; hương vị của m&oacute;n ăn sẽ thay đổi. C&aacute;c loại&nbsp;gia vị cơ bản&nbsp;bao gồm quế, hồi, gừng, đinh hương n&ecirc;n nước d&ugrave;ng trong veo, ngọt tự nhi&ecirc;n.</p>\r\n\r\n			<p>Du kh&aacute;ch c&oacute; thể t&igrave;m t&ocirc; phở b&ograve; truyền thống từ c&aacute;c qu&aacute;n b&igrave;nh d&acirc;n b&ecirc;n đường cho đến nh&agrave; h&agrave;ng sang trọng. Gi&aacute; trung b&igrave;nh&nbsp;từ 30.000 đồng. Ảnh:&nbsp;<em>Phong Vinh.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"am\" src=\"https://i-dulich.vnecdn.net/2018/11/02/Anh-2-JPG-9659-1462329616-1135-1541169730.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>B&ecirc;n cạnh những h&agrave;ng phở b&ograve; truyền thống, c&aacute;c h&agrave;ng phở g&agrave; vẫn được rất nhiều người y&ecirc;u th&iacute;ch bởi hương vị ri&ecirc;ng.&nbsp;Một trong những qu&aacute;n phở g&agrave; l&acirc;u đời nhất ở H&agrave; Nội nằm ở ng&atilde; tư Yết Ki&ecirc;u v&agrave; Đỗ H&agrave;nh. Một suất ăn tại đ&acirc;y c&oacute; gi&aacute; 40.000 đồng. Ảnh:&nbsp;<em>Phong Vinh.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Phở cuốnẢnh: ULinh.\" src=\"https://i-dulich.vnecdn.net/2018/11/02/500px-pho-cuo-n-chi-Linh-9314-1541169730.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Phở cuốn</strong></p>\r\n\r\n			<p>M&oacute;n ăn c&oacute; nguy&ecirc;n liệu đơn giản gồm b&aacute;nh phở, thịt b&ograve; v&agrave; một số loại rau. Tất cả nguy&ecirc;n liệu n&agrave;y sau khi sơ biến sẽ được cuốn tr&ograve;n như m&oacute;n gỏi cuốn. Thịt b&ograve; được ướp kỹ, kh&ocirc;ng x&agrave;o qu&aacute; ch&iacute;n n&ecirc;n khi ăn kh&ocirc;ng cảm thấy dai v&agrave; c&oacute; vị ngọt. B&aacute;nh phở d&ugrave;ng để cuốn l&agrave; loại mỏng, mềm v&agrave; hơi dai. Người ta thường đ&aacute;nh gi&aacute; m&oacute;n ăn c&oacute; ngon hay kh&ocirc;ng, trước ti&ecirc;n l&agrave; b&aacute;nh phải kh&ocirc;ng bị r&aacute;ch. Tiếp theo l&agrave; vị của ch&eacute;n nước chấm đi k&egrave;m được pha chua ngọt v&agrave; hơi cay.</p>\r\n\r\n			<p>Du kh&aacute;ch c&oacute; thể t&igrave;m thấy m&oacute;n ăn ở nhiều nơi nhưng khu vực hồ T&acirc;y l&agrave; nơi nổi tiếng nhất với m&oacute;n n&agrave;y. Gi&aacute; trung b&igrave;nh khoảng 40.000 đồng một suất.&nbsp;Ảnh:&nbsp;<em>ULinh.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Phở xàoa\" src=\"https://i-dulich.vnecdn.net/2018/11/02/500pxpho-xa-o-6815-1541169730.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Phở x&agrave;o</strong></p>\r\n\r\n			<p>B&aacute;t Đ&agrave;n v&agrave; H&agrave;ng Buồm l&agrave; hai con phố nổi tiếng với phở x&agrave;o. M&oacute;n ăn bắt mắt với thứ nước sốt đặc sệt quấn lấy thịt b&ograve; v&agrave;&nbsp;những sợi phở. Người th&iacute;ch ăn rau c&oacute; thể y&ecirc;u cầu cho th&ecirc;m, nếu kh&ocirc;ng bạn c&oacute; thể ăn k&egrave;m với đĩa rau sống v&agrave; b&aacute;t&nbsp;nước mắm.&nbsp;</p>\r\n\r\n			<p>M&oacute;n n&agrave;y c&ograve;n c&oacute; một biến tấu khi được một cửa h&agrave;ng tr&ecirc;n phố H&agrave;ng Buồm x&agrave;o kh&ocirc;, sợi săn, đ&ocirc;i chỗ c&oacute; ch&aacute;y cạnh thơm nức mũi. Thịt bắp b&ograve; gi&ograve;n, ướp gia vị đậm đ&agrave;. Tuy nhi&ecirc;n lượng thịt trong một phần ăn kh&aacute; khi&ecirc;m tốn. Kh&aacute;ch đến đ&acirc;y thường gọi th&ecirc;m một b&aacute;t bắp b&ograve; trần. Gi&aacute; cho một suất đầy đủ dao động từ 50.000 đồng.&nbsp;Ảnh:<em>&nbsp;Di Vỹ.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Phở chiên phồngẢnh: Đức Hùng.\" src=\"https://i-dulich.vnecdn.net/2018/11/02/500px-phochienphong-7525-1541169730.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Phở chi&ecirc;n phồng</strong></p>\r\n\r\n			<p>Đ&uacute;ng như t&ecirc;n gọi, phở chi&ecirc;n phồng l&agrave; những sợi b&aacute;nh được xắt bản to, thường c&oacute; h&igrave;nh vu&ocirc;ng rồi&nbsp;chi&ecirc;n l&ecirc;n trong chảo mỡ. B&aacute;nh phở sau khi chi&ecirc;n sẽ được rưới l&ecirc;n thứ nước sốt sền sệt, s&oacute;ng s&aacute;nh&nbsp;l&agrave;m&nbsp;từ c&aacute;c loại rau củ như nấm hương, rau cải, h&agrave;nh t&acirc;y c&ugrave;ng thịt b&ograve;, tim cật. Mỗi suất được dọn ra đầy đặn, k&egrave;m th&ecirc;m một ch&uacute;t dưa g&oacute;p ăn cho đỡ ng&aacute;n. Gi&aacute; b&igrave;nh qu&acirc;n&nbsp;l&agrave; 45.000 đồng một t&ocirc;.</p>\r\n\r\n			<p>Bạn c&oacute; thể t&igrave;m đến c&aacute;c địa chỉ nổi tiếng với m&oacute;n n&agrave;y ở phố Kh&acirc;m Thi&ecirc;n, Nguyễn Si&ecirc;u hoặc Trần Nh&acirc;n T&ocirc;ng.&nbsp;Ảnh:&nbsp;<em>Đức H&ugrave;ng.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Phở tíu trộnẢnh: Viet Nguyen.\" src=\"https://i-dulich.vnecdn.net/2018/11/02/chen2-8028-1536313956-1172-1541169730.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p><strong>Phở t&iacute;u</strong></p>\r\n\r\n			<p>Phở t&iacute;u cũng l&agrave; một m&oacute;n ăn quen thuộc, kh&aacute; nổi tiếng ở ng&otilde; chợ bởi sự kh&aacute;c lạ so với phở th&ocirc;ng thường. Kh&ocirc;ng nhiều nước như b&aacute;t phở b&ograve; t&aacute;i, ch&iacute;n, cũng chẳng &iacute;t nước như phở trộn, phở t&iacute;u được d&ugrave;ng với nước x&acirc;m xấp, ăn c&ugrave;ng thịt nạc th&aacute;i mỏng, bản to, lạc rang, h&agrave;nh kh&ocirc;.</p>\r\n\r\n			<p>Khi ăn thực kh&aacute;ch sẽ d&ugrave;ng th&igrave;a đũa đảo đều để b&aacute;nh phở ngấm gia vị, nước trộn. Phở t&iacute;u c&oacute; hương vị thơm, ngậy của h&agrave;nh kh&ocirc;, lạc rang v&agrave; vị ngọt đậm đ&agrave; của thịt quay kết hợp với vị chua chua của giấm.</p>\r\n\r\n			<p>Du kh&aacute;ch đến H&agrave; Nội kh&ocirc;ng thể bỏ qua qu&aacute;n ăn trong ng&otilde; chợ Đồng Xu&acirc;n, nơi đ&atilde; b&aacute;n m&oacute;n n&agrave;y từ những năm 1970. Qu&aacute;n nhỏ, đ&ocirc;ng kh&aacute;ch n&ecirc;n muốn kh&ocirc;ng chờ đợi bạn phải tới sớm. Gi&aacute; một b&aacute;t 30.000 đồng.&nbsp;Ảnh:<em>&nbsp;Viet Nguyen.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n', 0, 0, 1),
(19, 'Các chặng bay giá rẻ bán ra trong tháng 11', 4, '1541401471.screen-shot-2018-10-30-at-09-4-1756-5623-1540873934.jpg', '<p>Trong th&aacute;ng gần cuối năm, nhiều đường bay ngắn từ TP HCM đi Nha Trang, Đ&agrave; Lạt gi&aacute; chỉ từ 55.000 đến 299.000 đồng.</p>\r\n\r\n<p>Thị trường v&eacute; m&aacute;y bay nội địa th&aacute;ng 11 kh&ocirc;ng ch&ecirc;nh lệch nhiều so với th&aacute;ng trước. Tuy nhi&ecirc;n, du kh&aacute;ch vẫn c&oacute; thể t&igrave;m được v&eacute; trong khoảng gi&aacute; từ 55.000 đồng đến tr&ecirc;n dưới 3 triệu đồng, tuỳ đường bay v&agrave; thời điểm đặt.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Nhiều đường bay nội địa trong tháng 11 có giá từ 55.000 đồng. Ảnh chụp màn hình.\" src=\"https://i-dulich.vnecdn.net/2018/10/30/screen-shot-2018-10-30-at-09-4-1756-5623-1540873934.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Nhiều đường bay nội địa trong th&aacute;ng 11 c&oacute; gi&aacute; từ 55.000 đồng.&nbsp;<em>Ảnh chụp m&agrave;n h&igrave;nh.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Đường bay TP HCM - Nha Trang của h&atilde;ng h&agrave;ng kh&ocirc;ng gi&aacute; rẻ Jetstar c&oacute; gi&aacute; khởi điểm 55.000 đồng, gi&aacute; v&eacute; của Vietjet cũng chỉ ch&ecirc;nh lệch v&agrave;i ngh&igrave;n đồng. Đ&acirc;y l&agrave; gi&aacute; v&eacute; một chiều, chưa bao gồm thuế ph&iacute;.</p>\r\n\r\n<p>Trong th&aacute;ng 11, Vietnam Airlines cũng mở b&aacute;n v&eacute; 299.000 đồng cho đường bay TP HCM - Đ&agrave; Lạt, Bu&ocirc;n M&ecirc; Thuột. C&aacute;c chặng bay từ TP HCM đi Ph&uacute; Quốc, Nha Trang, Quy Nhơn c&ograve;n nhiều v&eacute; khuyến m&atilde;i từ 399.000 đồng. H&agrave;nh tr&igrave;nh bay từ H&agrave; Nội đi TP HCM vẫn tiếp tục được khai th&aacute;c với mức gi&aacute; từ 800.000 đồng. &nbsp;</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Máy bay phản lực loại nhỏ E190-E2 được thử nghiệm tại Hà Nội vào tháng 10.\" src=\"https://i-dulich.vnecdn.net/2018/10/31/Anh-1-Tau-bay-E190-E2-tai-Noi-8559-9626-1540924504.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>M&aacute;y bay phản lực loại nhỏ E190-E2 được thử nghiệm tại H&agrave; Nội v&agrave;o th&aacute;ng 10.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Theo một đại l&yacute; tại H&agrave; Nội, đ&acirc;y l&agrave; thời điểm miền Bắc bắt đầu v&agrave;o đ&ocirc;ng, một số tỉnh miền Trung đ&oacute;n kh&ocirc;ng kh&iacute; lạnh, n&ecirc;n c&aacute;c h&atilde;ng bay kh&ocirc;ng đưa ra nhiều chương tr&igrave;nh khuyến m&atilde;i như đầu thu. &nbsp;</p>\r\n\r\n<p>Những người c&oacute; kinh nghiệm l&acirc;u năm trong thị trường h&agrave;ng kh&ocirc;ng nhận định, đ&acirc;y đang l&agrave; m&ugrave;a thấp điểm của du lịch trong nước. Một số h&atilde;ng bắt đầu r&uacute;t bớt t&agrave;u bay về bảo dưỡng để phục vụ cho m&ugrave;a vận chuyển cuối năm. Kh&ocirc;ng &iacute;t m&aacute;y bay nội địa cũng được r&uacute;t về để cho thu&ecirc; chuyến đi nước ngo&agrave;i. Đ&acirc;y l&agrave; một trong những l&yacute; do khiến thị trường v&eacute; m&aacute;y bay trong th&aacute;ng 11 kh&ocirc;ng mấy s&ocirc;i động.</p>\r\n\r\n<p>Th&aacute;ng n&agrave;y, c&aacute;c h&atilde;ng h&agrave;ng kh&ocirc;ng c&ograve;n một v&agrave;i đợt mở b&aacute;n v&eacute; Tết &acirc;m lịch 2019. So với c&ugrave;ng kỳ năm ngo&aacute;i, gi&aacute; v&eacute; năm nay c&oacute; xu hướng tăng mạnh, nhiều người đ&atilde; đặt trước 4 - 5 th&aacute;ng để tr&aacute;nh t&igrave;nh trạng ch&aacute;y v&eacute; hoặc phải mua v&eacute; gi&aacute; cao những ng&agrave;y tết. &nbsp;</p>\r\n\r\n<p><em>Kayak</em>, một đơn vị sử dụng thuật to&aacute;n v&agrave; tr&iacute; tuệ th&ocirc;ng minh nh&acirc;n tạo để dự đo&aacute;n xu hướng gi&aacute; v&eacute; m&aacute;y bay nhận định, khả năng cao gi&aacute; v&eacute; tr&ecirc;n chặng bay TP HCM - H&agrave; Nội cuối th&aacute;ng 1/2019 sẽ tiếp tục tăng mạnh. Đơn vị n&agrave;y cũng khuyến nghị h&agrave;nh kh&aacute;ch n&ecirc;n mua v&eacute; c&agrave;ng sớm c&agrave;ng tốt.&nbsp;</p>\r\n', 0, 0, 1),
(20, '10 điều thú vị nhưng ít người biết về Paris phần 2', 4, '1541401591.4-Pinterest-1540775375_600x0.jpg', '<p>Nếu d&agrave;nh mỗi ng&agrave;y để tham quan một nh&agrave; h&agrave;ng, qu&aacute;n ăn... ngo&agrave;i trời ở Kinh đ&ocirc; &aacute;nh s&aacute;ng, bạn sẽ mất 29,8 năm.</p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/2-Out-and-About-with-Mary-Kay-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>C&oacute; khoảng 180 điểm b&aacute;n c&agrave; ph&ecirc; espresso với gi&aacute; một euro (26.000 đồng). Thậm ch&iacute;, người Ph&aacute;p c&ograve;n c&oacute; cả bản đồ đ&aacute;nh dấu địa điểm b&aacute;n, gi&uacute;p du kh&aacute;ch dễ d&agrave;ng t&igrave;m kiếm.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/3-Robyn-Lee-Flickr-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>Nếu d&agrave;nh mỗi ng&agrave;y để thăm một qu&aacute;n ngo&agrave;i trời, bạn sẽ phải mất 29,8 năm. Th&agrave;nh phố c&oacute; gần 10.000 qu&aacute;n như thế v&agrave; kh&ocirc;ng ai chắc được trong thời gian tới c&oacute; bao nhi&ecirc;u h&agrave;ng kh&aacute;c được mở.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/9-Marco-Bermardini-Flickr-1540775376_680x0.jpg\" /></p>\r\n\r\n<p>Nếu bạn th&iacute;ch b&aacute;nh m&igrave; baguettes, th&agrave;nh phố n&agrave;y l&agrave; sự lựa chọn đ&uacute;ng đắn khi c&oacute; hơn 1.700 cửa h&agrave;ng.</p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/5-1540798764_680x0.jpg\" /></p>\r\n\r\n<p>Paris c&oacute; nhiều bar nhưng &iacute;t hơn cửa h&agrave;ng b&aacute;nh m&igrave;, với hơn 1.100 qu&aacute;n. Ảnh:&nbsp;<em>France.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/3-1540798502_680x0.jpg\" /></p>\r\n\r\n<p>Mọi c&aacute;i c&acirc;y đều được thu thập số liệu, đo đạc kỹ lưỡng. Trong đ&oacute;, điểm c&acirc;y số 0 của Ph&aacute;p được đ&aacute;nh dấu ở s&acirc;n trước nh&agrave; thờ Đức B&agrave;. Hiện th&agrave;nh phố c&oacute; &iacute;t nhất 484.000 c&acirc;y xanh. Ảnh:&nbsp;<em>News.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/5-Paris-Property-Group-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>Th&agrave;nh phố từng chỉ c&oacute; một biển b&aacute;o &quot;Dừng lại&quot;, đặt ở lối ra của c&ocirc;ng ty bất động sản tại quận 16. Hệ thống giao th&ocirc;ng Paris chủ yếu dựa v&agrave;o việc nhường đường cho người b&ecirc;n phải. Tuy nhi&ecirc;n, tấm biển b&aacute;o tr&ecirc;n thường xuy&ecirc;n bị đ&aacute;nh cắp. Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/7-Stephanie-Kilgast-Flickr-1540775376_680x0.jpg\" /></p>\r\n\r\n<p>C&oacute; 6.100 con phố ở thủ đ&ocirc; nước Ph&aacute;p. 10 con phố đẹp nhất cho du kh&aacute;ch đi bộ ngắm cảnh l&agrave;:&nbsp;Rue des Barres, Rue de l&#39;Abreuvoir, Cour du Commerce-Saint-Andr&eacute;, Rue Montorgueil, Rue Cr&eacute;mieux, Rue des Rosiers, Rue Lepic, Rue Saint-Antoine, Avenue Winston Churchill, Quai de Jemmapes.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/2-1540798391_680x0.jpg\" /></p>\r\n\r\n<p>Con phố ngắn nhất l&agrave; Rue des Degr&eacute;s, d&agrave;i 5,75 m, nằm ở quận 2. Ảnh:&nbsp;<em>Paris ladouce.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/6-Pictr73-Flickr-1540775375_680x0.jpg\" /></p>\r\n\r\n<p>Chu&ocirc;ng ch&iacute;nh trong Nh&agrave; thờ Đức B&agrave; nặng 13 tấn. N&oacute; c&ograve;n được gọi với c&aacute;i t&ecirc;n Emmanuel.&nbsp;Ảnh:&nbsp;<em>Local.</em></p>\r\n\r\n<p><img alt=\"10 điều thú vị nhưng ít người biết về Paris\" src=\"https://i-dulich.vnecdn.net/2018/10/29/4-Pinterest-1540775375_600x0.jpg\" /></p>\r\n\r\n<p>Ch&iacute;nh quyền cho ph&eacute;p đặt 31 m&aacute;y b&aacute;n bao cao su tự động tr&ecirc;n đường phố. Trong trường hợp du kh&aacute;ch kh&ocirc;ng biết c&aacute;c địa điểm, bạn c&oacute; thể t&igrave;m kiếm ch&uacute;ng bằng bản đồ.</p>\r\n', 0, 0, 1),
(21, 'Bí mật về đảo sex chỉ phục vụ 30 khách mỗi năm', 4, '1541401629.sex-island-9-900x485-154117114-2187-9183-1541171506.jpg', '<p>Những bữa tiệc th&acirc;u đ&ecirc;m với người nổi tiếng, g&aacute;i b&aacute;n hoa, chất k&iacute;ch th&iacute;ch... l&agrave; mốt nghỉ dưỡng g&acirc;y tranh c&atilde;i của kh&aacute;ch du lịch phương T&acirc;y.</p>\r\n\r\n<p>Sau khi dư luận dậy s&oacute;ng trước loạt th&ocirc;ng tin quảng c&aacute;o về kỳ nghỉ tr&ecirc;n &quot;đảo sex&quot; tại Colombia ra mắt cuối năm ngo&aacute;i, ban tổ chức đ&atilde; chuyển lịch tr&igrave;nh sang một h&ograve;n đảo ngo&agrave;i khơi Caribbean.&nbsp;</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Du thuyền đưa khách ra đảo tư nhân. Ảnh: New York Post.\" src=\"https://i-dulich.vnecdn.net/2018/11/03/sex-island-2-1541171205-3630-1-5392-6645-1541258875.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Du thuyền đưa kh&aacute;ch ra đảo tư nh&acirc;n. Ảnh:&nbsp;<em>New York Post.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Kỳ nghỉ suy đồi n&agrave;y sẽ t&aacute;i diễn trong th&aacute;ng 12 năm nay, k&eacute;o d&agrave;i bốn ng&agrave;y, tiệc t&ugrave;ng th&acirc;u đ&ecirc;m suốt s&aacute;ng với rượu, thuốc kh&ocirc;ng giới hạn. Những vị kh&aacute;ch muốn tham gia phải trả gần 6.000 USD để sở hữu tấm v&eacute; v&agrave;ng.</p>\r\n\r\n<p>Ph&oacute;ng vi&ecirc;n&nbsp;<em>Sun</em>&nbsp;đ&atilde; b&iacute; mật tiếp cận một vị kh&aacute;ch từng c&oacute; mặt trong cuộc phi&ecirc;u lưu g&acirc;y tranh c&atilde;i hồi năm ngo&aacute;i. Ryan, 33 tuổi, hiện l&agrave; nha sĩ tại New York, Mỹ. &Ocirc;ng bố hai con n&agrave;y t&igrave;nh cờ thấy quảng c&aacute;o về h&agrave;nh tr&igrave;nh n&agrave;y tr&ecirc;n một tờ b&aacute;o v&agrave; quyết định l&ecirc;n đường khi vợ đi c&ocirc;ng t&aacute;c.</p>\r\n\r\n<p>Nhạc nổi l&ecirc;n chỉ v&agrave;i ph&uacute;t sau khi 30 kh&aacute;ch c&ugrave;ng l&ecirc;n du thuyền sang trọng với 60 c&ocirc; g&aacute;i b&aacute;n hoa để tới h&ograve;n đảo nhiệt đới thuộc sở hữu tư nh&acirc;n. Chất k&iacute;ch th&iacute;ch lu&ocirc;n sẵn c&oacute; v&agrave; những hoạt động th&aacute;c loạn k&eacute;o d&agrave;i bất tận.&nbsp;Phần lớn kh&aacute;ch đều l&agrave; những người đ&agrave;n &ocirc;ng th&agrave;nh đạt trong nhiều lĩnh vực từ b&aacute;c sĩ tới luật sư, khoảng 25-50 tuổi v&agrave; một nửa trong số đ&oacute; đ&atilde; c&oacute; gia đ&igrave;nh.</p>\r\n\r\n<p>&quot;C&oacute; v&agrave;i ch&agrave;ng trai tr&iacute; thức từ Thung lũng Silicon, họ chưa từng l&agrave;m chuyện n&agrave;y trước đ&oacute; n&ecirc;n rất lo lắng khi bắt đầu. T&ocirc;i gặp hai người đ&agrave;n &ocirc;ng Anh, một doanh nh&acirc;n v&agrave; một luật sư. Kh&aacute;ch khứa l&agrave; người Canada, Australia v&agrave; rất nhiều người Mỹ&quot;, Ryan n&oacute;i.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Hình ảnh gây tranh cãi trong video giới thiệu kỳ nghỉ. Ảnh: News.\" src=\"https://i-dulich.vnecdn.net/2018/11/02/sex-island-9-900x485-154117114-2187-9183-1541171506.jpg\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>H&igrave;nh ảnh g&acirc;y tranh c&atilde;i trong video giới thiệu kỳ nghỉ. Ảnh:&nbsp;<em>News</em>.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Khi du thuyền đến đảo, c&aacute;c kh&aacute;ch chuyển tới một kh&aacute;ch sạn năm sao sang trọng.&nbsp;Những hoạt động giải tr&iacute; kh&aacute;c gồm nhảy salsa, nh&agrave;o lộn từ du thuyền, một đ&ecirc;m tiệc disco.&nbsp;&quot;Đ&oacute; l&agrave; điều đi&ecirc;n rồ nhất t&ocirc;i từng l&agrave;m trong đời&quot;, Ryan nhận định về những g&igrave; anh đ&atilde; trải qua.&nbsp;</p>\r\n\r\n<p>C&acirc;u chuyện của Ryan một lần nữa dấy l&ecirc;n nhiều &yacute; kiến tr&aacute;i chiều khi được chia sẻ tr&ecirc;n&nbsp;<em>Fox</em>, thu h&uacute;t gần 3.000 b&igrave;nh luận.</p>\r\n\r\n<p>Nhiều người chỉ tr&iacute;ch những người đ&agrave;n &ocirc;ng n&agrave;y đang n&eacute;m tiền qua cửa sổ v&agrave; c&oacute; nguy cơ l&acirc;y bệnh truyền nhiễm qua đường t&igrave;nh dục do lối sống th&aacute;c loạn.&nbsp;&quot;Ch&agrave;o mừng tới đảo Herpes&quot;, một người mỉa mai.</p>\r\n\r\n<p>&quot;6.000 USD n&agrave;y sẽ k&eacute;o theo 100.000 USD để ly dị v&agrave; chữa HIV&quot;, t&agrave;i khoản Julio Gomez b&igrave;nh luận.</p>\r\n\r\n<p>Một người kh&aacute;c b&agrave;y tỏ: &quot;T&ocirc;i l&agrave; một người đ&agrave;n &ocirc;ng hạnh ph&uacute;c trong h&ocirc;n nh&acirc;n v&agrave; sẽ kh&ocirc;ng bao giờ nghĩ đến thứ g&igrave; thế n&agrave;y. Thật gh&ecirc; tởm. Bao nhi&ecirc;u b&agrave; vợ sẽ phải chịu hậu quả từ những cuộc ăn chơi của c&aacute;c &ocirc;ng chồng thế n&agrave;y?&quot;</p>\r\n', 0, 0, 1),
(22, 'Kinh nghiệm du lịch Bỉ bạn nên biết', 4, '1541401897.kinh-nghiem-du-lich-bi-ban-nen-biet-1-768x356.jpg', '<p>Nằm ở v&ugrave;ng trung t&acirc;m T&acirc;y &Acirc;u, nước Bỉ gi&aacute;p với Ph&aacute;p, H&agrave; Lan, Luxembourg v&agrave; Đức. Ở Bỉ người ta sử dụng 3 ng&ocirc;n ngữ ch&iacute;nh l&agrave; Ph&aacute;p, H&agrave; Lan v&agrave; Đức. V&igrave; vậy&nbsp;nếu bạn c&oacute; một tour&nbsp;du lịch Bỉ v&agrave; c&oacute; biết ch&uacute;t &iacute;t về 1 trong 3 nước n&agrave;y sẽ gi&uacute;p cho chuyến du lịch của bạn thuận tiện v&agrave; dễ d&agrave;ng hơn đấy. V&agrave; Bỉ cũng l&agrave; 1 quốc gia c&oacute; nền kinh tế v&ocirc; c&ugrave;ng ph&aacute;t triển ở ch&acirc;u &Acirc;u.</p>\r\n\r\n<p>Thời điểm th&iacute;ch hợp để đi du lịch</p>\r\n\r\n<p><a href=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-2.jpg\"><img alt=\"Kinh nghiệm du lịch Bỉ bạn nên biết\" src=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-2-1024x473.jpg\" style=\"height:314px; width:680px\" /></a></p>\r\n\r\n<p>Kinh nghiệm du lịch Bỉ bạn n&ecirc;n biết: Thời điểm th&iacute;ch hợp để đi du lịch</p>\r\n\r\n<p>V&igrave; ở Bỉ thuộc v&ugrave;ng kh&iacute; hậu &ocirc;n đới n&ecirc;n thời tiết nơi đ&acirc;y quanh năm v&ocirc; c&ugrave;ng m&aacute;t mẻ v&agrave; dễ chịu. Nhiệt độ v&agrave;o m&ugrave;a h&egrave; trung b&igrave;nh khoảng 23 độ C v&agrave; m&ugrave;a đ&ocirc;ng l&agrave; khoảng 1 độ C. Thời gian th&iacute;ch hợp nhất để đi du lịch Bỉ c&oacute; lẽ l&agrave; v&agrave;o&nbsp;<strong>đầu th&aacute;ng 4</strong>&nbsp;cho đến&nbsp;<strong>th&aacute;ng 5</strong>. Tuy vậy bạn thực sự c&oacute; thể đến Bỉ v&agrave;o bất cứ thời điểm n&agrave;o trong năm m&agrave; kh&ocirc;ng gặp nhiều vấn đề về thời tiết. Một điều m&agrave; bạn n&ecirc;n để &yacute; đ&oacute; l&agrave; nếu bạn c&oacute; visa Schengen th&igrave; kh&ocirc;ng cần phải xin visa Bỉ.</p>\r\n\r\n<p>&gt;&gt;Xem th&ecirc;m:&nbsp;<strong><a href=\"http://tourdulichgiare.vn/cac-diem-du-lich-bi.html\">C&aacute;c điểm du lịch Bỉ</a></strong></p>\r\n\r\n<h3>Di chuyển</h3>\r\n\r\n<p><a href=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-3.jpg\"><img alt=\"Kinh nghiệm du lịch Bỉ bạn nên biết\" src=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-3.jpg\" style=\"height:452px; width:680px\" /></a></p>\r\n\r\n<p>Kinh nghiệm du lịch Bỉ bạn n&ecirc;n biết: Di chuyển</p>\r\n\r\n<p>Nếu di chuyển từ Việt Nam đến Bỉ th&igrave; kh&ocirc;ng c&oacute; g&igrave; qu&aacute; kh&oacute; khăn. Bạn c&oacute; thể lựa chọn v&ocirc; số h&atilde;ng h&agrave;ng kh&ocirc;ng c&oacute; uy t&iacute;n như&nbsp;<strong>Qatar Airways</strong>,<strong>&nbsp;Turkish Airlines</strong>,&nbsp;<strong>Thai Airways,</strong>&hellip; C&ograve;n nếu bạn đang đi 1 chuyến du lịch xuy&ecirc;n ch&acirc;u &Acirc;u th&igrave; c&oacute; thể sử dụng&nbsp;<strong>t&agrave;u hỏa</strong>&nbsp;từ c&aacute;c nước gần Bỉ như&nbsp;<strong>H&agrave; Lan</strong>,&nbsp;<strong>Anh</strong>,&nbsp;<strong>Ph&aacute;p</strong>,&nbsp;<strong>Đức</strong>&hellip; để đến Bỉ.</p>\r\n\r\n<p>V&igrave; hệ thống giao th&ocirc;ng ở Bỉ v&ocirc; c&ugrave;ng hiện đại v&agrave; ph&aacute;t triển n&ecirc;n bạn c&oacute; thể ho&agrave;n to&agrave;n an t&acirc;m về việc đi lại ở đ&acirc;y. Nếu muốn tiết kiệm chi ph&iacute; th&igrave;&nbsp;<strong>xe lửa</strong>&nbsp;v&agrave;<strong>&nbsp;xe bu&yacute;t</strong>&nbsp;l&agrave; 2 phương tiện m&agrave; bạn c&oacute; thể nghĩ đến. Ngo&agrave;i ra bạn cũng c&oacute; thể thu&ecirc;&nbsp;<strong>taxi</strong>&nbsp;hoặc những chiếc&nbsp;<strong>xe</strong>&nbsp;<strong>đạp</strong>&nbsp;để c&oacute; thể đến những địa điểm tham quan gần nơi bạn lưu tr&uacute;.</p>\r\n\r\n<p>Địa điểm du lịch ở Bỉ</p>\r\n\r\n<p>Du lịch tại Vương Quốc Bỉ&nbsp;chắc chắn kh&ocirc;ng thể bỏ qua những địa điểm đ&atilde; l&agrave;m n&ecirc;n h&igrave;nh ảnh của vương quốc n&agrave;y. C&oacute; thể l&agrave; c&ocirc;ng vi&ecirc;n&nbsp;<strong>mini europe&nbsp;</strong>xinh đẹp, c&aacute;c nh&agrave; thờ&nbsp;<em>cathedral of st michael and st gudula</em>cổ k&iacute;nh&hellip; Ngo&agrave;i ra c&aacute;c&nbsp;<em>địa điểm du lịch ở Brussel</em>&nbsp;cũng l&agrave; nơi đ&aacute;ng để tham quan.</p>\r\n\r\n<p>Ngo&agrave;i ra việc ngồi dưới m&aacute;i hi&ecirc;n của những nh&agrave; h&agrave;ng, thưởng thức một t&aacute;ch cafe, ngắm nh&igrave;n đường phốBỉ tấp nập cũng&nbsp;l&agrave; một trong những h&agrave;nh động m&agrave; du kh&aacute;ch cảm thấy th&iacute;ch th&uacute; khi đến Bỉ. Như một c&aacute;ch để họ h&ograve;a m&igrave;nh v&agrave;o cuộc sống c&ugrave;ng với người d&acirc;n bản xứ nơi đ&acirc;y vậy.</p>\r\n\r\n<h3>Thu&ecirc; kh&aacute;ch sạn</h3>\r\n\r\n<p><a href=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-4.jpg\"><img alt=\"Kinh nghiệm du lịch Bỉ bạn nên biết\" src=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-4-1024x681.jpg\" style=\"height:453px; width:680px\" /></a></p>\r\n\r\n<p>Kinh nghiệm du lịch Bỉ bạn n&ecirc;n biết: Thu&ecirc; kh&aacute;ch sạn</p>\r\n\r\n<p>Ở Bỉ tuy kh&ocirc;ng nhiều kh&aacute;ch sạn như những nước kh&aacute;c nhưng nh&igrave;n chung kh&aacute;ch sạn nơi đ&acirc;y cũng v&ocirc; c&ugrave;ng đầy đủ tiện nghi. Gi&aacute; cả cũng v&ocirc; c&ugrave;ng hợp l&iacute; n&ecirc;n bạn c&oacute; thể an t&acirc;m lựa chọn kh&aacute;ch sạn ph&ugrave; hợp với nhu cầu của bản th&acirc;n. Bạn&nbsp;<strong>n&ecirc;n đặt ph&ograve;ng sớm</strong>&nbsp;để được giảm gi&aacute; v&agrave; tr&aacute;nh trường hợp hết ph&ograve;ng v&agrave;o những m&ugrave;a du lịch ở Bỉ. Bạn cũng n&ecirc;n đặt ph&ograve;ng&nbsp;<strong>gần những điểm du lịch</strong>&nbsp;m&agrave; bạn đang c&oacute; &yacute; định đến để tiết kiệm thời gian di chuyển.</p>\r\n\r\n<p>&gt;&gt;Xem th&ecirc;m:&nbsp;<strong><a href=\"http://tourdulichgiare.vn/nhung-mon-an-doc-dao-cua-am-thuc-bi.html\">Những m&oacute;n ăn độc đ&aacute;o của ẩm thực Bỉ</a></strong></p>\r\n\r\n<h3>Những m&oacute;n ăn ở Bỉ</h3>\r\n\r\n<p><a href=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-5.jpg\"><img alt=\"Kinh nghiệm du lịch Bỉ bạn nên biết\" src=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-5.jpg\" style=\"height:505px; width:680px\" /></a></p>\r\n\r\n<p>Kinh nghiệm du lịch Bỉ bạn n&ecirc;n biết: Những m&oacute;n ăn ở Bỉ</p>\r\n\r\n<p>Ở Bỉ c&oacute; kh&aacute; nhiều m&oacute;n ăn th&uacute; vị m&agrave; bạn n&ecirc;n thử 1 lần trong đời. M&oacute;n đầu ti&ecirc;n được nhắc đến đ&oacute; ch&iacute;nh l&agrave;<strong>Carbonnade Flamande</strong>. Đ&acirc;y l&agrave; 1 trong những m&oacute;n ăn ngon nổi tiếng ở Bỉ. M&oacute;n ăn n&agrave;y cũng c&oacute; thể t&igrave;m kiếm kh&aacute; dễ d&agrave;ng khi ch&uacute;ng c&oacute; mặt ở rất nhiều qu&aacute;n ăn v&agrave; nh&agrave; h&agrave;ng khắp nước Bỉ.&nbsp;<strong>Thịt b&ograve; sống Steak Tartare</strong>&nbsp;cũng l&agrave; 1 trong những m&oacute;n ăn th&uacute; vị m&agrave; bạn n&ecirc;n thử khi đến đ&acirc;y. Ngo&agrave;i ra c&ograve;n c&oacute; 1 số m&oacute;n kh&aacute;c m&agrave; bạn cũng n&ecirc;n thử như&nbsp;<strong>waterzooi</strong>,&nbsp;<strong>boterhamme</strong>&hellip;</p>\r\n\r\n<p>Ở Bỉ th&igrave;&nbsp;<strong>bia</strong>&nbsp;v&agrave;&nbsp;<strong>chocolate</strong>&nbsp;l&agrave; 2 thứ m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua. Bỉ được biết đến l&agrave; đất nước l&agrave;m n&ecirc;n những loại bia ngon bậc nhất thế giới. Chocolate cũng xuất hiện v&agrave; l&agrave;m n&ecirc;n những n&eacute;t nghệ thuật v&ocirc; c&ugrave;ng độc đ&aacute;o của ẩm thực Bỉ. Những m&oacute;n b&aacute;nh ngọt nơi đ&acirc;y cũng được người d&acirc;n địa phương cũng như du kh&aacute;ch v&ocirc; c&ugrave;ng ưa chuộng.</p>\r\n\r\n<p>&gt;&gt;Xem th&ecirc;m:&nbsp;<strong><a href=\"http://tourdulichgiare.vn/mua-sam-khi-di-du-lich-bi.html\">Mua sắm khi đi du lịch Bỉ</a></strong></p>\r\n\r\n<h3>Mua sắm ở Bỉ</h3>\r\n\r\n<p><a href=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-6.jpg\"><img alt=\"Kinh nghiệm du lịch Bỉ bạn nên biết\" src=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-6.jpg\" style=\"height:457px; width:680px\" /></a></p>\r\n\r\n<p>Kinh nghiệm du lịch Bỉ bạn n&ecirc;n biết: Mua sắm ở Bỉ</p>\r\n\r\n<p>Ở Bỉ th&igrave; nh&igrave;n chung gi&aacute; cả sẽ cao hơn rất nhiều so với ở Việt Nam. Nếu bạn l&agrave; người c&oacute; điều kiện th&igrave; việc mua sắm cũng kh&ocirc;ng c&oacute; g&igrave; đ&aacute;ng lo ngại đối với v&iacute; tiền của bạn. Tuy vậy d&ugrave; kh&ocirc;ng qu&aacute; dư giả th&igrave; bạn cũng c&oacute; thể mua 1 số thứ l&agrave;m qu&agrave; cho những người th&acirc;n của m&igrave;nh. C&oacute; 2 con đường mua sắm nổi tiếng m&agrave; bạn c&oacute; thể gh&eacute; đến đ&oacute; l&agrave;&nbsp;<strong>Rue Antoine Dansaert</strong>&nbsp;v&agrave;&nbsp;<strong>Boulevard de Waterloo</strong>. Ngo&agrave;i ra th&igrave;&nbsp;<strong>khu chợ Brussel</strong>cũng c&oacute; kh&aacute; nhiều thứ th&uacute; vị m&agrave; bạn c&oacute; thể kh&aacute;m ph&aacute; khi đến đ&acirc;y.</p>\r\n\r\n<p>&gt;&gt;Xem th&ecirc;m:&nbsp;<a href=\"http://tourdulichgiare.vn/tag/tour-du-lich-bi\"><strong>Kinh nghiệm du lịch Bỉ từ A-Z</strong></a></p>\r\n\r\n<h3>Những lưu &yacute; kh&aacute;c khi đi du lịch Bỉ</h3>\r\n\r\n<p><a href=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-7.jpg\"><img alt=\"Kinh nghiệm du lịch Bỉ bạn nên biết\" src=\"http://tourdulichgiare.vn/wp-content/uploads/2015/10/kinh-nghiem-du-lich-bi-ban-nen-biet-7.jpg\" style=\"height:502px; width:680px\" /></a></p>\r\n\r\n<p>Kinh nghiệm du lịch Bỉ bạn n&ecirc;n biết: Những lưu &yacute; kh&aacute;c khi đi du lịch Bỉ</p>\r\n\r\n<p>Khi di chuyển ngo&agrave;i đường th&igrave; bạn đừng n&ecirc;n mang những t&agrave;i sản qu&iacute; đi theo để đề ph&ograve;ng trường hợp mất cắp. Tuy Bỉ l&agrave; 1 đất nước ph&aacute;t triển nhưng c&aacute;i g&igrave; cũng c&oacute; thể xảy ra v&igrave; vậy bạn kh&ocirc;ng n&ecirc;n chủ quan. Về việc chuẩn bị h&agrave;nh l&iacute; th&igrave; bạn n&ecirc;n chuẩn bị những loại quần &aacute;o tiện dụng dễ thay để c&oacute; thể thuận lợi trong việc thay đồ cũng như hoạt động trong chuyến du lịch của bạn. Với chị em phụ nữ th&igrave; n&ecirc;n mang những bộ kit sản phẩm mĩ phẩm nhỏ để tr&aacute;nh mang v&aacute;c cả 1 vali mỹ phẩm theo để sử dụng. Nếu bạn c&oacute; 1 chuyến h&agrave;nh tr&igrave;nh d&agrave;i th&igrave; c&oacute; thể để v&agrave;i chai nước trong ba l&ocirc; của m&igrave;nh tr&aacute;nh việc kh&ocirc;ng c&oacute; nước uống trong chuyến đi.</p>\r\n', 0, 0, 1),
(23, 'Du lịch Hà Giang mùa hoa tam giác mạch nên đi mấy ngày?', 4, '1541402051.1.jpg', '<p>Th&aacute;ng 10, H&agrave; Giang bước v&agrave;o m&ugrave;a hoa tam gi&aacute;c mạch rất đẹp, một độc giả Zing.vn mong bạn đọc tư vấn lịch tr&igrave;nh ph&ugrave; hợp kh&aacute;m ph&aacute; miền đất v&ugrave;ng bi&ecirc;n giới n&agrave;y.</p>\r\n\r\n<p>M&igrave;nh ở miền Trung. Cuối th&aacute;ng 10 n&agrave;y, m&igrave;nh định ra Bắc v&agrave; l&ecirc;n H&agrave; Giang một chuyến. M&igrave;nh rất th&iacute;ch v&ugrave;ng đất nơi địa đầu Tổ quốc n&agrave;y. Theo m&igrave;nh t&igrave;m hiểu,&nbsp; đi H&agrave; Giang cuối th&aacute;ng 10 l&agrave; thời điểm đẹp nhất v&igrave; hoa tam gi&aacute;c mạch nở nhiều.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Du lich Ha Giang mua hoa tam giac mach nen di may ngay? hinh anh 1\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/mdf_eioxrd/2018_09_25/1.jpg\" style=\"height:720px; width:1080px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Nguyễn Hương Lan mong bạn đọc tư vấn lịch tr&igrave;nh đi H&agrave; Giang m&ugrave;a hoa tam gi&aacute;c mạch ph&ugrave; hợp với chuyến đi một m&igrave;nh. Ảnh:&nbsp;<em>@jennylamngo.</em></td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>M&igrave;nh đang băn khoăn kh&ocirc;ng biết n&ecirc;n đi mấy ng&agrave;y, tham quan những địa điểm n&agrave;o v&agrave; chi ph&iacute; mất khoảng bao nhi&ecirc;u? M&igrave;nh muốn l&ecirc;n H&agrave; Giang rồi mới thu&ecirc; xe m&aacute;y để kh&aacute;m ph&aacute;. M&igrave;nh rất mong độc giả&nbsp;tư vấn gi&uacute;p m&igrave;nh.</p>\r\n\r\n<p><em>Bạn đọc c&oacute; thắc mắc cần giải đ&aacute;p hoặc muốn chia sẻ những kinh nghiệm về du lịch c&oacute; thể để lại b&igrave;nh luận b&ecirc;n dưới b&agrave;i viết.</em></p>\r\n', 0, 0, 1),
(25, 'Khám phá đảo ngọc Phú Quốc với dịch vụ bustour Hop-on Hop-off', 4, '1541402198.image001_1.jpg', '<p>Ng&agrave;y 11/10, C&ocirc;ng ty Cổ phần dịch vụ H&agrave;ng kh&ocirc;ng S&acirc;n bay T&acirc;n Sơn Nhất (Sasco) ch&iacute;nh thức khai trương dịch vụ Hop-on Hop-off tại đảo ngọc Ph&uacute; Quốc.</p>\r\n\r\n<p>Ph&uacute; Quốc từ l&acirc;u đ&atilde; được v&iacute; như &ldquo;thi&ecirc;n đường biển đảo&rdquo;. B&ecirc;n cạnh b&atilde;i Sao, b&atilde;i D&agrave;i với bờ c&aacute;t trắng mịn d&agrave;i, biển xanh trong vắt, Ph&uacute; Quốc c&ograve;n thu h&uacute;t du kh&aacute;ch bởi sự gần gũi, mộc mạc của những con suối b&igrave;nh y&ecirc;n, vườn hồ ti&ecirc;u xanh ng&aacute;t, những l&agrave;ng ch&agrave;i nhộn nhịp hay khu chợ đ&ecirc;m sầm uất.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Kham pha dao ngoc Phu Quoc voi dich vu bustour Hop-on Hop-off hinh anh 1\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2018_10_16/image001_1.jpg\" style=\"height:720px; width:1080px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Ph&uacute; Quốc c&oacute; nhiều điểm tham quan hấp dẫn cho du kh&aacute;ch.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Để th&uacute;c đẩy sự ph&aacute;t triển của du lịch Ph&uacute; Quốc trong thời gian tới, c&ocirc;ng ty Sasco khai trương dịch vụ Hop-on Hop-off. Theo đ&oacute;, 15 trạm dừng ch&acirc;n được đặt ở c&aacute;c điểm tham quan nổi tiếng của Ph&uacute; Quốc: khu du lịch B&atilde;i Sao, ch&ugrave;a Hộ Quốc, l&agrave;ng ch&agrave;i H&agrave;m Ninh, khu du lịch suối tranh, Trung t&acirc;m bảo tồn ch&oacute; xo&aacute;y Ph&uacute; Quốc, Ngọc trai Ngọc Hiền, s&acirc;n bay Quốc tế Ph&uacute; Quốc&hellip; Du kh&aacute;ch c&oacute; thể t&ugrave;y &yacute; lướt nhanh c&aacute;c điểm du lịch hay thong thả tận hưởng những khoảnh khắc tuyệt vời ở mỗi điểm dừng. Lịch tr&igrave;nh tham quan do du kh&aacute;ch chủ động, thiết kế ph&ugrave; hợp với quỹ thời gian, &yacute; th&iacute;ch của m&igrave;nh.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Kham pha dao ngoc Phu Quoc voi dich vu bustour Hop-on Hop-off hinh anh 2\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2018_10_16/image003_1.jpg\" style=\"height:3191px; width:4786px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Đại diện c&ocirc;ng ty Sasco trong lễ khai trương dịch vụ Hop-on Hop-off.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Bustour Hop-on Hop-off c&oacute; kh&ocirc;ng gian rộng r&atilde;i với 45 ghế ngồi ph&ugrave; hợp địa h&igrave;nh v&agrave; kh&iacute; hậu của Ph&uacute; Quốc. Tr&ecirc;n xe c&oacute; trang bị Wi-Fi, hệ thống thuyết minh bằng nhiều ng&ocirc;n ngữ kh&aacute;c nhau, giới thiệu n&eacute;t đặc sắc của c&aacute;c điểm tham quan, nhằm gi&uacute;p du kh&aacute;ch hiểu hơn về văn h&oacute;a, con người, lịch sử v&agrave; cảnh quan tươi đẹp của Ph&uacute; Quốc.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Kham pha dao ngoc Phu Quoc voi dich vu bustour Hop-on Hop-off hinh anh 3\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2018_10_16/image005_2.jpg\" style=\"height:2832px; width:4256px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Bustour được đặt tại c&aacute;c điểm tham quan nổi tiếng của Ph&uacute; Quốc.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Chỉ với 220.000 đồng, du kh&aacute;ch sẽ sở hữu một chiếc v&eacute; c&oacute; gi&aacute; trị sử dụng trong 24 giờ kể từ khi k&iacute;ch hoạt. Du kh&aacute;ch c&oacute; thể bắt đầu tour từ bất kỳ điểm dừng n&agrave;o trong h&agrave;nh tr&igrave;nh. Xe hoạt động từ 8h đến 19h h&agrave;ng ng&agrave;y v&agrave; tần suất 40 ph&uacute;t/chuyến (cứ c&aacute;ch 40 ph&uacute;t tại bất kỳ điểm n&agrave;o trong h&agrave;nh tr&igrave;nh đều c&oacute; một xe đến v&agrave; đi).</p>\r\n\r\n<p>V&eacute; bustour Hop-on Hop-off được b&aacute;n tr&ecirc;n website&nbsp;<a href=\"http://www.phuquocbustour.com/\">www.phuquocbustour.com</a>&nbsp;v&agrave; hệ thống dịch vụ của Sasco (Sasco shop, Sasco travel) tại s&acirc;n bay T&acirc;n Sơn Nhất, c&aacute;c đại l&yacute; ở Ph&uacute; Quốc v&agrave; nh&acirc;n vi&ecirc;n tr&ecirc;n bustour Ph&uacute; Quốc.</p>\r\n\r\n<p><strong><a href=\"https://news.zing.vn/video-video-kham-pha-dao-ngoc-phu-quoc-voi-dich-vu-bustour-hop-on-hop-off-post884775.html\" target=\"_blank\">Video - Kh&aacute;m ph&aacute; đảo ngọc Ph&uacute; Quốc với dịch vụ bustour Hop-on Hop-off</a></strong>&nbsp;Ng&agrave;y 11/10, C&ocirc;ng ty Cổ phần dịch vụ H&agrave;ng kh&ocirc;ng S&acirc;n bay T&acirc;n Sơn Nhất (Sasco) ch&iacute;nh thức khai trương dịch vụ Hop-on Hop-off tại đảo ngọc Ph&uacute; Quốc.</p>\r\n', 0, 0, 1),
(26, 'Thư giãn, giảm căng thằng tại Pù luông ngày đầu thu', 4, '1541402371.9.jpg', '<p>Sau chuyến nghỉ dưỡng ở P&ugrave; Lu&ocirc;ng trong 2 ng&agrave;y cuối tuần, m&igrave;nh rất ấn tượng bởi vẻ đẹp nơi đ&acirc;y vượt xa tr&iacute; tưởng tượng của m&igrave;nh cũng như những người bạn đi c&ugrave;ng.</p>\r\n\r\n<p>P&ugrave; Lu&ocirc;ng nằm trong khu bảo tồn quốc gia P&ugrave; Lu&ocirc;ng, thuộc huyện B&aacute; Thước, tỉnh&nbsp;<a href=\"https://news.zing.vn/tieu-diem/thanh-hoa.html\">Thanh H&oacute;a</a>, l&agrave; nơi sinh sống chủ yếu của người d&acirc;n tộc Th&aacute;i, Mường. Xung quanh thi&ecirc;n đường du lịch n&agrave;y c&ograve;n c&oacute; c&aacute;c địa danh độc đ&aacute;o kh&aacute;c cho bạn kh&aacute;m ph&aacute; như bản L&aacute;c (Mai Ch&acirc;u, H&ograve;a B&igrave;nh), suối c&aacute; thần Thanh H&oacute;a, vườn quốc gia C&uacute;c Phương (Ninh B&igrave;nh), rất thuận tiện trong những chuyến tham quan kết hợp.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/4_1.jpg\" style=\"height:640px; width:964px\" /></p>\r\n\r\n<h3>N&ecirc;n đến P&ugrave; Lu&ocirc;ng m&ugrave;a n&agrave;o?</h3>\r\n\r\n<p>P&ugrave; Lu&ocirc;ng c&oacute; thời tiết m&aacute;t mẻ quanh năm, m&ugrave;a n&agrave;o bạn cũng c&oacute; thể đến trải nghiệm. Tuy nhi&ecirc;n, c&aacute; nh&acirc;n m&igrave;nh nghĩ nơi đ&acirc;y đẹp nhất vẫn l&agrave; m&ugrave;a l&uacute;a ch&iacute;n, chớm thu. Ruộng bậc thang ở đ&acirc;y được trồng 2 vụ trong năm, tầm th&aacute;ng 5 v&agrave;o vụ đến th&aacute;ng 6 l&uacute;a ch&iacute;n v&agrave;ng, vụ thứ 2 tầm th&aacute;ng 9 v&agrave;o vụ v&agrave; th&aacute;ng 10 l&uacute;a ch&iacute;n.</p>\r\n\r\n<p>Ch&iacute;nh v&igrave; thế, thời điểm n&agrave;y l&agrave; th&iacute;ch hợp nhất để trải nghiệm P&ugrave; Lu&ocirc;ng, l&uacute;a trải v&agrave;ng khắp một v&ugrave;ng rộng lớn đẹp kh&ocirc;n tả. Ngo&agrave;i ra, kh&iacute; hậu v&agrave;o thu cũng m&aacute;t dịu hơn b&igrave;nh thường.</p>\r\n\r\n<p>g. Bạn c&oacute; thể nhờ người d&acirc;n chở về C&agrave;nh N&agrave;ng để bắt xe kh&aacute;ch nhưng chi ph&iacute; hơi cao.</p>\r\n\r\n<p>C&aacute;ch 3 cũng l&agrave; c&aacute;ch m&igrave;nh lựa chọn, dự định lần n&agrave;y sẽ đến P&ugrave; Lu&ocirc;ng thư gi&atilde;n thật thoải m&aacute;i n&ecirc;n m&igrave;nh đặt lu&ocirc;n &quot;combo&quot; của dịch vụ nơi đ&acirc;y, c&oacute; xe đưa đ&oacute;n 2 chiều đi về, đ&oacute;n v&agrave; trả tại nh&agrave; h&aacute;t Lớn H&agrave; Nội. Ngo&agrave;i ra, combo c&ograve;n bao gồm ph&ograve;ng nghỉ v&agrave; ăn uống, rất ph&ugrave; hợp với ch&uacute;ng m&igrave;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><br />\r\n<img alt=\"\" src=\"/banhkeo/public/uploads/images/9.jpg\" style=\"height:682px; width:1024px\" /></p>\r\n', 0, 0, 1),
(27, 'Du lịch tiết kiệm - xu hướng mới thời hiện đại', 4, '1541402413.122329693.jpg', '<p>Du lịch nhiều nơi với mức gi&aacute; tiết kiệm, ổn định v&agrave; nhiều ưu đ&atilde;i đang trở th&agrave;nh xu hướng, gi&uacute;p dịch vụ thẻ kỳ nghỉ ng&agrave;y c&agrave;ng phổ biến.</p>\r\n\r\n<p>Vừa trở về từ chuyến du lịch ch&acirc;u &Acirc;u d&agrave;i ng&agrave;y, chị Nguyễn Mai Hương (phường Nh&acirc;n Ch&iacute;nh, Thanh Xu&acirc;n, H&agrave; Nội) chia sẻ, mỗi năm gia đ&igrave;nh chị đi du lịch nước ngo&agrave;i 1-2 lần, xa th&igrave; đi ch&acirc;u &Acirc;u, Mỹ, Australia, gần th&igrave; đi c&aacute;c nước ch&acirc;u &Aacute;.</p>\r\n\r\n<p>&ldquo;Đi du lịch l&agrave; c&aacute;ch t&ocirc;i dạy c&aacute;c con học hỏi v&agrave; tiếp nhận những nền văn h&oacute;a mới, c&oacute; th&ecirc;m nhiều trải nghiệm mới để l&agrave;m đầy th&ecirc;m kiến thức, kỹ năng của c&aacute;c con sau n&agrave;y&rdquo;, chị Hương cho biết.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Du lich tiet kiem - xu huong moi thoi hien dai hinh anh 1\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2018_10_04/122329693.jpg\" style=\"height:394px; width:700px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Du lịch gi&aacute; rẻ ng&agrave;y c&agrave;ng được ưa chuộng tại Việt Nam v&agrave; thế giới.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Thuộc diện gia đ&igrave;nh kh&aacute; giả nhưng chị Hương vẫn phải t&iacute;nh to&aacute;n kỹ lưỡng cho c&aacute;c chuyến đi. Chị thường l&ecirc;n kế hoạch sớm để đặt ph&ograve;ng, v&eacute; m&aacute;y bay v&agrave; c&aacute;c dịch vụ. Thỉnh thoảng gia đ&igrave;nh chị cũng đặt qua c&ocirc;ng ty tour, nhưng thường tự đi bởi c&oacute; thể tăng trải nghiệm cho c&aacute;c con ở những điểm đến mới.</p>\r\n\r\n<p>Mới đ&acirc;y khi được giới thiệu về thẻ kỳ nghỉ Crystal Holidays, sản phẩm c&oacute; thể gi&uacute;p gia đ&igrave;nh chị tiết kiệm chi ph&iacute; tối đa cho c&aacute;c chuyến đi, chị Hương h&agrave;o hứng: &rdquo;T&ocirc;i t&igrave;m hiểu ở nước ngo&agrave;i cũng c&oacute; c&aacute;c loại thẻ kỳ nghỉ nhưng tại Việt Nam th&igrave; c&ograve;n mới qu&aacute;. Với những cam kết chắc chắn từ chủ đầu tư - Tập đo&agrave;n Crystal Bay c&ugrave;ng kinh nghiệm trong ng&agrave;nh du lịch, t&ocirc;i tin chắc nhiều gia đ&igrave;nh sẽ th&iacute;ch thẻ n&agrave;y&rdquo;.Theo t&iacute;nh to&aacute;n của chị Hương, t&ugrave;y nước v&agrave; t&ugrave;y địa điểm m&agrave; gi&aacute; ph&ograve;ng chị thường đặt tại kh&aacute;ch sạn 4-5 sao dao động trong khoảng 100-300 USD/đ&ecirc;m. V&igrave; thế, những chuyến đi d&agrave;i h&agrave;ng tuần, chi ph&iacute; kh&aacute;ch sạn tương đối đắt đỏ.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, khi chị sở hữu thẻ Hoa hồng hạn mức 50 năm, hạng kh&aacute;ch sạn 5 sao, chi ph&iacute; đặt ph&ograve;ng chỉ gần 800.000 đồng/đ&ecirc;m, tương đương với chỉ khoảng&nbsp;35 USD/đ&ecirc;m. &ldquo;Một mức gi&aacute; rẻ bất ngờ v&agrave; ổn định trong suốt thời gian sử dụng thẻ. Gia đ&igrave;nh t&ocirc;i c&oacute; thể tiết kiệm tiền để mua sắm v&agrave; đi chơi nhiều nơi hơn&rdquo;, chị Hương h&agrave;o hứng.</p>\r\n\r\n<table align=\"center\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"Du lich tiet kiem - xu huong moi thoi hien dai hinh anh 2\" src=\"https://znews-photo.zadn.vn/w660/Uploaded/wyhktpu/2018_10_04/_NT_4942_copy.jpg\" style=\"height:1365px; width:2048px\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>Tập đo&agrave;n Crystal Bay mang đến kh&aacute;ch h&agrave;ng nhiều trải nghiệm hấp dẫn.</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Cũng như chị Hương, anh Ho&agrave;ng Anh Tuấn tại phường Tr&uacute;c Bạch, Ba Đ&igrave;nh, H&agrave; Nội quyết định sở hữu thẻ Crystal Holidays loại Hoa lan trong 50 năm, ở kh&aacute;ch sạn 5 sao mỗi năm 7 đ&ecirc;m. Theo anh Tuấn, gia đ&igrave;nh anh c&oacute; quyền chia nhỏ số ng&agrave;y nghỉ h&agrave;ng năm th&agrave;nh nhiều kỳ hoặc quy đổi to&agrave;n bộ số ng&agrave;y nghỉ th&agrave;nh lượng ph&ograve;ng tương đương cho một kỳ. Nếu kh&ocirc;ng sử dụng hết kỳ nghỉ, anh được quyền b&aacute;n lại hoặc cho chủ đầu tư - Tập đo&agrave;n Crystal Bay thu&ecirc; lại hoặc số đ&ecirc;m nghỉ c&ograve;n lại với gi&aacute; kh&ocirc;ng thấp hơn 2 lần gi&aacute; đ&ecirc;m nghỉ.</p>\r\n\r\n<p>Trong trường hợp kh&ocirc;ng c&oacute; nhu cầu tiếp tục sử dụng thẻ, từ năm thứ 3 đến năm thứ 10 của thời hạn sử dụng thẻ, anh b&aacute;n lại cho chủ đầu tư với gi&aacute; gốc cộng với một khoản chia sẻ lợi &iacute;ch bằng 6% gi&aacute; gốc/năm cho số năm đ&atilde; sử dụng. Ngo&agrave;i ra, nếu c&oacute; nhu cầu, anh c&ograve;n được quyền ưu ti&ecirc;n mua một sản phẩm bất động sản nghỉ dưỡng (biệt thự, căn hộ dịch vụ, condotel) tại c&aacute;c dự &aacute;n do Crystal Bay l&agrave;m chủ đầu tư.</p>\r\n\r\n<p>Theo &ocirc;ng L&ecirc; Đ&igrave;nh Vinh, Ph&oacute; chủ tịch Timeshare Vietnam, đơn vị độc quyền ph&aacute;t h&agrave;nh Thẻ kỳ nghỉ Crystal Holidays, mức gi&aacute; rẻ kh&ocirc;ng đồng nghĩa với dịch vụ k&eacute;m. Chủ đầu tư Thẻ Crystal Holidays với kinh nghiệm c&ugrave;ng bề d&agrave;y trong ng&agrave;nh du lịch, mạng lưới đối t&aacute;c rộng khắp thế giới cam kết chất lượng dịch vụ đẳng cấp quốc tế.</p>\r\n\r\n<p>Với Crystal Holidays, chỉ cần 2-3 click, người d&ugrave;ng c&oacute; thể đặt ph&ograve;ng th&ocirc;ng qua app tr&ecirc;n điện thoại th&ocirc;ng minh. Kh&ocirc;ng chỉ vậy, chủ đầu tư c&ograve;n sẵn s&agrave;ng hỗ trợ c&aacute;c kh&aacute;ch h&agrave;ng khi trải nghiệm kỳ nghỉ, như visa du lịch, li&ecirc;n hệ đặt c&aacute;c dịch vụ tại điểm đến&hellip;</p>\r\n\r\n<p>Đồng thời, để mang lại sự an t&acirc;m cho du kh&aacute;ch, Tập đo&agrave;n Crystal Bay tặng mỗi chủ thẻ Crystal Holidays bảo hiểm th&acirc;n thể v&agrave; bảo hiểm tai nạn. Đ&acirc;y l&agrave; ưu đ&atilde;i kh&aacute;c biệt v&agrave; cũng l&agrave; t&acirc;m huyết của chủ đầu tư.</p>\r\n\r\n<p>Xu hướng đi du lịch đang ng&agrave;y c&agrave;ng gia tăng ở Việt Nam theo mức thu nhập b&igrave;nh qu&acirc;n đầu người tăng dần qua c&aacute;c năm. Theo Tổng Cục thống k&ecirc;, năm 2017 c&oacute; hơn 73 triệu lượt người Việt đi du lịch trong nước v&agrave; khoảng 6,5 triệu người ra nước ngo&agrave;i du lịch. Việt Nam đang đứng v&agrave;o top 6 nước c&oacute; tốc độ tăng trưởng du lịch nhanh nhất thế giới v&agrave; v&agrave;o danh s&aacute;ch 50 nước c&oacute; nguồn kh&aacute;ch tăng trưởng nhanh nhất thế giới.</p>\r\n\r\n<p>Từ thực tế n&agrave;y, thẻ Crystal Holidays l&agrave; một giải ph&aacute;p hữu &iacute;ch hỗ trợ c&aacute;c gia đ&igrave;nh c&oacute; được c&aacute;c chuyến đi với chi ph&iacute; rẻ, hợp l&yacute; nhưng vẫn được hưởng những dịch vụ đẳng cấp quốc tế.</p>\r\n', 0, 0, 1);
INSERT INTO `tin_tuc` (`id_tin`, `tieu_de`, `danh_muc_id`, `hinh_anh`, `noi_dung`, `luot_xem`, `id_nguoi_dang`, `hien_thi`) VALUES
(28, 'Tất tần tật những kinh nghiệm bạn cần biết trước khi du lịch Bình Ba', 4, '1541402481.kinh-nghiem-du-lich-binh-ba-ivivu.jpg', '<p>B&igrave;nh Ba l&agrave; một đảo nhỏ, diện t&iacute;ch tr&ecirc;n 3km2, thuộc x&atilde; Cam B&igrave;nh, th&agrave;nh phố Cam Ranh, tỉnh Kh&aacute;nh H&ograve;a. C&aacute;ch Nha Trang 60 km, c&aacute;ch th&agrave;nh phố Hồ Ch&iacute;nh Minh tầm 390km. Muốn đến B&igrave;nh Ba, bắt buộc ch&uacute;ng ta phải đến cảng Ba Ng&ograve;i. Cảng Ba Ng&ograve;i c&aacute;ch s&acirc;n bay Cam Ranh 15km, B&igrave;nh Ba c&aacute;ch cảng Ba Ng&ograve;i cũng 15km.</p>\r\n\r\n<p>Trước đ&acirc;y, B&igrave;nh Ba l&agrave; đảo qu&acirc;n sự, hạn chế d&acirc;n ở ngo&agrave;i v&agrave;o đảo. B&acirc;y giờ, luật lệ đ&atilde; được nới lỏng, nhưng vẫn cấm người ngoại quốc. Đ&acirc;y cũng l&agrave; l&yacute; do, v&igrave; sao b&acirc;y giờ đảo B&igrave;nh Ba mới nổi tiếng! Ngo&agrave;i ra, nếu kh&ocirc;ng muốn bị đuổi khỏi đảo sớm, bạn cũng kh&ocirc;ng n&ecirc;n t&ograve; m&ograve; đến những v&ugrave;ng bị qu&acirc;n đội cấm hoặc c&oacute; những h&agrave;nh động g&acirc;y rối, qu&aacute; kh&iacute;ch.</p>\r\n\r\n<p><a href=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu.jpg\"><img alt=\"Toàn cảnh đảo Bình Ba.\" src=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu.jpg\" style=\"height:462px; width:748px\" /></a></p>\r\n\r\n<p>To&agrave;n cảnh đảo B&igrave;nh Ba. Ảnh: lanbienxanh.vn.</p>\r\n\r\n<p>V&igrave; được qu&acirc;n đội quản l&yacute; kh&aacute; kỹ, n&ecirc;n B&igrave;nh Ba vẫn l&agrave; một đảo tương đối hoang sơ, thi&ecirc;n nhi&ecirc;n vẫn c&ograve;n giữ được n&eacute;t mộc mạc, chưa bị con người t&agrave;n ph&aacute;. Dạo gần đ&acirc;y, với lượng kh&aacute;ch k&eacute;o về B&igrave;nh Ba ng&agrave;y c&agrave;ng nhiều, hạ tầng cơ sở chưa ph&aacute;t triển theo kịp với lượng kh&aacute;ch đến, khiến m&ocirc;i trường ở đảo kh&ocirc;ng c&ograve;n trong trẻo như thuở n&agrave;o.</p>\r\n\r\n<h2><strong>N&ecirc;n du lịch B&igrave;nh Ba v&agrave;o thời điểm n&agrave;o?</strong></h2>\r\n\r\n<p>Bạn c&oacute; thể&nbsp;<strong>du lịch&nbsp;B&igrave;nh Ba</strong>&nbsp;l&uacute;c n&agrave;o bạn muốn, nhưng l&yacute; tưởng nhất vẫn l&agrave; từ th&aacute;ng 3 đến th&aacute;ng 9. B&ecirc;n cạnh đ&oacute;, bạn cũng n&ecirc;n cố gắng đến B&igrave;nh Ba v&agrave;o ng&agrave;y thường, bởi ng&agrave;y lễ tết lượng kh&aacute;ch đổ về đ&acirc;y cực đ&ocirc;ng, l&agrave;m bạn kh&oacute; m&agrave; thưởng thức hết vẻ đẹp của đảo. Ngo&agrave;i ra, để kh&ocirc;ng phải chụp giật, bạn cũng n&ecirc;n d&agrave;nh ra khoảng tr&ecirc;n 2 ng&agrave;y ở tr&ecirc;n đảo.</p>\r\n\r\n<h2><strong>Phương tiện di chuyển</strong></h2>\r\n\r\n<p><strong>C&oacute; 3 c&aacute;ch để du lịch B&igrave;nh Ba: M&aacute;y bay, &ocirc; t&ocirc; v&agrave; xe m&aacute;y.</strong></p>\r\n\r\n<p><strong><em>M&aacute;y bay</em></strong>: H&agrave;ng ng&agrave;y, c&oacute; rất nhiều chuyến bay từ th&agrave;nh phố Hồ Ch&iacute; Minh đi Nha Trang. Ngay khi vừa xuống m&aacute;y bay, sẽ c&oacute; h&agrave;ng d&agrave;i taxi đậu sẵn ở s&acirc;n bay Cam Ranh để chở bạn đến cảng Ba Ng&ograve;i. Với 15km, bạn chỉ mất tầm hơn nửa tiếng để đến nơi.</p>\r\n\r\n<p>Lưu &yacute;: bạn phải canh giờ m&aacute;y bay, giờ t&agrave;u ra đảo sao cho hợp l&yacute;, để kh&ocirc;ng xảy ra t&igrave;nh trạng đến cảng phải ngồi đợi mỏi m&ograve;n.</p>\r\n\r\n<p><strong>Vietnam Airlines</strong></p>\r\n\r\n<p>&ndash; Văn ph&ograve;ng khu vực miền Nam S&acirc;n bay Quốc tế T&acirc;n Sơn Nhất</p>\r\n\r\n<p>49 Trường Sơn, Quận T&acirc;n B&igrave;nh, Th&agrave;nh phố Hồ Ch&iacute; Minh.</p>\r\n\r\n<p>Tel: (84.8) 8446667 Fax: (84.8) 8485312 Sita : SGNAVVN</p>\r\n\r\n<p>Email: vna.sro@vietnamair.com.vn</p>\r\n\r\n<p>&ndash; Trung t&acirc;m đặt giữ chỗ qua điện thoại Miền Nam: 08-8320320</p>\r\n\r\n<p><strong>Jet Star Pacific Airline&nbsp;Th&agrave;nh phố Hồ Ch&iacute; Minh<em>:&nbsp;</em></strong>(Giờ mở cửa: Từ 07:30 đến 18:00)</p>\r\n\r\n<p>&ndash; Ph&ograve;ng v&eacute;, số 01 C&ocirc;ng trường M&ecirc; Linh, Quận 1 (84-8) 8.258.101 (84-8) 8.258.102<br />\r\n&ndash; Ph&ograve;ng v&eacute;, số 4 đường Trường Sơn, Quận T&acirc;n B&igrave;nh (84-8) 8.442.705 (84-8) 8.486.256<br />\r\n&ndash; Ph&ograve;ng v&eacute;, số 177 V&otilde; Thị S&aacute;u, Quận 3 (84-8) 2.907.349 (84-8) 9.325.980</p>\r\n\r\n<p><strong>VietJetAir TPHCM</strong></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><em>- S&acirc;n bay Quốc tế T&acirc;n Sơn Nhất</em><br />\r\nTel: +84 (8) 3 547 4174<br />\r\nFax: +84 (8) 3 848 9343</p>\r\n\r\n<p><strong>- VietJetAir Trường Sơn</strong></p>\r\n\r\n<p>Địa chỉ: 2C Trường Sơn/48 Phan Đ&igrave;nh Gi&oacute;t, P.2, Q.T&acirc;n B&igrave;nh, Tp Hồ Ch&iacute; Minh</p>\r\n\r\n<p>Tel: 08 38 454 640<br />\r\nFax: 08 38 454 643<br />\r\nGiờ mở cửa: 07:00 &ndash; Giờ đ&oacute;ng cửa: 21:00 (CN &amp; NL l&agrave;m việc tới 19 :00)</p>\r\n\r\n<p><strong>&ndash; VietJetAir Nguyễn Kiệm</strong></p>\r\n\r\n<p>Địa chỉ: 35 Nguyễn Kiệm, P.3, Q.G&ograve; Vấp<br />\r\nTel:1900 1774<br />\r\nFax: +84 (8) 6 266 7667<br />\r\nGiờ mở cửa: 7:30 &ndash; Giờ đ&oacute;ng cửa: 21:30&nbsp;(CN &amp; NL l&agrave;m việc tới 18:00).</p>\r\n\r\n<p><a href=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu1.jpg\"><img alt=\"Sân bay Cam Ranh.\" src=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu1.jpg\" style=\"height:511px; width:768px\" /></a></p>\r\n\r\n<p>S&acirc;n bay Cam Ranh. Ảnh: Seatimes.com</p>\r\n\r\n<p><strong><em>&Ocirc; t&ocirc;:&nbsp;</em></strong>Xe chất lượng cao, xe tour.</p>\r\n\r\n<p><strong>PHƯƠNG TRANG</strong></p>\r\n\r\n<p>&ndash; Tại Cam Ranh:&nbsp;Địa chỉ: L&ocirc; Số 0, Đường L&ecirc; Duẩn, Cam Lộc, Cam Ranh (gần Bến Xe Cam Ranh).<br />\r\nĐiện thoại: Cam Ranh &ndash; (058) 626 0263</p>\r\n\r\n<p>&ndash; Tại Nha Trang:&nbsp;Điện Thọai: 0583. 524.315<br />\r\n&ndash; Tại TP HCM:&nbsp;Điện Thọai: (08) 38.375.570<br />\r\nĐịa chỉ: 272 Đề Th&aacute;m, Quận 1.&nbsp;Điện Thọai: (08) 38.333.468<br />\r\nĐịa chỉ: 328A L&ecirc; Hồng Phong, Quận 10.</p>\r\n\r\n<p><strong>H&Agrave; LINH</strong></p>\r\n\r\n<p>-Tại Cam Ranh:&nbsp;Địa chỉ: 114 Đại Lộ H&ugrave;ng Vương, Ba Ng&ograve;i, Cam Ranh.<br />\r\nĐiện thoại: Cam Ranh &ndash; 058. 626 0056<br />\r\n&ndash; Tại Nha Trang:&nbsp;Địa chỉ: 14B Trần Hưng Đạo, TP Nha Trang<br />\r\n&ndash; Tại S&agrave;i G&ograve;n:&nbsp;Điện Thoại : 08 350 75 345</p>\r\n\r\n<p><strong>PHƯƠNG NAM</strong></p>\r\n\r\n<p>&ndash; Chạy tuyến S&agrave;i G&ograve;n &ndash; Cam Ranh &ndash; Nha Trang:<br />\r\n+ Khởi h&agrave;nh v&agrave; về tại khu phố T&acirc;y Phạm Ngũ L&atilde;o.<br />\r\n+ Xuất ph&aacute;t tại S&agrave;i G&ograve;n v&agrave; Nha Trang đều 8h-9h tối.<br />\r\n+ Xe giường nằm.<br />\r\n<em>- C&aacute;c điểm giao dịch:</em><br />\r\n+ S&agrave;i G&ograve;n: 275F Phạm Ngũ L&atilde;o. ĐT: (08) 66540386<br />\r\n201 Phạm Ngũ L&atilde;o. ĐT: (08) 66570386<br />\r\n+ Nha Trang: số 3 Nguyễn Ch&aacute;nh. ĐT: (058) 6507677-625.8322<br />\r\nQL1 Suối T&acirc;n, Cam L&acirc;m (058) 374 335<br />\r\nQL1 Cam H&ograve;a, Cam L&acirc;m ĐT: (058) 386 3662<br />\r\nQL1 B&atilde;i Giếng 1, Cam Đức, Cam L&acirc;m. ĐT: (058) 6284755<br />\r\nQL1 TT Cam Đức, Cam L&acirc;m. ĐT: (058) 3859267<br />\r\nQL1 Cam Th&agrave;nh Bắc, Cam L&acirc;m. ĐT: (058) 3859752 &ndash; 2242290.<br />\r\nQL1 Cầu Bắc, Cam Th&agrave;nh Bắc, Cam L&acirc;m. ĐT: (058) 3895376<br />\r\n44B QL1 Cam Ranh. ĐT: (058) 3854232</p>\r\n\r\n<p><strong>H&Ograve;A THUẬN ANH</strong></p>\r\n\r\n<p>&ndash; Tuyến S&agrave;i G&ograve;n &ndash; Cam Ranh &ndash; Nha Trang.<br />\r\n+ S&agrave;i G&ograve;n: Xuất bến v&agrave; về tại 258A L&ecirc; Hồng Phong.<br />\r\n+ Giờ chạy: S&agrave;i G&ograve;n: 20h30, Cam Ranh: 21h30, Nha Trang 20h30.<br />\r\n&ndash; Tuyến Cần Thơ &ndash; Nha Trang xuất ph&aacute;t tại Cần Thơ v&agrave;o thứ 2, 4, 6 l&uacute;c 4h30 chiều. Xe đời mới 36 ghế nằm cao cấp.<br />\r\n<em>- C&aacute;c địa chỉ li&ecirc;n hệ:</em><br />\r\n+ S&agrave;i G&ograve;n: 258A L&ecirc; Hồng Phong. ĐT: (08) 62.722.955 &ndash; 62.725.001.<br />\r\nNg&atilde; tư Thủ Đức số 748-750 XL H&agrave; Nội, Q9. ĐT (08) 37.306.125.<br />\r\n+ Cam Ranh: 95 H&ugrave;ng Vương. ĐT: (058) 3 954 955 &ndash; 221 2955.<br />\r\n+ Nha Trang: 1/24 Trần Quang Khải. ĐT: (058) 3524 471 &ndash; 3522 632.<br />\r\n+ Cần Thơ: 60 đường 3/2 Hưng Lợi, Ninh Kiều. ĐT: (0710) 3888.688<br />\r\n+ Vĩnh Long: Si&ecirc;u Thị CoopMark. ĐT: (070)383 7838.<br />\r\n+ Long Xuy&ecirc;n: Si&ecirc;u thị CoopMark. ĐT: (076) 3940011<br />\r\n+ S&oacute;c Trăng: 5 Ph&uacute; Lợi. ĐT: (079) 3614 858<br />\r\n+ Bạc Li&ecirc;u: 158 H&ograve;a B&igrave;nh, P3. ĐT: (0781)395 8555</p>\r\n\r\n<p><strong>C&Uacute;C T&Ugrave;NG</strong></p>\r\n\r\n<p>&ndash; Chạy tuyến: S&agrave;i G&ograve;n &ndash; Ninh H&ograve;a &ndash; Nha Trang &ndash; Đ&agrave; Nẵng;&nbsp;Vũng T&agrave;u &ndash; Nha Trang v&agrave;&nbsp;Cần Thơ &ndash; Nha Trang.<br />\r\n&ndash; Xe giường nằm ghế ngồi cao cấp.<br />\r\n&ndash; Ph&ograve;ng v&eacute; Nha Trang: (058) 247.1999 &ndash; 625.5666<br />\r\n&ndash; Ph&ograve;ng v&eacute; bến xe miền Đ&ocirc;ng: (08) 3511.6863<br />\r\n&ndash; Tại bến xe Miền Đ&ocirc;ng xuất bến v&agrave;o buổi s&aacute;ng v&agrave; tối mỗi tiếng 1 chuyến (chuyến cuối 10h30 tối).<br />\r\n+ Xe 79B-00001: Xuất bến S&agrave;i G&ograve;n 20h40, xuất bến Nha Trang 9h40.&nbsp;Điện thoại: 0989.305.305<br />\r\n+ Xe số 79D-8050: Xuất bến Nha Trang 10h15, xuất bến S&agrave;i G&ograve;n 21h45.&nbsp;Điện thoại: 0904.447.373 &ndash; 0902.006.455.</p>\r\n\r\n<p><strong>MINH DŨNG</strong></p>\r\n\r\n<p>&ndash; Tại S&agrave;i G&ograve;n<br />\r\n+Tuyến S&agrave;i G&ograve;n &ndash; Ninh H&ograve;a<br />\r\nGiờ xuất bến tại 2 đầu l&agrave; 20h.<br />\r\n+Tuyến S&agrave;i G&ograve;n &ndash; Nha Trang<br />\r\nGiờ xuất bến tại S&agrave;i G&ograve;n: 9h45, 21h30, 22h30.<br />\r\n&ndash; Tại Nha Trang: 20h30, 21h30.<br />\r\n+Tuyến S&agrave;i G&ograve;n &ndash; Cam Ranh<br />\r\nGiờ xuất bến tại S&agrave;i G&ograve;n: 13h.<br />\r\nTại Cam Ranh 22h.<br />\r\nĐiện thoại đặt v&eacute;: (08) 35115234. Văn ph&ograve;ng (08) 3519.0519 &ndash; 37442744</p>\r\n\r\n<p><strong>THE SINH TOURIST (Sinh Caf&eacute;)&nbsp;</strong>(www.thesinhtourist.vn)</p>\r\n\r\n<p>&ndash; Tại Tp.Hồ Ch&iacute; Minh: 246 &ndash; 248 Đường Đề Th&aacute;m, Quận 1, Tp.HCM &ndash; Điện thoại: (08). 38367338-38376833<br />\r\n&ndash; Nha Trang: 90C H&ugrave;ng Vương &ndash; Điện thoại: (058).3524.329</p>\r\n\r\n<p><strong>MAI LINH&nbsp;</strong>(www.mailinh.vn)</p>\r\n\r\n<p>&ndash; Địa chỉ: 64 &ndash; 68 Hai B&agrave; Trưng, P.BN, Q.1, TP Hồ Ch&iacute; Minh<br />\r\nLi&ecirc;n hệ đặt v&eacute;: 08.9292929 (Tp.HCM) hoặc c&aacute;c đại l&yacute; của Mai Linh<br />\r\nNha Trang: 20A, Nguyễn Thiện Thuật, Nha Trang, Kh&aacute;nh H&ograve;a &ndash; Điện thoại: (058).36255.888</p>\r\n\r\n<p>Đặt v&eacute; ở bến xe miền Đ&ocirc;ng: gi&aacute; tất nhi&ecirc;n l&agrave; rẻ hơn c&aacute;c h&atilde;ng xe kể tr&ecirc;n, nhưng chất lượng lại v&ocirc; c&ugrave;ng.</p>\r\n\r\n<p>Bến xe miền Đ&ocirc;ng:&nbsp;292 Đinh Bộ Lĩnh, P.26, Q.B&igrave;nh Thạnh, TP Hồ Ch&iacute; Minh<br />\r\nĐiện thoại: (08) 899 4056; (08) 898 4441; (08) 898 4442; (08) 898 4893<br />\r\nEmail: webmaster@benxemiendong.com.vn<br />\r\nWebsite: www.benxemiendong.com.vn.</p>\r\n\r\n<p><a href=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu2.jpg\"><img alt=\"Một cảnh đẹp của Bình Ba.\" src=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu2.jpg\" style=\"height:467px; width:700px\" /></a></p>\r\n\r\n<p>Một cảnh đẹp của B&igrave;nh Ba. Ảnh: dichvudulichbien.worldexpress.com.</p>\r\n\r\n<p><strong><em>Xe m&aacute;y:</em></strong></p>\r\n\r\n<p>V&igrave; qu&atilde;ng đường tương đối xa, bạn n&ecirc;n c&acirc;n nhắc cẩn thận t&igrave;nh trạng sức khỏe bản th&acirc;n, thời tiết, t&igrave;nh trạng &ldquo;chiến m&atilde;&rdquo; để kh&ocirc;ng xảy ra những điều đ&aacute;ng tiếc tr&ecirc;n đường đi hoặc tới nơi mệt đứt hơi kh&ocirc;ng c&ograve;n sức chơi bời. Từ th&agrave;nh phố Hồ Ch&iacute; minh chạy xe theo quốc lộ 1A (hướng Bắc). Tr&ecirc;n đường đi sẽ đi ngang qua Phan Thiết (B&igrave;nh Thuận), Phan Rang &ndash; Th&aacute;p Ch&agrave;m (Ninh Thuận)&hellip;</p>\r\n\r\n<p>Tốt nhất, bạn n&ecirc;n gửi xe m&aacute;y ở cảng Ba Ng&ograve;i, c&aacute;c b&atilde;i xe, hoặc người quen, rồi một m&igrave;nh thong dong ra đảo. Đầu ti&ecirc;n, muốn mang xe ra đảo, bạn phải thu&ecirc; thuyền ri&ecirc;ng. Sau nữa, đảo nhỏ, đi bộ v&agrave;i v&ograve;ng l&agrave; hết. C&ograve;n nếu muốn kh&aacute;m ph&aacute; nhiều ng&oacute;c ng&aacute;ch xa hơn, bạn c&oacute; thể thu&ecirc; xe m&aacute;y.</p>\r\n\r\n<h2><strong>Giờ t&agrave;u chạy từ cảng Ba Ng&ograve;i ra B&igrave;nh Ba</strong></h2>\r\n\r\n<p><strong>&nbsp;</strong>Từ cảng Ba Ng&ograve;i phương tiện duy nhất để đi ra đảo B&igrave;nh Ba l&agrave; t&agrave;u.</p>\r\n\r\n<p>T&agrave;u chạy tuyến Ba Ng&ograve;i &ndash; B&igrave;nh Ba khởi h&agrave;nh h&agrave;ng ng&agrave;y. Gi&aacute; v&eacute;: 25.000 &ndash; 30.000 đồng/người (t&ugrave;y loại t&agrave;u kh&aacute;ch).</p>\r\n\r\n<p><em>Giờ t&agrave;u chạy</em>:</p>\r\n\r\n<p>&ndash; Ba Ng&ograve;i &ndash; B&igrave;nh Ba: 7h s&aacute;ng, 10h s&aacute;ng, 1h30 chiều, 4h chiều.<br />\r\n&ndash; B&igrave;nh Ba &ndash; Ba Ng&ograve;i: 5h s&aacute;ng, 9h s&aacute;ng, 12h30 trưa, 4h30 chiều.</p>\r\n\r\n<p>Nếu thu&ecirc; t&agrave;u ri&ecirc;ng th&igrave; giờ khởi h&agrave;nh do người thu&ecirc; quyết định.<br />\r\n&ndash; Li&ecirc;n hệ thu&ecirc; t&agrave;u: 0976.789.186 (Anh Hậu) hoặc 0944.506.371 (qu&ecirc;n t&ecirc;n).</p>\r\n\r\n<h2><strong>Chỗ ở</strong></h2>\r\n\r\n<p>Tr&ecirc;n đảo cũng c&oacute; kha kh&aacute; nh&agrave; nghỉ, đa phần đều đầy đủ tiện nghỉ. Gi&aacute; thường l&agrave; 100k/người v&agrave;o c&aacute;c ng&agrave;y trong tuần (cuối tuần sẽ đắt hơn gấp rưỡi hoặc gấp đ&ocirc;i). Trong c&aacute;c nh&agrave; nghỉ th&igrave; nổi tiếng nhất l&agrave; nh&agrave; nghỉ của ch&uacute; Bảy Hộ (0983 858 941), v&igrave; vị tr&iacute; ngay s&aacute;t b&atilde;i Nồm, c&oacute; d&atilde;y ph&ograve;ng nh&igrave;n ra biển, lại sạch sẽ, đầy đủ (chỉ kh&ocirc;ng c&oacute; tủ lạnh).</p>\r\n\r\n<p>Nh&agrave; nghỉ của chị Dung 0972332202, gi&aacute; lưu tr&uacute; khoảng 70.000đ/ đ&ecirc;m, ăn 70.000đ/ bữa. Nh&agrave; chị Tuyền 0914.493.793, hoặc số điện thoại mẹ chị Tuyền 0962.466.528. Nh&agrave; nghỉ rộng r&atilde;i m&aacute;t mẻ, ở được khoảng 40 kh&aacute;ch, c&oacute; khoảng 7 ph&ograve;ng tắm/ WC.</p>\r\n\r\n<p><a href=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu3.jpg\"><img alt=\"Bãi Nhà Cũ xinh đẹp.\" src=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu3.jpg\" style=\"height:417px; width:700px\" /></a></p>\r\n\r\n<p>B&atilde;i Nh&agrave; Cũ xinh đẹp. Ảnh: dichvudulichbien.worldpress.com.</p>\r\n\r\n<p>Nếu muốn tiết kiệm hơn, kh&ocirc;ng cần nhiều tiện nghi, chỉ cần chỗ ở th&igrave; c&oacute; thể ở homestay, gi&aacute; chỉ bằng ph&acirc;n nửa gi&aacute; nh&agrave; nghỉ. Ở homestay th&igrave; c&oacute; chỗ của chị &Uacute;t Tăng (0918 948 953). C&oacute; thể hỏi chị &Uacute;t Tăng hoặc hỏi bất cứ ai ở khu b&atilde;i Nồm để t&igrave;m nh&agrave; nguy&ecirc;n căn. Ngo&agrave;i ra c&ograve;n chị Ngọc T&uacute;, số điện thoại: 0942016958. Nh&agrave; rộng r&atilde;i, tho&aacute;ng m&aacute;t, sạch sẽ đủ tiện nghi. Gần chợ v&agrave; c&aacute;c b&atilde;i tắm. Th&iacute;ch hợp với c&aacute;c đo&agrave;n du lịch khoảng 20 người. Tầm tr&ecirc;n dưới 1 triệu/ng&agrave;y.</p>\r\n\r\n<p>C&aacute;c bạn c&oacute; thể li&ecirc;n hệ trước để khi xuống đ&ograve; ở cảng sẽ c&oacute; người lấy xe m&aacute;y chở đến nh&agrave; nghỉ lu&ocirc;n. Người d&acirc;n tr&ecirc;n đảo n&agrave;y rất thoải m&aacute;i v&agrave; dễ thương, bạn c&oacute; thể v&agrave;o xe ph&ograve;ng sau đ&oacute; mới quyết định ở hay đi chỗ kh&aacute;c m&agrave; chẳng ai phiền l&ograve;ng hay cằn nhằn g&igrave; cả. Hơn nữa, hầu như ai cũng sẽ nhiệt t&igrave;nh chỉ dẫn bạn nhiều điều th&uacute; vị.</p>\r\n\r\n<h2><strong>Ăn</strong></h2>\r\n\r\n<p>B&igrave;nh Ba nổi tiếng l&agrave; đảo t&ocirc;m h&ugrave;m v&agrave; hải sản tươi sống, thế n&ecirc;n nhất định ch&uacute;ng ta kh&ocirc;ng thể bỏ qua khoản đặc sản n&agrave;y rồi. Bạn c&oacute; thể nhờ người tổ chức tour để vừa đi t&agrave;u ra c&aacute;c b&atilde;i tắm, vừa ăn uống tr&ecirc;n b&egrave;. T&ocirc;m th&igrave; ở đ&acirc;y c&oacute; t&ocirc;m h&ugrave;m v&agrave; t&ocirc;m xanh, mỗi loại c&oacute; 2 mức gi&aacute; l&agrave; t&ocirc;m h&ugrave;m ngộp gi&aacute; dao động: 400.000 &ndash; 600.000đ/ kg, t&ocirc;m sống: 1.400.000đ/ kg &ndash; 1.800.000đ/ kg. Gi&aacute; t&ocirc;m t&ugrave;y theo ng&agrave;y, theo m&ugrave;a nhưng chắc chắn l&agrave; sẽ rẻ hơn nơi kh&aacute;c từ 30% đến 50%.</p>\r\n\r\n<p>Tuy nhi&ecirc;n, bạn kh&ocirc;ng n&ecirc;n ăn nhiều qu&aacute;, m&agrave; h&atilde;y d&agrave;nh tới tối. Nếu muốn tổ chức party kết hợp karaoke th&igrave; sẽ c&oacute; người lo trọn g&oacute;i. Nếu muốn &ldquo;thử th&aacute;ch bản th&acirc;n&rdquo; một ch&uacute;t, th&igrave; bạn h&atilde;y tự đi chợ v&agrave; tổ chức buffet hải sản cho ch&iacute;nh m&igrave;nh. Tầm 4 &ndash; 5h chiều, bạn h&atilde;y ra khu vực bến t&agrave;u, đi dọc đ&oacute; sẽ c&oacute; nhiều nơi b&aacute;n hải sản.</p>\r\n\r\n<p>C&oacute; rất nhiều từ t&ocirc;m h&ugrave;m, t&ocirc;m xanh, c&aacute;c loại c&aacute;, ốc biển, hải s&acirc;m, b&agrave;o ngư, h&agrave;u, mực,&hellip; H&atilde;y đi hỏi gi&aacute; v&agrave; chọn cho m&igrave;nh những m&oacute;n ph&ugrave; hợp nhất, sau đ&oacute; nhờ qu&aacute;n chế biến. Bạn c&oacute; thể ngồi ăn ngay tại qu&aacute;n hoặc nhờ họ mang ra b&atilde;i Nồm để c&oacute; thể ăn vừa ngắm biển. Họ sẽ cho bạn mượn hết từ l&ograve;, nồi cho đến ch&eacute;n đũa, việc của bạn chỉ l&agrave; mượn một tấm bạt (hỏi nh&agrave; nghỉ) v&agrave; t&igrave;m vị tr&iacute; th&iacute;ch hợp nhất, rồi tự ăn uống h&aacute;t h&ograve; th&ocirc;i!</p>\r\n\r\n<p><a href=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu4.jpg\"><img alt=\"Tôm hùm - đặc sản Bình Ba. \" src=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu4.jpg\" style=\"height:405px; width:720px\" /></a></p>\r\n\r\n<p>T&ocirc;m h&ugrave;m &ndash; đặc sản B&igrave;nh Ba. Ảnh: catviet.vn.</p>\r\n\r\n<p>Ngo&agrave;i ra, c&ograve;n c&oacute; nhiều h&agrave;ng qu&aacute;n b&aacute;n nhiều m&oacute;n rất ngon v&agrave; rẻ như b&aacute;nh canh/b&uacute;n chả c&aacute; (chi&ecirc;n hoặc hấp), b&uacute;n thịt nướng, m&igrave;, nui, hủ t&iacute;u, sinh tố, b&aacute;nh tr&aacute;ng nướng&hellip; Gi&aacute; c&aacute;c m&oacute;n n&agrave;y chỉ từ 10k &ndash; 20k, qu&aacute; hấp dẫn phải kh&ocirc;ng? Hoặc ở nh&agrave; nghỉ cũng sẽ nhận nấu cơm cho bạn nếu bạn y&ecirc;u cầu, nhưng gi&aacute; 1 phần cơm như thế th&igrave; kh&aacute; đắt (80k -90k/phần) so với chất lượng m&agrave; cũng kh&ocirc;ng c&oacute; g&igrave; nổi bật lắm.</p>\r\n\r\n<p>V&agrave;o mỗi s&aacute;ng sớm, khoảng từ 5h30 &ndash; 7h sẽ c&oacute; họp chợ ở ngay bến t&agrave;u. C&aacute;c t&agrave;u đi đ&aacute;nh c&aacute;, c&acirc;u mực sẽ về v&agrave; c&oacute; nhiều người b&agrave;y b&aacute;n hải sản ngay tr&ecirc;n đ&oacute;, chủ yếu l&agrave; c&aacute; v&agrave; mực, gi&aacute; kh&aacute; rẻ. C&ograve;n muốn mua nhiều loại c&aacute; hơn, đặc biệt l&agrave; c&aacute; bốp th&igrave; c&aacute;c bạn đợi đến 8h s&aacute;ng c&aacute; sẽ l&ecirc;n th&ecirc;m. Thường mọi người buổi s&aacute;ng trước khi rời B&igrave;nh Ba sẽ ra chợ để mua hải sản v&agrave; đ&oacute;ng th&ugrave;ng để mang về. Ở đ&acirc;y c&oacute; b&aacute;n sẵn th&ugrave;ng giữ lạnh v&agrave; bạn sẽ được người b&aacute;n cho đ&aacute; v&agrave; ướp c&aacute;, đ&oacute;ng th&ugrave;ng gi&uacute;p hết nh&eacute;.</p>\r\n\r\n<h2><strong>Chơi</strong></h2>\r\n\r\n<p><strong>Ban ng&agrave;y:&nbsp;Tắm biển v&agrave; ngắm san h&ocirc;</strong></p>\r\n\r\n<p>&ndash; Điều kh&ocirc;ng thể bỏ qua khi&nbsp;<em>du lịch&nbsp;B&igrave;nh Ba</em>&nbsp;l&agrave; thu&ecirc; t&agrave;u đi c&aacute;c b&atilde;i tắm v&agrave; lặn biển xem san h&ocirc;. Thường gi&aacute; t&agrave;u đi trọn g&oacute;i hết tất cả c&aacute;c b&atilde;i l&agrave; 1tr &ndash; 1,2tr/chuyến t&ugrave;y chỗ, do đ&oacute; nếu đi đ&ocirc;ng th&igrave; sẽ tiết kiệm hơn. N&oacute;i l&agrave; &ldquo;lặn ngắm san h&ocirc;&rdquo; nhưng thực ra bạn kh&ocirc;ng nhất thiết phải lặn, chỉ cần mặc &aacute;o phao v&agrave; nằm tr&ecirc;n phao, &uacute;p mặt xuống mặt biển l&agrave; c&oacute; thể xem được rồi, thế n&ecirc;n ai kh&ocirc;ng biết bơi cũng đừng ngại m&agrave; h&atilde;y thử nh&eacute;! N&ecirc;n mang theo k&iacute;nh bơi để dễ bơi v&agrave; lặn, v&igrave; k&iacute;nh lặn mang kh&ocirc;ng quen sẽ hơi kh&oacute; mang v&agrave; kh&oacute; chịu đấy.</p>\r\n\r\n<p>&ndash; Thậm ch&iacute;, bạn c&oacute; thể kh&ocirc;ng cần xuống nước m&agrave; vẫn ngắm san h&ocirc; được. Thỉnh thoảng, ở b&atilde;i Nh&agrave; Cũ khi thủy triều xuống thấp, từng dải san h&ocirc; đầy m&agrave;u sắc sẽ phơi m&igrave;nh dưới &aacute;nh mặt trời, tha hồ cho bạn vừa đi vừa nh&igrave;n ngắm, sờ m&oacute;, thưởng thức.</p>\r\n\r\n<p><a href=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu5.jpg\"><img alt=\"San hô lộ thiên ở bãi Nhà Cũ.\" src=\"https://cdn3.ivivu.com/2014/07/kinh-nghiem-du-lich-binh-ba-ivivu5.jpg\" style=\"height:427px; width:635px\" /></a></p>\r\n\r\n<p>San h&ocirc; lộ thi&ecirc;n ở b&atilde;i Nh&agrave; Cũ. Ảnh: b&aacute;o Thể thao v&agrave; văn h&oacute;a.</p>\r\n\r\n<p>&ndash; C&oacute; rất nhiều b&atilde;i, nhưng thường th&igrave; đi 7 b&atilde;i, trong đ&oacute; c&oacute; 3 b&atilde;i c&oacute; thể đi bằng xe m&aacute;y l&agrave; b&atilde;i Nồm, b&atilde;i Chướng v&agrave; b&atilde;i Nh&agrave; Cũ. B&atilde;i Nồm: đ&acirc;y l&agrave; một b&atilde;i c&aacute;t rất rộng v&agrave; c&oacute; chỗ c&oacute; đ&aacute; ngầm. Đa số người d&acirc;n địa phương v&agrave; du kh&aacute;ch tắm biển ở đ&acirc;y v&igrave; biển rất sạch, trong. B&atilde;i Chướng: Nằm c&aacute;ch cầu cảng chừng 30 ph&uacute;t đi bộ. Đ&acirc;y l&agrave; b&atilde;i biển duy nhất tr&ecirc;n đảo ngắm được b&igrave;nh minh. B&atilde;i Nh&agrave; Cũ: B&atilde;i n&agrave;y c&oacute; nhiều nhum (cầu gai) n&ecirc;n c&oacute; thể bị đ&acirc;m v&agrave;o ch&acirc;n khi tắm. Tuy nhi&ecirc;n b&atilde;i n&agrave;y c&oacute; thể n&oacute;i l&agrave; rất tuyệt vời về sự trong v&agrave; sạch của nước biển.</p>\r\n\r\n<p>&ndash; V&igrave; vậy nếu c&oacute; nhiều thời gian, c&aacute;c bạn chỉ n&ecirc;n thu&ecirc; t&agrave;u đi 4 b&atilde;i c&ograve;n lại th&ocirc;i, thứ nhất l&agrave; gi&aacute; thu&ecirc; t&agrave;u sẽ thấp xuống (c&ograve;n khoảng 600k), thứ hai l&agrave; c&oacute; nhiều thời gian để tắm v&agrave; xem san h&ocirc; hơn ở mỗi nơi, nhớ y&ecirc;u cầu đồ nghề (&aacute;o phao, phao, k&iacute;nh lặn) v&agrave; người hướng dẫn để xem san h&ocirc; đ&uacute;ng chỗ đẹp nhất nh&eacute;.</p>\r\n\r\n<p>&ndash; Ngo&agrave;i ra c&ograve;n c&oacute; dịch vụ cho ăn uống tr&ecirc;n b&egrave; chung với thu&ecirc; t&agrave;u (li&ecirc;n hệ mấy nơi tổ chức tour ở c&aacute;c nh&agrave; nghỉ gần bến t&agrave;u tr&ecirc;n B&igrave;nh Ba l&agrave; c&oacute;, hoặc hỏi bất kỳ người d&acirc;n n&agrave;o).</p>\r\n\r\n<p><strong>Thu&ecirc; xe m&aacute;y, kh&aacute;m ph&aacute; đảo</strong></p>\r\n\r\n<p><em>-&nbsp;</em>Sau phần tắm, xem san h&ocirc; th&igrave; phần th&uacute; vị kh&ocirc;ng k&eacute;m l&agrave; thu&ecirc; xe m&aacute;y để đi kh&aacute;m ph&aacute; đảo. Gi&aacute; thu&ecirc; xe l&agrave; 100k/ng&agrave;y, c&oacute; rất nhiều chỗ cho thu&ecirc; xe tr&ecirc;n đảo v&agrave; c&aacute;c nh&agrave; nghỉ đều c&oacute; lu&ocirc;n dịch vụ n&agrave;y. N&ecirc;n đi xe m&aacute;y từ s&aacute;ng sớm ra b&atilde;i Chướng để ngắm b&igrave;nh minh, sau đ&oacute; chạy kh&aacute;m ph&aacute; hết những đường đ&egrave;o tr&ecirc;n n&uacute;i. C&oacute; nhiều chỗ sẽ cho bạn những tầm nh&igrave;n rất đẹp, đặc biệt l&agrave; 3 điểm cao nhất ở 3 hướng Bắc, Nam v&agrave; Đ&ocirc;ng. Bạn sẽ c&oacute; cơ hội quan s&aacute;t to&agrave;n cảnh vịnh Cam Ranh, ngắm h&ograve;n R&ugrave;a, t&agrave;u duy&ecirc;n hải tuần tra, c&aacute;c t&agrave;u đ&aacute;nh bắt nho nhỏ hay to&agrave;n cảnh khu d&acirc;n cư của đảo đấy.</p>\r\n\r\n<p>&ndash; C&aacute;c di t&iacute;ch cũ như l&ocirc; cốt, bệ s&uacute;ng thần c&ocirc;ng, đường hầm ven n&uacute;i&hellip;cũng rất đ&aacute;ng cho bạn đặt ch&acirc;n tới.</p>\r\n\r\n<p>&ndash; Ở b&atilde;i Nồm c&oacute; 1 khu c&ocirc;ng vi&ecirc;n nho nhỏ trồng nhiều c&acirc;y, gần đấy c&oacute; cho thu&ecirc; v&otilde;ng. M&ocirc;t giấc nghỉ trưa m&aacute;t rượi, đong đưa c&ugrave;ng tiếng s&oacute;ng v&agrave; gi&oacute; biển đ&uacute;ng l&agrave; rất tuyệt đấy. Gi&aacute; thu&ecirc; v&otilde;ng cũng chỉ 5k/c&aacute;i th&ocirc;i (c&oacute; thế ch&acirc;n 50k/c&aacute;i), rất đ&aacute;ng để thử!</p>\r\n\r\n<p><strong>Buổi tối:</strong></p>\r\n\r\n<p><em>-&nbsp;</em>Hoạt động th&uacute; vị đầu ti&ecirc;n m&agrave; bạn n&ecirc;n thử l&agrave; soi c&ograve;ng. C&aacute;c b&atilde;i biển kh&ocirc;ng c&oacute; người ở quanh đảo B&igrave;nh Ba c&oacute; rất nhiều c&ograve;ng, c&aacute;c bạn c&oacute; thể cầm đen pin đi soi v&agrave; rượt c&ograve;ng v&agrave;o buổi tối rất vui. Đặc biệt l&agrave; bạn c&oacute; thể tận hưởng m&oacute;n thịt c&ograve;ng chi&ecirc;n rất ngon sau đ&oacute;.<br />\r\n&ndash; Buổi tối, nếu đi theo nh&oacute;m th&igrave; c&oacute; thể li&ecirc;n hệ anh T&ugrave;ng R&acirc;u (0969 819 797) để anh tổ chức party hải sản + karaoke tr&ecirc;n b&atilde;i biển thật ho&agrave;nh tr&aacute;ng.</p>\r\n\r\n<p>&ndash; Một tr&ograve; kh&aacute;c cũng kh&aacute; th&uacute; vị đ&oacute; l&agrave; li&ecirc;n hệ những chủ b&egrave; để được l&ecirc;n b&egrave; v&agrave; theo đi c&acirc;u t&ocirc;m, c&acirc;u mực. 1 số c&ocirc;ng ty tổ chức tour cũng c&oacute; tổ chức lu&ocirc;n c&aacute;i n&agrave;y, hoặc bạn cứ hỏi bất kỳ người d&acirc;n n&agrave;o tr&ecirc;n đảo, sẽ chẳng kh&oacute; để bạn t&igrave;m được đ&uacute;ng nơi đ&acirc;u</p>\r\n', 0, 0, 1);
INSERT INTO `tin_tuc` (`id_tin`, `tieu_de`, `danh_muc_id`, `hinh_anh`, `noi_dung`, `luot_xem`, `id_nguoi_dang`, `hien_thi`) VALUES
(29, 'Đến nhà cổ Bình Thủy – tham quan ngôi nhà cổ đẹp nhất xứ Tây Đô', 1, '1540407463.nha-co-binh-thuy1-min_1540371371.jpg', '<p>Cuối thế kỉ 18. V&ugrave;ng ph&iacute;a T&acirc;y Nam v&agrave;m s&ocirc;ng Cần Thơ ph&ugrave; sa nước ngọt tưới m&aacute;t quanh năm. Đ&acirc;y cũng l&agrave; nơi thu h&uacute;t nhiều người đổ về chọn đất an cư lập nghiệp. Trong đ&oacute; c&oacute; gia tộc Họ Dương.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Gia tộc Họ Dương đến đất Nam Bộ lập nghiệp, đ&atilde; sống qua nhiều thế hệ tại đ&acirc;y. Ở thế hệ thứ 3, &ocirc;ng Dương Văn Vị cũng l&agrave; người kh&aacute; gi&agrave;u c&oacute; của d&ograve;ng họ, đ&atilde; chọn v&ugrave;ng đất gần v&agrave;m rạch B&igrave;nh Thủy l&agrave;m nơi dựng nh&agrave;.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy1-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Nh&agrave; cổ B&igrave;nh Thủy nh&igrave;n từ ch&iacute;nh diện</em></p>\r\n\r\n<p><br />\r\nL&uacute;c mới đầu, nh&agrave; được &ocirc;ng Vị dựng bằng gỗ v&agrave; lợp ng&oacute;i, d&ugrave;ng để thờ tổ ti&ecirc;n. M&atilde;i đến 30 năm sau đ&oacute; mới bắt đầu được t&ocirc;n tạo lại. Sau khi &ocirc;ng Vị mất, con trai &uacute;t của &ocirc;ng l&agrave; Dương Chấn Kỷ (l&uacute;c đ&oacute; l&agrave; một điền chủ rất gi&agrave;u c&oacute;) tiếp tục x&acirc;y dựng v&agrave; ho&agrave;n thiện ng&ocirc;i nh&agrave; v&agrave;o năm 1911.<br />\r\n&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Kỷ l&agrave; một thương gia rất th&iacute;ch t&igrave;m t&ograve;i c&aacute;i mới lạ, nhất l&agrave; về kiến tr&uacute;c v&agrave; nghệ thuật. Thời bấy giờ đang thịnh h&agrave;nh tr&agrave;o lưu T&acirc;y phương, &ocirc;ng liền cho người x&acirc;y dựng ng&ocirc;i nh&agrave; theo kiến tr&uacute;c ch&acirc;u &Acirc;u.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Những năm 1980, hậu duệ đời thứ 5 của gia tộc họ Dương l&agrave; &ocirc;ng Dương Văn Ng&ocirc;n c&oacute; th&uacute; chơi hoa kiểng. &Ocirc;ng sưu tầm kh&aacute; nhiều giống lan qu&yacute; v&agrave; hay tổ chức giao lưu, chơi lan. Do đ&oacute;, thời n&agrave;y ng&ocirc;i nh&agrave; c&ograve;n c&oacute; thể l&agrave; &ldquo;Vườn lan B&igrave;nh Thủy&rdquo;<br />\r\n&nbsp;</p>\r\n\r\n<h3>Du lịch Cần Thơ nhớ gh&eacute; thăm nh&agrave; cổ B&igrave;nh Thủy &ndash; nh&agrave; cổ đẹp nhất xứ T&acirc;y Đ&ocirc;<br />\r\n&nbsp;</h3>\r\n\r\n<p>Trong số 70 ng&ocirc;i nh&agrave; cổ ở Cần thơ th&igrave; nh&agrave; cổ B&igrave;nh Thủy l&agrave; đẹp nhất. Ng&ocirc;i nh&agrave; cho đến ngay vẫn giữ nguy&ecirc;n được sự bề thế v&agrave; đẹp đẽ như thời điểm mới x&acirc;y do được tr&ugrave;ng tu kĩ lưỡng. Thoạt nh&igrave;n kiến tr&uacute;c c&oacute; vẻ cổ k&iacute;nh, trang nghi&ecirc;m, nhưng đường n&eacute;t đi&ecirc;u khắc lại to&aacute;t n&ecirc;n vẻ ph&oacute;ng kho&aacute;ng như t&iacute;nh c&aacute;ch con người miền T&acirc;y.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Cổng r&agrave;o l&agrave;m bằng b&ecirc; t&ocirc;ng v&agrave; sắt ki&ecirc;n cố theo kiểu dinh thự Ph&aacute;p. Cổng phụ được x&acirc;y theo kiến tr&uacute;c &Aacute; Đ&ocirc;ng với 4 cột tr&ograve;n vững chắc.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-7-min.jpg\" style=\"height:532px; width:800px\" /></p>\r\n\r\n<p><em>Cổng lớn được x&acirc;y dựng như cổng một biệt thự thời nay<br />\r\n<img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-9-min.jpg\" style=\"height:533px; width:800px\" /></em></p>\r\n\r\n<p><em>Cổng phụ mang n&eacute;t kiến tr&uacute;c phương Đ&ocirc;ng</em></p>\r\n\r\n<p><br />\r\nNh&agrave; cổ nằm tr&ecirc;n một diện t&iacute;ch lớn khoảng 8.000 m&eacute;t vu&ocirc;ng, bao gồm cả vườn tược. Ri&ecirc;ng khu nh&agrave; được bao bọc bởi c&acirc;y cảnh được cắt tỉa chu đ&aacute;o, hoa nở rộ bốn m&ugrave;a. Khiến cho ng&ocirc;i nh&agrave; vừa mang n&eacute;t ho&agrave;i cổ k&iacute;nh đ&aacute;o vừa sống động, thơ mộng v&agrave; tươi mới.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy2-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Xung quanh nh&agrave; trồng kh&aacute; nhiều hoa v&agrave; c&acirc;y cảnh được cắt tỉa chu đ&aacute;o</em><br />\r\n<br />\r\n<br />\r\nTrước khi v&agrave;o được b&ecirc;n trong, du kh&aacute;ch sẽ nh&igrave;n thấy một cầu thang c&aacute;ch điệu h&igrave;nh c&aacute;nh cung tao nh&atilde;, trang tr&iacute; hoa văn tinh tế. Cầu thang n&agrave;y l&agrave; điểm nối từ khoảng s&acirc;n đến t&ograve;a nh&agrave;. Nh&agrave; c&oacute; 5 gian 2 m&aacute;i, chia th&agrave;nh nh&agrave; trước, giữa v&agrave; sau. Nền nh&agrave; được l&aacute;t gạch nhập khẩu từ Ph&aacute;, đ&oacute;ng trần platfond, tr&ecirc;n c&aacute;c cửa lớn đều được chạm khắc rất tinh tế.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-15-min.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><em>Cầu thang xoắn nối liền nh&agrave; với s&acirc;n vườn</em></p>\r\n\r\n<p><br />\r\nB&ecirc;n trong, nh&agrave; cổ B&igrave;nh Thủy nổi bật với 3 bộ b&agrave;n ghế cổ đẹp v&agrave; 1 sập gụ được trang tr&iacute; khảm ngọ trai tinh xảo, đường n&eacute;t hoa văn chạm khắc mềm mại, xinh đẹp. Ở gian giữa b&agrave;y b&agrave;n thờ tổ ti&ecirc;n đ&uacute;ng kiểu của người Nam Bộ. Gian thờ được sơn son thếp v&agrave;ng, giường thờ, sập gụ được chạm khắc tinh xảo Mai, lan, c&uacute;c, tr&uacute;c, Ph&uacute;c &ndash; Lộc &ndash; Thọ, long, l&acirc;n, quy, phụng&hellip;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy4-min.jpg\" style=\"height:531px; width:800px\" /></p>\r\n\r\n<p><em>Bộ b&agrave;n ghế cổ<br />\r\n<br />\r\n<strong><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-6-min.jpg\" style=\"height:534px; width:800px\" /></strong></em></p>\r\n\r\n<p><br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://tour.dulichvietnam.com.vn/du-lich-trong-nuoc/du-lich-mien-tay.html/63\"><strong>Tour lịch miền T&acirc;y gi&aacute; rẻ</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Nh&agrave; cổ B&igrave;nh Thủy</strong>&nbsp;c&ograve;n c&oacute; cả một kho đồ cổ đ&atilde; được g&igrave;n giữ suốt nhiều thế kỉ qua. Trong đ&oacute; c&oacute; hai bộ b&agrave;n ghế xuất xứ từ V&acirc;n Nam, Trung Quốc, mặt b&agrave;n l&agrave;m bằng đ&aacute; cẩm thạch, v&acirc;n xanh; bộ salon kiểu Ph&aacute;p đời Louis XV, cặp đ&egrave;n treo thế kỉ 19&hellip; Những hiện vật n&agrave;y cho ta biết được mức độ gi&agrave;u c&oacute; v&agrave; th&uacute; chơi đồ cao cấp của mấy đời gia đ&igrave;nh họ Dương đất B&igrave;nh Thủy, Cần Thơ.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-16-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Đồ cổ trong nh&agrave; cổ B&igrave;nh Thủy vẫn c&ograve;n đến ng&agrave;y nay</em><br />\r\n&nbsp;</p>\r\n\r\n<p>Khu vườn xung quanh nh&agrave; được trang tr&iacute; h&ograve;n non bộ, c&acirc;y cảnh được cắt tỉa xinh đẹp, tinh tế.&nbsp;Nh&agrave; cổ B&igrave;nh Thủy với kiến tr&uacute;c ho&agrave;i cổ độc đ&aacute;o đ&atilde; trở th&agrave;nh địa điểm quay phim cho c&aacute;c bộ phim như &ldquo;Người đẹp T&acirc;y Đ&ocirc;&rdquo;, &ldquo;Những nẻo đường xa&rdquo;, &ldquo;Nợ đời&rdquo;, &ldquo;Người t&igrave;nh&rdquo;.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Năm 2009, nh&agrave; cổ B&igrave;nh Thủy đ&atilde; được Bộ Văn h&oacute;a - Thể thao &amp; Du lịch c&ocirc;ng nhận l&agrave; Di t&iacute;ch kiến tr&uacute;c nghệ thuật cấp quốc gia.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Nh&agrave; cổ B&igrave;nh Thủy</strong>&nbsp;hiện nay l&agrave; một trong những điểm tham quan thu h&uacute;t du kh&aacute;ch khi du lịch Cần Thơ. Nh&agrave; cổ mở cửa tiếp đ&oacute;n du kh&aacute;ch hằng ng&agrave;y. Đến đ&acirc;y l&agrave; đến với kh&ocirc;ng gian thơ mộng, hơi ho&agrave;i cổ nhưng cũng rất ph&oacute;ng kho&aacute;ng. Giới trẻ cũng thường t&igrave;m đến đ&acirc;y để chụp ảnh lưu niệm bởi khung cảnh nơi đ&acirc;y l&ecirc;n ảnh rất đẹp.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-8-min.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><br />\r\nNếu c&oacute; dịp&nbsp;<strong>du lịch Cần Thơ</strong>&nbsp;hoặc&nbsp;<strong>du lịch miền T&acirc;y</strong>, đừng qu&ecirc;n gh&eacute; thăm nh&agrave; cổ B&igrave;nh Thủy &ndash; ng&ocirc;i nh&agrave; cổ đẹp nhất xứ T&acirc;y Đ&ocirc;. Du kh&aacute;ch c&oacute; thể đăng k&iacute; tour du lịch để được thiết kế lộ tr&igrave;nh tham quan l&iacute; tưởng nhất, tiết kiệm thời gian v&agrave; chi ph&iacute; nhất.&nbsp;</p>\r\n\r\n<p><strong><em>Cẩm Luyến</em></strong></p>\r\n', 6, 0, 1),
(30, 'Khám phá vẻ đẹp Côn Đảo dưới sắc trời mùa thu', 1, '1540407610.con-dao-20-min_1539855807.jpg', '<p><strong>C&ocirc;n Đảo</strong>&nbsp;(B&agrave; Rịa &ndash; Vũng T&agrave;u) l&agrave; một quần đảo bao gồm 16 h&ograve;n đảo lớn nhỏ, diện t&iacute;ch khoảng 76 km vu&ocirc;ng. C&ocirc;n Đảo nằm c&aacute;ch Vũng T&agrave;u 97 hải l&yacute; v&agrave; c&aacute;ch s&ocirc;ng Hậu 45 hải l&yacute;.<br />\r\nTầm th&aacute;ng 10 - 11 l&agrave; thời điểm C&ocirc;n Đảo bước v&agrave;o m&ugrave;a kh&ocirc;, cũng l&agrave; l&uacute;c đẹp nhất khi trời v&agrave;o thu. Đ&acirc;y ch&iacute;nh l&agrave; thời điểm l&iacute; tưởng để đi du lịch C&ocirc;n Đảo. Ngo&agrave;i biển &iacute;t nhiều sẽ c&oacute; s&oacute;ng lớn, nhưng điều n&agrave;y chẳng g&acirc;y ảnh hưởng g&igrave; mấy, nếu bạn đến c&aacute;c h&ograve;n đảo v&agrave; ở trong b&atilde;i biển.&nbsp;<br />\r\nC&ocirc;n Đảo qua thời gian vẫn giữ được n&eacute;t đẹp thi&ecirc;n nhi&ecirc;n hoang sơ, dịu d&agrave;ng. Khi &aacute;nh nắng trời thu chiếu rọi, nước biển chuyển sang ngọc b&iacute;ch đẹp đẽ. B&atilde;i c&aacute;t trải d&agrave;i mịn m&agrave;ng, b&atilde;i tắm sạch sẽ, nước trong veo. &Aacute;nh nắng m&ugrave;a thu cũng kh&ocirc;ng qu&aacute; gắt như nắng hạ, du kh&aacute;ch c&oacute; thể tắm biển tại b&atilde;i biển Đầm Trầu.&nbsp;</p>\r\n\r\n<p>Người ta đến C&ocirc;n Đảo đầu ti&ecirc;n l&agrave; để thăm Nh&agrave; t&ugrave; C&ocirc;n Đảo. Nơi địa ngục trần gian n&agrave;y đ&atilde; từng giam giữ v&agrave; tra tấn d&atilde; man những người t&ugrave; c&aacute;ch mạng, người y&ecirc;u nước. Ngo&agrave;i nh&agrave; t&ugrave; C&ocirc;n Đảo c&ograve;n c&oacute; nghĩa trang H&agrave;ng Dương cũng thu h&uacute;t nhiều du kh&aacute;ch đến tưởng nhớ v&agrave; b&agrave;y tỏ l&ograve;ng biết ơn với c&aacute;c chiến sĩ c&aacute;ch mạng ng&agrave;y xưa.&nbsp;</p>\r\n\r\n<p>V&agrave;o m&ugrave;a n&agrave;y, nếu muốn c&oacute; một chuyến đi tham quan nhẹ nh&agrave;ng cũng đừng ngại chọn C&ocirc;n Đảo. Sắc trời m&ugrave;a thu tạo ra cho nơi đ&acirc;y khung cảnh đẹp tuyệt điệu. Du kh&aacute;ch c&oacute; thể tham quan c&aacute;c địa điểm như Dinh Ch&uacute;a Đảo, Hệ thống chuồng cọp, Miếu b&agrave; Phi Yến, B&atilde;i Nh&aacute;t, Cảng Bến Đầm, H&ograve;n B&agrave;, Ch&ugrave;a N&uacute;i Một, B&atilde;i Suối N&oacute;ng, H&ograve;n Bảy Cạnh, H&ograve;n T&agrave;i, H&ograve;n Cau, H&ograve;n Tre Lớn&hellip;</p>\r\n\r\n<p>Nếu đ&atilde; du lịch đến C&ocirc;n Đảo v&agrave;o m&ugrave;a n&agrave;y, h&atilde;y thử trải nghiệm lặn ngắm san h&ocirc; một lần nh&eacute;. Cũng đừng qu&ecirc;n thưởng thức qua những m&oacute;n ăn hấp dẫn của nơi n&agrave;y. C&aacute; thu một nắng, ch&aacute;o h&agrave;u, cua mặt trăng, mắm nhum, t&ocirc;m h&ugrave;m đỏ&hellip;đều l&agrave; đặc sản C&ocirc;n Đảo độc đ&aacute;o, hấp dẫn</p>\r\n\r\n<p>Kh&aacute;c với c&aacute;c khu du lịch đ&ocirc;ng đ&uacute;c, ồn &agrave;o kh&aacute;c,&nbsp;<strong>C&ocirc;n Đảo</strong>&nbsp;cho đến nay vẫn giữ được vẹn nguy&ecirc;n n&eacute;t đẹp tự nhi&ecirc;n đ&aacute;ng tr&acirc;n qu&yacute;. Người d&acirc;n nơi đ&acirc;y nhiệt t&igrave;nh, th&acirc;n thiện, ch&acirc;n chất, dễ mến. Kh&ocirc;ng kh&iacute; th&igrave; lu&ocirc;n thanh b&igrave;nh y&ecirc;n ả. Nếu như đ&atilde; qu&aacute; mỏi mệt với những bon chen nơi th&agrave;nh phố, h&atilde;y đến C&ocirc;n Đảo m&ugrave;a thu để nghỉ ngơi nh&eacute;</p>\r\n', 4, 0, 1),
(31, 'Khu nghỉ dưỡng thân thiện với thiên nhiên nhất châu Á', 1, '1540412990.22-10-201857-740388268-w500-5136-1540206579_180x108.png', '<p>Đến nay, InterContinental Danang Sun Peninsula Resort trở th&agrave;nh một minh chứng điển h&igrave;nh cho triết l&yacute; kiến tr&uacute;c n&agrave;y của &ocirc;ng.<strong>Mỗi ng&agrave;y một... xanh hơn</strong></p>\r\n\r\n<p>Bậc thầy kiến tr&uacute;c Bill Bensley từng chia sẻ cảm gi&aacute;c &ldquo;giống như được về nh&agrave;&rdquo; mỗi khi trở lại khu nghỉ dưỡng b&ecirc;n b&aacute;n đảo Sơn Tr&agrave;. Điều &ocirc;ng t&acirc;m đắc nhất ở &ldquo;ng&ocirc;i nh&agrave;&rdquo; n&agrave;y l&agrave; n&oacute; mỗi ng&agrave;y một xanh hơn.</p>\r\n\r\n<p>&ldquo;Ng&agrave;y ấy, t&ocirc;i đ&atilde; t&igrave;m c&aacute;ch hạn chế tối đa việc động chạm m&ocirc;i trường trong qu&aacute; tr&igrave;nh thi c&ocirc;ng. Giờ đ&acirc;y, mọi thứ đ&atilde; xanh tươi trở lại v&agrave; thậm ch&iacute; tr&ocirc;ng c&ograve;n tự nhi&ecirc;n hơn trước kia. T&ocirc;i thật sự rất y&ecirc;u nơi n&agrave;y, tất cả những g&igrave; t&ocirc;i l&agrave;m l&agrave; cố gắng x&acirc;y n&ecirc;n một c&ocirc;ng tr&igrave;nh cho ph&eacute;p mọi người sống c&ugrave;ng với thi&ecirc;n nhi&ecirc;n&rdquo;, Bill Bensley chia sẻ.</p>\r\n\r\n<p>Thật vậy, nối từ tầng Biển cả đến tầng Thi&ecirc;n đường của khu nghỉ dưỡng l&agrave; chiếc cầu thang gỗ uốn lượn quanh co như mọc giữa rừng gi&agrave;. Bill Bensley đ&atilde; thống nhất với chủ đầu tư khu nghỉ dưỡng n&agrave;y - Tập đo&agrave;n Sun Group, rằng tr&ecirc;n con đường đi của c&aacute;i cầu thang gỗ đ&oacute;, kh&ocirc;ng c&oacute; chuyện chặt c&acirc;y l&agrave;m đường, chỉ c&oacute; thể l&agrave; uốn đường để nhường c&acirc;y. Nguy&ecirc;n tắc đ&oacute; được t&ocirc;n trọng tối đa, từ l&uacute;c x&acirc;y dựng đến khi chỉnh trang để đ&oacute;n đo&agrave;n thượng kh&aacute;ch APEC năm 2017.</p>\r\n\r\n<p>Cũng từ cầu thang gỗ, du kh&aacute;ch sẽ thấy những c&aacute;nh rừng nguy&ecirc;n sinh, ngọn đồi huyền thoại, b&atilde;i c&aacute;t trắng muốt ven theo đại dương xanh ngọc. Tất cả c&ograve;n đ&oacute;, vẻ đẹp nguy&ecirc;n vẹn của thi&ecirc;n tạo.</p>\r\n\r\n<p>Mỗi căn biệt thự trong khu nghỉ dưỡng được đặt men theo c&aacute;c ngọn đồi, h&agrave;i ho&agrave; với n&uacute;i rừng. Du kh&aacute;ch vừa c&oacute; thể đứng ở ban c&ocirc;ng ngắm nh&igrave;n khung cảnh kỳ vĩ của b&aacute;n đảo Sơn Tr&agrave;, vừa nghe tiếng g&aacute;y của g&agrave; mặt đỏ, xem những ch&uacute; s&oacute;c nhảy nh&oacute;t, v&agrave; theo d&otilde;i lo&agrave;i voọc ch&agrave; v&aacute; ch&acirc;n n&acirc;u qu&yacute; hiếm t&igrave;m kiếm l&aacute; non tr&ecirc;n t&aacute;n c&acirc;y gần đ&oacute;. &ldquo;Nghỉ dưỡng giữa rừng nguy&ecirc;n sinh&rdquo;, đ&oacute; l&agrave; trải nghiệm đang chờ đ&oacute;n du kh&aacute;ch tại InterContinental Danang Sun Peninsula Resort.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/10/22/22-10-201856-276787815-w500-6557-1540206579.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Voọc ch&agrave; v&aacute; ch&acirc;n n&acirc;u nổi tiếng ở b&aacute;n đảo Sơn Tr&agrave;.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Năm 2018, &ldquo;khu nghỉ dưỡng sang trọng bậc nhất thế giới&rdquo; n&agrave;y lại tiếp tục được World Travel Awards khu vực ch&acirc;u &Aacute;, ch&acirc;u Đại Dương trao tặng c&ugrave;ng l&uacute;c 5 giải thưởng, trong đ&oacute; c&oacute; danh hiệu &ldquo;Khu nghỉ dưỡng th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n nhất ch&acirc;u &Aacute;&quot;.</p>\r\n\r\n<p><strong>Tầm nh&igrave;n cho tương lai</strong></p>\r\n\r\n<p>Khu nghỉ c&oacute; ri&ecirc;ng một nh&agrave; động vật học l&agrave; điều hiếm c&oacute; kh&ocirc;ng chỉ ở Việt Nam m&agrave; tr&ecirc;n cả thế giới. Tuy nhi&ecirc;n, đ&oacute; l&agrave; điều đ&oacute; đ&atilde; trở th&agrave;nh hiện thực khi James Morrison l&agrave; nh&agrave; động vật học của ri&ecirc;ng InterContinental Danang Sun Peninsula Resort.</p>\r\n\r\n<p>James Morrison bắt đầu l&agrave;m việc trong lĩnh vực bảo tồn từ năm 2011 tại Vườn quốc gia Kruger, Nam Phi. Đam m&ecirc; bảo tồn thi&ecirc;n nhi&ecirc;n, &ocirc;ng tới Việt Nam để giảng dạy v&agrave; nghi&ecirc;n cứu m&ocirc;i trường tại Đại học Quốc gia H&agrave; Nội. Sau nhiều h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; v&agrave; nghi&ecirc;n cứu c&aacute;c c&ocirc;ng vi&ecirc;n quốc gia ở Myanmar, Bangladesh, Nepal... &ocirc;ng quyết định trở về Việt Nam, trong vai tr&ograve; Trưởng ph&ograve;ng Bảo tồn, C&ocirc;ng vi&ecirc;n quốc gia P&ugrave; M&aacute;t cho tổ chức Bảo vệ Động vật hoang d&atilde; của Việt Nam v&agrave; hiện l&agrave;m việc cho resort ở Sơn Tr&agrave;.</p>\r\n\r\n<p>&ldquo;Chỉ c&oacute; một v&agrave;i nơi ở Việt Nam sở hữu đa dạng sinh học phong ph&uacute; như b&aacute;n đảo Sơn Tr&agrave;&rdquo;, James chia sẻ, &ldquo;Đ&oacute; l&agrave; l&yacute; do việc bảo vệ hệ sinh th&aacute;i l&agrave; rất quan trọng&quot;. Theo đ&oacute;, &ocirc;ng đảm nhận việc nghi&ecirc;n cứu, tư vấn v&agrave; chia sẻ kiến thức về động vật hoang d&atilde; của khu resort v&agrave; c&aacute;c hệ sinh th&aacute;i xung quanh, để giảm thiểu t&aacute;c động đến m&ocirc;i trường tự nhi&ecirc;n bằng c&aacute;ch thực hiện c&aacute;c hoạt động kinh doanh một c&aacute;ch c&oacute; tr&aacute;ch nhiệm. &quot;C&ugrave;ng nhau, ch&uacute;ng ta c&oacute; thể x&acirc;y dựng sự ph&aacute;t triển bền vững kh&ocirc;ng chỉ cho khu dưỡng m&agrave; c&ograve;n cho to&agrave;n bộ v&ugrave;ng sinh th&aacute;i - đ&oacute; l&agrave; tầm nh&igrave;n cho tương lai&rdquo;, James khẳng định.</p>\r\n\r\n<p>Để từng bước thực hiện sứ mệnh của m&igrave;nh tại khu nghỉ dưỡng, James Morrison v&agrave; đồng sự ở resort đ&atilde; c&oacute; một loạt hoạt động, từ n&acirc;ng cấp hệ thống đ&egrave;n LED để giảm đến 75% năng lượng ti&ecirc;u thụ nhằm giảm lượng kh&iacute; thải ra m&ocirc;i trường, đến d&agrave;nh 100% lợi nhuận b&aacute;n đồ lưu niệm tại khu nghỉ được quy&ecirc;n g&oacute;p cho một tổ chức phi ch&iacute;nh phủ nhằm bảo vệ voọc ch&acirc;n n&acirc;u.</p>\r\n\r\n<p>Du kh&aacute;ch đến đ&acirc;y cũng sẽ được James Morrison v&agrave; c&aacute;c nh&acirc;n vi&ecirc;n chia sẻ kiến thức về động vật hoang d&atilde;, như một c&aacute;ch truyền th&ocirc;ng về &yacute; thức bảo vệ m&ocirc;i trường sống. Theo chủ đầu tư, c&aacute;c hoạt động nhằm từng bước đưa resort trở th&agrave;nh một biểu tượng mới của nghỉ dưỡng xanh, xứng đ&aacute;ng với danh hiệu Khu nghỉ dưỡng th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n nhất ch&acirc;u &Aacute; 2018.</p>\r\n', 7, 0, 1),
(32, 'Độc đáo lễ hội Ok Om Bok của người Khmer', 1, '1540414790.le-hoi-ok-om-bok-4-min_1540290643.jpg', '<p>Truyền thuyết&nbsp;<strong>người Khmer&nbsp;</strong>cho rằng thỏ ch&iacute;nh l&agrave; tiền kiếp của Phật Th&iacute;ch Ca. Xưa kia sống ở bờ s&ocirc;ng Hằng. Một h&ocirc;m, thần Sakah giả l&agrave;m ăn xin thử l&ograve;ng thỏ. Kh&ocirc;ng c&oacute; g&igrave; cho thần n&ecirc;n thỏ đốt lửa, tự nhảy v&agrave;o để mời người ăn thịt m&igrave;nh. Lửa bỗng dưng tắt v&agrave; người ăn xin biến mất, thần Sakah hiện ra. Thần muốn khen ngợi l&ograve;ng hy sinh của thỏ n&ecirc;n vẽ h&igrave;nh thỏ l&ecirc;n Mặt trăng. Trong t&iacute;n ngưỡng của người Khmer, lễ c&uacute;ng trăng l&agrave; để tưởng nhớ tiền kiếp của đức Phật Th&iacute;ch Ca. Đồng thời cũng l&agrave; dịp tạ ơn thần mặt trăng đ&atilde; bảo vệ m&ugrave;a m&agrave;ng suốt một năm qua.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-2-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<h2><br />\r\nLễ c&uacute;ng mặt trăng Ok Om Bok</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&agrave;i ng&agrave;y trước ng&agrave;y rằm th&aacute;ng 10 &acirc;m lịch, người Khmer đ&atilde; bắt đầu chuẩn bị mọi thứ để l&agrave;m lễ. Đến ng&agrave;y, người Khmer sẽ b&agrave;y biện b&agrave;n c&uacute;ng trong s&acirc;n nh&agrave;, s&acirc;n ch&ugrave;a để quan s&aacute;t mặt trăng.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Trước khi trăng l&ecirc;n, người ta cắm hai thanh tre l&ecirc;n mặt đất, c&aacute;ch nhau 3 m&eacute;t, g&aacute;c ngang một thanh tre kh&aacute;c như h&igrave;nh c&aacute;i cổng. Đặt một c&aacute;i b&agrave;n dưới cổng đ&oacute;. Tr&ecirc;n b&agrave;n b&agrave;y cốm dẹp, khoai lan, khoai m&ocirc;n, dừa tươi, chuối, b&aacute;nh kẹo, tr&agrave;&hellip; Cứ mỗi lần r&oacute;t tr&agrave; l&agrave; họ lại một lần khấn v&aacute;i đức Phật.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-5-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>M&acirc;m c&uacute;ng đầy đủ mọi thứ của người Khmer</em><br />\r\n<br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://www.dulichvietnam.com.vn/tra-vinh/\" target=\"_blank\"><strong>Cẩm nang du lịch Tr&agrave; Vinh</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p>Khi trăng l&ecirc;n đỉnh đầu, người lớn tuổi được người Khmer tin tưởng sẽ đại diện ra c&uacute;ng tạ mặt trăng. R&oacute;t tr&agrave;, thắp nhang rồi khấn nguyện. Trong qu&aacute; tr&igrave;nh c&uacute;ng, trẻ con sẽ tập trung lại chờ được ăn b&aacute;nh. C&uacute;ng xong, người ta sẽ ph&aacute;t b&aacute;nh cho c&aacute;c em nhỏ.&nbsp; Người lớn sẽ đ&uacute;t b&aacute;nh, kẹo cho c&aacute;c em cho đến khi đầy miệng, sau đ&oacute; vỗ lưng c&aacute;c em v&agrave; hỏi c&aacute;c em về ước muốn của m&igrave;nh. Người khmer tin rằng, ước muốn của c&aacute;c em nhỏ cũng l&agrave; động lực của người lớn trong năm tới.<br />\r\n<br />\r\n<img alt=\"lễ hội Ok Om Bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-1-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Người l&agrave;m lễ đ&uacute;t đồ ăn cho trẻ em v&agrave; hỏi c&aacute;c em về ước muốn của m&igrave;nh</em><br />\r\n&nbsp;</p>\r\n\r\n<h2>Trong dịp lễ n&agrave;y c&oacute; 2 sự kiện l&agrave; thả đ&egrave;n gi&oacute; v&agrave; đua ghe Ngo</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thả đ&egrave;n gi&oacute;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&egrave;n gi&oacute; được l&agrave;m từ tre, giấy quyến v&agrave; d&acirc;y kẽm. Đ&aacute;y đ&egrave;n để trống c&oacute; lớp g&ograve;n b&ecirc;n trong. L&uacute;c thả th&igrave; đốt lớp g&ograve;n, nhiều người c&ugrave;ng n&acirc;ng đ&egrave;n l&ecirc;n cao. Nhiệt độ l&agrave;m giấy căng phồng, tạo ra lực đẩy để đ&egrave;n bay l&ecirc;n. Đ&egrave;n gi&oacute; bay l&ecirc;n trời mang theo nguyện ước của người Khmer.&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-4-min.jpg\" style=\"height:500px; width:800px\" /><br />\r\n&nbsp;</p>\r\n\r\n<h3>Đua ghe Ngo</h3>\r\n\r\n<p><br />\r\nĐua ghe Ngo l&agrave; sự kiện được chờ đợi thứ hai của lễ hội Ok Om Bok. Ghe Ngo l&agrave; t&ecirc;n của một loại thuyền độc mộc. Ghe d&agrave;i 25 - 30 m&eacute;t, rộng khoảng 1 - 1,4 m&eacute;t. Tr&ecirc;n ghe đ&oacute;ng nhiều thanh gỗ g&aacute;c ngang vừa cho hai tay bơi ngồi theo cặp. Mỗi ghe đua c&oacute; thể chở từ 40 - 60 người ch&egrave;o. Người lớn tuổi, c&oacute; chức sắc, được nể trọng sẽ điều khiển nhịp ch&egrave;o v&agrave; ngồi ở mũi ghe. Người cầm l&aacute;i ngồi ở đu&ocirc;i ghe. Mỗi ghe Ngo t&ocirc; vẽ những biểu tượng như cọp, rồng, sư tử&hellip; Trước khi đua, ghe Ngo lu&ocirc;n được tu bổ, hạ thủy tập dượt, l&agrave;m lễ c&uacute;ng b&aacute;i trước rất kĩ c&agrave;ng. C&uacute;ng b&aacute;i trước l&agrave; để cầu c&aacute;c vị thần ph&ugrave; hộ cho qu&aacute; tr&igrave;nh diễn ra cuộc đua được thuận lợi, an to&agrave;n.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Đến trưa ng&agrave;y rằm, người d&acirc;n ra hai b&ecirc;n bờ s&ocirc;ng h&aacute;o hức chờ đợi cuộc đua bắt đầu. Sau hồi c&ograve;i hiệu lệnh xuất ph&aacute;t, từng đội ghe đua nhau ch&egrave;o trong tiếng trống, tiếng loa, tiếng reo h&ograve; của người cỗ vũ.&nbsp;<br />\r\n<br />\r\n<img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Cuộc đua ghe Ngo v&igrave; danh dự của mỗi phum sốc Khmer diễn ra s&ocirc;i nổi</em><br />\r\n<br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://tour.dulichvietnam.com.vn/du-lich-trong-nuoc/du-lich-mien-tay.html/63\" target=\"_blank\"><strong>Tour du lịch miền T&acirc;y</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p>Ghe Ngo l&agrave; một sản phẩm văn h&oacute;a độc đ&aacute;o của người Khmer Nam Bộ. Ghe cũng l&agrave; biểu tượng của sự no ấm, sung t&uacute;c, l&agrave; đại diện cho mỗi phum s&oacute;c, x&atilde;, huyện. Do đ&oacute;, cuộc đua ghe thường diễn ra quyết liệt kh&ocirc;ng chỉ v&igrave; giải thưởng, m&agrave; c&ograve;n v&igrave; danh dự v&agrave; vinh quang của phum s&oacute;c. Đua ghe Ngo cũng l&agrave; m&ocirc;n thể thao hấp dẫn v&agrave; h&agrave;o hứng.&nbsp;<br />\r\n<br />\r\nNếu du kh&aacute;ch&nbsp;<strong>du lịch miền T&acirc;y Nam Bộ</strong>&nbsp;trong dịp n&agrave;y, được h&ograve;a m&igrave;nh v&agrave;o lễ hội Ok Om Bok, sẽ cảm thấy trải nghiệm n&agrave;y v&ocirc; c&ugrave;ng đ&aacute;ng gi&aacute;. Kh&ocirc;ng kh&iacute; rộn r&agrave;ng, vui nhộn của lễ hỗi sẽ khiến bạn qu&ecirc;n hết mọi phiền muộn, h&ograve;a m&igrave;nh v&agrave;o lễ hội mỗi năm chỉ c&oacute; một lần n&agrave;y.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Lễ hội Ok Om Bok&nbsp;</strong>mỗi năm chỉ c&oacute; một lần, mang &yacute; nghĩa v&ocirc; c&ugrave;ng s&acirc;u sắc trong đời sống tinh thần người Khmer. Thể hiện kh&aacute;t vọng v&agrave; t&acirc;m hồn người Khmer, thể hiện sự biết ơn của người Khmer với đấng bề tr&ecirc;n. Việc giữ truyền thống tổ chức lễ hội Ok Om Bok cũng g&oacute;p phần bảo tồn, ph&aacute;t huy n&eacute;t đẹp văn h&oacute;a gi&agrave;u t&iacute;nh nh&acirc;n văn của người Khmer Nam Bộ. Kh&ocirc;ng chỉ vậy, lễ hội c&ograve;n l&agrave; một sản phẩm du lịch thu h&uacute;t du kh&aacute;ch đến với v&ugrave;ng đất n&agrave;y.&nbsp;</p>\r\n', 7, 0, 1),
(33, 'Đà Nẵng sắp mở tuyến phố đi bộ kết hợp chợ đêm', 1, '1540527062.du-lich-da-nang_1540365374.jpg', '<p>Ng&agrave;y 23/10, Sở Du lịch TP Đ&agrave; Nẵng th&ocirc;ng tin về việc tr&igrave;nh UBND th&agrave;nh phố xem x&eacute;t mở tuyến phố đi bộ tr&ecirc;n đường H&ugrave;ng Vương. Tuyến đường sầm uất thường xuy&ecirc;n đ&oacute;n lượng kh&aacute;ch lớn đến tham quan v&agrave; mua sắm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đường H&ugrave;ng Vương song song với nhiều con đường s&ocirc;i động của Đ&agrave; Nẵng như đường L&ecirc; Duẩn, Nguyễn Văn Linh. Đ&acirc;y được xem l&agrave; mạch giao th&ocirc;ng xuy&ecirc;n suốt, kết nối quận Hải Ch&acirc;u - trung t&acirc;m kinh tế, ch&iacute;nh trị, văn h&oacute;a của to&agrave;n th&agrave;nh phố với c&aacute;c khu vực l&acirc;n cận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuyến giao th&ocirc;ng n&agrave;y cũng gắn liền với c&aacute;c hoạt động thương mại. Chợ H&agrave;n v&agrave; chợ Cồn l&agrave; hai khu chợ nhộn nhịp bậc nhất nơi đ&acirc;y. Ngo&agrave;i ra, c&aacute;c c&ocirc;ng tr&igrave;nh hiện đại như si&ecirc;u thị Big C, nh&agrave; h&aacute;t Trưng Vương v&agrave; quảng trường cũng nằm trong khu vực.</p>\r\n\r\n<p><img alt=\"Chợ Cồn, thiên đường ăn vặt nổi tiếng của Đà Nẵng. Ảnh: Kiểu Sơn.\" src=\"https://www.dulichvietnam.com.vn/data/image/0hoi-cho-du-lich/tau%20thong%20nhat/cho-con-da-nang.jpg\" style=\"height:480px; width:800px\" /><br />\r\nChợ Cồn, thi&ecirc;n đường ăn vặt nổi tiếng của Đ&agrave; Nẵng. Ảnh: Kiểu Sơn.<br />\r\n&nbsp;</p>\r\n\r\n<p>Theo Sở Du lịch Đ&agrave; Nẵng, những khu xung quanh như phố ẩm thực đ&ecirc;m tr&ecirc;n đường Phạm Hồng Th&aacute;i, &ldquo;con đường c&agrave; ph&ecirc;&rdquo; Y&ecirc;n B&aacute;i hay nh&agrave; thờ ch&iacute;nh t&ograve;a Đ&agrave; Nẵng tr&ecirc;n đường Trần Ph&uacute; cũng sẽ hỗ trợ cho việc h&igrave;nh th&agrave;nh khu phố đi bộ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu th&aacute;ng 9 năm nay, th&agrave;nh phố Đ&agrave; Nẵng cũng đ&atilde; mở chợ đ&ecirc;m kết hợp phố đi bộ tại đoạn đường L&yacute; Nam Đế - Mai Hắc Đế thuộc phường An Hải T&acirc;y, quận Sơn Tr&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo l&atilde;nh đạo Sở Du lịch Đ&agrave; Nẵng, l&acirc;u nay, hoạt động vui chơi giải tr&iacute; về đ&ecirc;m của Đ&agrave; Nẵng c&ograve;n hạn chế. Th&agrave;nh phố sẽ ưu ti&ecirc;n bố tr&iacute; c&aacute;c h&agrave;ng h&oacute;a, sản phẩm của địa phương, xen kẽ c&aacute;c dịch vụ ăn uống, giải tr&iacute; ở khu phố đi bộ, chợ v&agrave;o buổi tối.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong 10 th&aacute;ng năm 2018, tổng kh&aacute;ch du lịch đến Đ&agrave; Nẵng ước đạt hơn 6,8 triệu lượt, tăng 21,5% so với c&ugrave;ng kỳ 2017. Trong đ&oacute;, lượng kh&aacute;ch quốc tế hơn 2,5 triệu lượt, tăng 31% so với c&ugrave;ng kỳ 2017. Số kh&aacute;ch nội địa hơn 4,3 triệu lượt, tăng 16,7% so với c&ugrave;ng kỳ 2017. Tổng thu du lịch ước đạt 23.661 tỷ đồng, tăng 43% so với c&ugrave;ng kỳ năm 2017.</p>\r\n', 1, 1, 1),
(34, 'Vietravel tổ chức ngày hội tư vấn, khuyến mại du lịch', 3, '1541402935.25.jpg', '<p><strong>Hơn 5.000 suất du lịch trọn g&oacute;i giảm tới 30%, ưu đ&atilde;i cho kh&aacute;ch h&agrave;ng đăng k&yacute; tour trực tiếp&hellip; l&agrave; những hoạt động hấp dẫn trong ng&agrave;y hội tư vấn du lịch Vietravel&rsquo;s Day (7-11/11).</strong></p>\r\n\r\n<p>Cụ thể, du kh&aacute;ch đến với văn ph&ograve;ng Vietravel tại H&agrave; Nội, Hải Ph&ograve;ng, Quảng Ninh, Thanh H&oacute;a v&agrave; Vinh sẽ được tư vấn, giải đ&aacute;p v&agrave; cung cấp th&ocirc;ng tin du lịch miễn ph&iacute;. C&aacute;c kh&aacute;ch h&agrave;ng c&oacute; nhu cầu đăng k&yacute; mua tour trong dịp n&agrave;y sẽ được giới thiệu ch&ugrave;m tour khuyến m&atilde;i giảm tới 30, tour du lịch trong nước trọn g&oacute;i từ 1,99 triệu đồng v&agrave; du lịch nước ngo&agrave;i với tổng chi ph&iacute; từ 6,3 triệu đồng.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/24.jpg\" style=\"height:440px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Rất đ&ocirc;ng kh&aacute;ch h&agrave;ng đến tham gia ng&agrave;y hội tư vấn du lịch của Vietravel.</p>\r\n\r\n<p>B&ecirc;n cạnh mức gi&aacute; tốt, đ&atilde; gồm thuế ph&iacute; trọn g&oacute;i, c&aacute;c tour khuyến m&atilde;i n&agrave;y c&ograve;n c&oacute; chương tr&igrave;nh tham quan đầy đủ, dịch vụ tương đương c&aacute;c tour th&ocirc;ng thường. Ch&ugrave;m tour khuyến mại &aacute;p dụng cho hơn 40 tuyến điểm du lịch trong v&agrave; ngo&agrave;i nước như Sapa, H&agrave; Giang, Cao Bằng, Đ&agrave; Nẵng, Ph&uacute; Quốc, Quy Nhơn, miền T&acirc;y, Th&aacute;i Lan, Hong Kong, Dubai, H&agrave;n Quốc, Nhật Bản, Australia, ch&acirc;u &Acirc;u&hellip; đem lại cho du kh&aacute;ch nhiều lựa chọn trong h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute;, trải nghiệm.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/25.jpg\" style=\"height:440px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; Thổ Nhĩ Kỳ trở th&agrave;nh điểm đến ng&agrave;y c&agrave;ng được ưa chuộng.</p>\r\n\r\n<p>Ngo&agrave;i ra, du kh&aacute;ch đăng k&yacute; chương tr&igrave;nh tour khởi h&agrave;nh v&agrave;o dịp Tết Dương lịch, Tết &Acirc;m lịch v&agrave; đầu năm 2019 cũng sẽ được ưu đ&atilde;i giảm gi&aacute; đến một triệu đồng mỗi người, hoặc nhận voucher ph&ograve;ng kh&aacute;ch sạn 5 sao Pao&rsquo;s Sapa khi mua tour &Acirc;u - Australia, tặng lệ ph&iacute; visa Mỹ trị gi&aacute; 3,8 triệu đồng...</p>\r\n\r\n<p>Ngo&agrave;i ra, du kh&aacute;ch đăng k&yacute; bất kỳ tour đi Th&aacute;i Lan, Singapore, H&agrave;n Quốc, Nhật Bản đều sẽ được nhận th&ecirc;m c&aacute;c phần qu&agrave; hấp dẫn như &aacute;o thun, ổ cắm đa năng, t&uacute;i đựng đồ c&aacute; nh&acirc;n, voucher mua sắm trị gi&aacute;&nbsp;10 USD&nbsp;tại trung t&acirc;m Takashimaya (Singapore)&hellip; từ cục x&uacute;c tiến, tổng cục du lịch của c&aacute;c quốc gia n&agrave;y.</p>\r\n\r\n<p>Đ&acirc;y l&agrave; hoạt động tri &acirc;n thường ni&ecirc;n của Vietravel nh&acirc;n dịp kỉ niệm ng&agrave;y th&agrave;nh lập c&ocirc;ng ty v&agrave; nhận giải thưởng du lịch thế giới World Travel Awards 2018.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/26.jpg\" style=\"height:440px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; Ấn Độ một điểm du lịch t&acirc;m linh nổi tiếng.</p>\r\n\r\n<p>Kh&aacute;ch h&agrave;ng c&oacute; thể được giảm 2 triệu đồng v&agrave; nhận voucher FLC 2 đ&ecirc;m khi đăng k&yacute; theo nh&oacute;m đ&ocirc;ng c&aacute;c tour T&acirc;y Ban Nha, Israel, Ai Cập, Ai Cập - Dubai, Morroco, Cuba, Mỹ - Canada, Tr&ugrave;ng Kh&aacute;nh - Lệ Giang - Shangrila, T&acirc;y An - Trịnh Ch&acirc;u - Khai Phong - Lạc Dương&hellip; v&agrave; được giảm 2 triệu đồng mỗi người khi đăng k&yacute; tour Bhutan, Ấn Độ, Myanmar, Nhật - H&agrave;n (nghỉ tại đền, ch&ugrave;a) theo nh&oacute;m đ&ocirc;ng trước 9/12.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/27.jpg\" style=\"height:370px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Sapporo l&agrave; điểm đến mới ở Nhật Bản.</p>\r\n\r\n<p>Tại sự kiện cuối năm n&agrave;y, Vietravel tiếp tục giới thiệu tới kh&aacute;ch h&agrave;ng 15 tuyến sản phẩm mới trải khắp c&aacute;c ch&acirc;u lục cho m&ugrave;a du lịch năm 2019. Theo đ&oacute;, c&aacute;c tuyến du lịch ch&acirc;u &Aacute; mới được đưa v&agrave;o giới thiệu từ th&aacute;ng 11/2018 nh&acirc;n m&ugrave;a l&aacute; v&agrave;ng v&agrave; dự kiến k&eacute;o d&agrave;i cho đến hết m&ugrave;a hoa anh đ&agrave;o như Fukuoka - Kumamoto - Aso - Beppu (6 ng&agrave;y) gi&aacute; từ 19,9 triệu; li&ecirc;n tuyến Nhật Bản - H&agrave;n Quốc (9 ng&agrave;y) gi&aacute; từ 43,9 triệu (chỉ cần l&agrave;m một visa); ngắm tuyết rơi, chơi trượt tuyết, c&acirc;u c&aacute; hồ băng tại Hokkaido trong tour Tokyo - Sapporo - Otaru - Furano (6 ng&agrave;y) gi&aacute; từ 54,9 triệu.</p>\r\n\r\n<p>Hai h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; Trung Quốc mới l&agrave; Tr&ugrave;ng Kh&aacute;nh - Lệ Giang - Shangrila v&agrave; T&acirc;y An - Trịnh Ch&acirc;u - Khai Phong - Lạc Dương đều c&oacute; lịch tr&igrave;nh 6 ng&agrave;y, gi&aacute; khởi điểm 20,9 - 21,9 triệu, hứa hẹn mang đến những ấn tượng đẹp về cảnh quan kh&ocirc;ng k&eacute;m g&igrave; Phượng Ho&agrave;ng cổ trấn.</p>\r\n\r\n<p>Sang đến đầu năm 2019, hai tuyến bay thẳng bằng m&aacute;y bay nguy&ecirc;n chuyến tới Bodh Gaya (Ấn Độ) v&agrave; Bhutan sẽ được triển khai ngay sau dịp Tết dương lịch v&agrave; &Acirc;m lịch. Sản phẩm du lịch ch&acirc;u &Acirc;u kết nối với T&acirc;y Ban Nha, Bồ Đ&agrave;o Nha cũng ch&iacute;nh thức được đưa v&agrave;o lịch khởi h&agrave;nh thường xuy&ecirc;n với những li&ecirc;n tuyến mang đến cảnh quan thi&ecirc;n nhi&ecirc;n ch&acirc;u &Acirc;u đẹp, đặc trưng v&agrave; y&ecirc;n b&igrave;nh như miền Nam nước Ph&aacute;p - Monaco - T&acirc;y Ban Nha, T&acirc;y Ban Nha - Bồ Đ&agrave;o Nha - Morroco, Italy - Cộng h&ograve;a San Marino - Venice - Verona - Hồ Garda - Milan.&nbsp; C&aacute;c h&agrave;nh tr&igrave;nh xa như Israel, Mỹ - Canada, Brazil - Peru - Argentina, Cuba, Morroco&hellip; mang đến trải nghiệm mới lạ, độc đ&aacute;o hướng tới ph&acirc;n kh&uacute;c du kh&aacute;ch trung v&agrave; cao cấp.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/28.jpg\" style=\"height:441px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp; Th&agrave;nh phố Cordoba (T&acirc;y Ban Nha) thu h&uacute;t du kh&aacute;ch nhờ vẻ đẹp b&igrave;nh y&ecirc;n, cổ k&iacute;nh.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 2, 0, 1),
(35, 'Olympia Travel hỗ trợ xin visa Nhật Bản dễ dàng', 3, '1541406602.image003_3.jpg', '<p>Kh&ocirc;ng &iacute;t người loay hoay xin visa du lịch Nhật Bản hoặc cố gắng tho&aacute;t v&ograve;ng luẩn quẩn chuẩn bị hồ sơ - nộp hồ sơ - hồ sơ rớt. C&aacute;ch xin visa Nhật Bản l&agrave; mấu chốt cho c&aacute;c vấn đề tr&ecirc;n.</p>\r\n\r\n<p>Nhật Bản từ l&acirc;u l&agrave; một trong những quốc gia thu h&uacute;t được nhiều du kh&aacute;ch Việt Nam đến tham quan nghỉ dưỡng. Xứ hoa anh đ&agrave;o c&oacute; kh&iacute; hậu &ocirc;n h&ograve;a, dễ chịu lại được thi&ecirc;n nhi&ecirc;n ưu đ&atilde;i nhiều phong cảnh đẹp. Ngo&agrave;i ra, Nhật Bản c&ograve;n sở hữu một nền văn h&oacute;a đặc sắc c&ugrave;ng phong c&aacute;ch ẩm thực cầu kỳ, độc đ&aacute;o&hellip; Đ&oacute; l&agrave; những l&yacute; do nhiều du kh&aacute;ch chưa bao giờ từ bỏ giấc mơ đến đ&acirc;y một lần.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/image001_4.jpg\" style=\"height:449px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nhật Bản thu h&uacute;t nhiều du kh&aacute;ch thế giới.</p>\r\n\r\n<p>Kinh tế ph&aacute;t triển, hội nhập quốc tế s&acirc;u rộng, c&ugrave;ng sự thay đổi tiến bộ của luật ph&aacute;p tạo điều kiện thuận lợi cho người nước ngo&agrave;i xin visa Việt Nam. Ngược lại, người Việt Nam cũng xin visa đi nước ngo&agrave;i với mục đ&iacute;ch du lịch, l&agrave;m việc, c&ocirc;ng t&aacute;c, du học, thăm th&acirc;n&hellip; dễ d&agrave;ng v&agrave; nhanh ch&oacute;ng hơn. Theo đ&oacute;, chỉ cần du kh&aacute;ch c&oacute; l&yacute; lịch trong sạch th&igrave; việc xin visa Nhật Bản kh&ocirc;ng c&ograve;n l&agrave; điều kh&oacute; khăn.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/image003_3.jpg\" style=\"height:412px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Nhiều người loay hoay kh&ocirc;ng biết n&ecirc;n xin Nhật Bản thế n&agrave;o.</p>\r\n\r\n<p>Nắm bắt xu thế đ&oacute;, C&ocirc;ng ty CP TM DV Olympia Travel ra đời v&agrave; trở th&agrave;nh địa chỉ tin cậy, chuy&ecirc;n cung cấp c&aacute;c tour du lịch nước ngo&agrave;i v&agrave; dịch vụ l&agrave;m visa uy t&iacute;n đi c&aacute;c nước với thủ tục đơn giản, nhanh ch&oacute;ng, chuy&ecirc;n nghiệp v&agrave; tiết kiệm.</p>\r\n\r\n<p>Với đội ngũ tư vấn vi&ecirc;n chuy&ecirc;n nghiệp v&agrave; gi&agrave;u kinh nghiệm, h&agrave;ng năm, c&ocirc;ng ty Olympia Travel giải quyết h&agrave;ng ngh&igrave;n bộ hồ sơ du học, du lịch v&agrave; định cư tại c&aacute;c nước như Australia, Đ&agrave;i Loan (Trung Quốc), Nhật Bản, H&agrave;n Quốc...</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/image005_3.jpg\" style=\"height:495px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Olympia Travel l&agrave; địa chỉ uy t&iacute;n, chuy&ecirc;n cung cấp c&aacute;c tour du lịch nước ngo&agrave;i v&agrave; dịch vụ l&agrave;m visa.</p>\r\n\r\n<p>T&ugrave;y mục đ&iacute;ch sang Nhật Bản của kh&aacute;ch h&agrave;ng, Olympia Travel sẽ tư vấn cụ thể những thủ tục cần thiết khi l&agrave;m visa. Đơn cử nếu kh&aacute;ch h&agrave;ng muốn l&agrave;m visa du lịch, việc đầu ti&ecirc;n l&agrave; chuẩn bị những hồ sơ cần thiết (dựa tr&ecirc;n những y&ecirc;u cầu của Đại sứ qu&aacute;n Nhật Bản). Trong qu&aacute; tr&igrave;nh l&agrave;m visa, c&ocirc;ng ty chịu to&agrave;n bộ tr&aacute;ch nhiệm cũng như giải đ&aacute;p thắc mắc của kh&aacute;ch h&agrave;ng về c&aacute;c vấn đề li&ecirc;n quan đến visa như hồ sơ cần những loại giấy tờ g&igrave;, thời gian l&agrave;m bao l&acirc;u, gi&aacute; trị sử dụng được bao nhi&ecirc;u ng&agrave;y, c&aacute;ch xin gia hạn...</p>\r\n\r\n<p>B&ecirc;n cạnh đ&oacute;, tư vấn vi&ecirc;n của Olympia Travel cũng theo d&otilde;i s&aacute;t sao từng c&ocirc;ng đoạn trong qu&aacute; tr&igrave;nh giao dịch, bất kỳ sai s&oacute;t trong kh&acirc;u chuẩn bị đều được nh&acirc;n vi&ecirc;n li&ecirc;n hệ với kh&aacute;ch h&agrave;ng để điều chỉnh. Điều n&agrave;y kh&ocirc;ng chỉ gi&uacute;p kh&aacute;ch h&agrave;ng r&uacute;t ngắn được thời gian chờ đợi m&agrave; c&ograve;n tr&aacute;nh t&igrave;nh trạng lộn xộn, tập trung đ&ocirc;ng khi xin visa tại đại sứ qu&aacute;n.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/image007.jpg\" style=\"height:495px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Olympia Travel mang đến cho kh&aacute;ch h&agrave;ng thủ tục xin visa đơn giản, nhanh ch&oacute;ng v&agrave; tiết kiệm chi ph&iacute;.</p>\r\n\r\n<p>Với hơn 7 năm kinh nghiệm trong lĩnh vực xin visa, Olympia Travel đ&atilde; cấp giấy th&ocirc;ng h&agrave;nh cho h&agrave;ng ngh&igrave;n lữ kh&aacute;ch được đến những v&ugrave;ng đất mơ ước. Trong tương lai, c&ocirc;ng ty tiếp tục phấn đấu đảm bảo về chất lượng l&agrave;m việc chuy&ecirc;n nghiệp, cam kết hồ sơ xin visa hợp lệ, ph&ugrave; hợp với mục đ&iacute;ch cho chuyến đi của kh&aacute;ch h&agrave;ng.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 0, 1);
INSERT INTO `tin_tuc` (`id_tin`, `tieu_de`, `danh_muc_id`, `hinh_anh`, `noi_dung`, `luot_xem`, `id_nguoi_dang`, `hien_thi`) VALUES
(36, 'Bí ẩn vùng đất có ngọn lửa cháy sáng hàng nghìn năm ở châu Á', 3, '1541421429.1200pxYanar_Dagh.JPG', '<p>Đất nước nhỏ b&eacute; Azerbaijan nằm giữa Đ&ocirc;ng &Acirc;u v&agrave; Trung &Aacute; được mệnh danh l&agrave; &quot;v&ugrave;ng đất lửa&quot; do sở hữu ngọn lửa kỳ lạ li&ecirc;n tục ch&aacute;y s&aacute;ng, nằm tr&ecirc;n ngọn n&uacute;i Yanar Dag.</p>\r\n\r\n<p>Theo nguồn tin từ&nbsp;<em>BBC</em>, nữ hướng dẫn vi&ecirc;n tại Azerbaijan cho biết, ngọn lửa bất tử n&agrave;y đ&atilde; ch&aacute;y s&aacute;ng c&aacute;ch đ&acirc;y h&agrave;ng ngh&igrave;n năm. N&oacute; chưa từng bị dập tắt d&ugrave; trời mưa, gi&ocirc;ng gi&oacute; hay c&oacute; tuyết.</p>\r\n\r\n<p>Ngọn lửa n&agrave;y nằm tr&ecirc;n một sườn n&uacute;i thuộc b&aacute;n đảo Absheron (Azerbaijan), do hiện tượng thi&ecirc;n nhi&ecirc;n kỳ lạ m&agrave; &quot;Yanar Dag&quot; được người d&acirc;n địa phương chọn l&agrave;m t&ecirc;n cho ngọn n&uacute;i n&agrave;y. &quot;Yanar Dag&quot; c&oacute; nghĩa l&agrave; &quot;ngọn n&uacute;i đang ch&aacute;y&quot;.&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/1200pxYanar_Dagh.JPG\" style=\"height:440px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ngọn lửa cổ đại ch&aacute;y li&ecirc;n tục suốt 4.000 năm tr&ecirc;n sườn n&uacute;i Yanar Dag</p>\r\n\r\n<p>Nguy&ecirc;n nh&acirc;n sinh ra lửa l&agrave; do sự r&ograve; rỉ kh&iacute; gas tr&ecirc;n ngọn n&uacute;i. V&ugrave;ng đất n&agrave;y từng in dấu ch&acirc;n của nhiều nh&agrave; th&aacute;m hiểm, ngọn lửa kỳ lạ tr&ecirc;n n&uacute;i Yanar Dag cũng xuất hiện trong nhiều tư liệu cổ xưa c&oacute; ni&ecirc;n đại từ thế kỷ 13.</p>\r\n\r\n<h3>Tục thờ thần lửa ở Azerbaijan</h3>\r\n\r\n<p>Lửa tr&ecirc;n n&uacute;i trước kia xuất hiện rất nhiều ở Azerbaijan v&igrave; nước n&agrave;y c&oacute; nguồn t&agrave;i nguy&ecirc;n kh&iacute; gas, dầu mỏ phong ph&uacute;. Tuy nhi&ecirc;n, việc khai th&aacute;c c&aacute;c kh&iacute; tự nhi&ecirc;n n&agrave;y ng&agrave;y một ph&aacute;t triển, những ngọn lử dần bị dập tắt. Ngọn lửa tr&ecirc;n n&uacute;i Yanar Dag c&oacute; lẽ l&agrave; &quot;n&uacute;i lửa&quot; duy nhất v&agrave; ch&aacute;y l&acirc;u nhất c&ograve;n s&oacute;t lại ở Azerbaijan.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/BakuFireTempleUNESCO.jpg\" style=\"height:495px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Thờ thần lửa l&agrave; nghi thức quan trọng trong t&ocirc;n gi&aacute;o&nbsp;Zoroastrian cổ đại.</p>\r\n\r\n<p>V&agrave;o những năm đầu ti&ecirc;n trước C&ocirc;ng nguy&ecirc;n, lửa đ&oacute;ng vai tr&ograve; quan trọng trong t&ocirc;n gi&aacute;o Zoroastrian cổ đại ở&nbsp;Azerbaijan. T&ocirc;n gi&aacute;o Zoroastrian c&oacute; tục thờ thần lửa, bắt nguồn từ Iran sau đ&oacute; lan sang Azerbaijan v&agrave; ph&aacute;t triển mạnh mẽ.&nbsp;</p>\r\n\r\n<p>Đối với người Zoroastrians, lửa l&agrave; cầu nối giữa con người v&agrave; thế giới si&ecirc;u nhi&ecirc;n v&agrave; l&agrave; phương tiện gi&uacute;p con người khai s&aacute;ng được t&acirc;m linh, tr&iacute; tuệ. Lửa gi&uacute;p thanh lọc, duy tr&igrave; sự sống, đ&oacute;ng vai tr&ograve; thiết yếu trong việc thờ c&uacute;ng, tế lễ.&nbsp;</p>\r\n\r\n<p>Những ngọn lửa xuất ph&aacute;t từ tự nhi&ecirc;n rất được người Azerbaijan xưa coi trọng. Ng&agrave;y nay, kh&aacute;ch du lịch tới Azerbaijan thường quan t&acirc;m đến hiện tượng kỳ lạ chứ kh&ocirc;ng để &yacute; đến nguồn gốc t&ocirc;n gi&aacute;o, t&acirc;m linh của ngọn lửa bất tử.</p>\r\n\r\n<p>Bất chấp những t&agrave;i liệu nghi&ecirc;n cứu về nguồn gốc của ngọn lửa cổ xưa bắt đầu ch&aacute;y từ h&agrave;ng ngh&igrave;n năm trước, nhiều &yacute; kiến cho rằng lửa từ n&uacute;i Yanar Dag xuất ph&aacute;t từ một đ&aacute;m ch&aacute;y gần thủ đ&ocirc; Baku (Azerbaijan) v&agrave;o năm 1950.</p>\r\n\r\n<h3><strong>Đền thờ thần lửa&nbsp;Ateshgah&nbsp;</strong></h3>\r\n\r\n<p>Để c&oacute; c&aacute;i nh&igrave;n s&acirc;u sắc hơn về lịch sử thờ lửa của người d&acirc;n Azerbaijan, du kh&aacute;ch n&ecirc;n đến đền Lửa Ateshgah, nằm về ph&iacute;a đ&ocirc;ng thủ đ&ocirc; Baku.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/1200pxAteshgah_Fire_Temple.jpg\" style=\"height:439px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Đền thờ Ateshgah thờ thần lửa được x&acirc;y dựng từ thế kỷ 17.</p>\r\n\r\n<p>Hướng dẫn vi&ecirc;n địa phương chia sẻ về ng&ocirc;i đền: &quot;Từ thời xa xưa, người d&acirc;n đất nước ch&uacute;ng t&ocirc;i nghĩ rằng đ&acirc;y l&agrave; nơi thần lửa sinh sống&quot;. Đền Ateshgah được x&acirc;y dựng v&agrave;o thế kỷ 17, 18 bởi những người di cư từ Ấn Độ đến Baku. T&ecirc;n đền Ateshgah xuất ph&aacute;t từ tiếng Ba Tư, mang &yacute; nghĩa &quot;ng&ocirc;i nh&agrave; lửa&quot;.</p>\r\n\r\n<p>Tại khu trung t&acirc;m của ng&ocirc;i đền, c&oacute; một ngọn lửa tự nhi&ecirc;n. Tuy nhi&ecirc;n, ngọn lửa n&agrave;y đ&atilde; tắt v&agrave;o năm 1969. Từ đ&oacute;, ngọn lửa chỉ được đốt v&agrave;o c&aacute;c dịp tế lễ hoặc thắp s&aacute;ng phục vụ kh&aacute;ch du lịch.</p>\r\n\r\n<h3>Điểm dừng ch&acirc;n của đo&agrave;n h&agrave;nh hương thời con đường tơ lụa</h3>\r\n\r\n<p>Đền thờ Ateshgah được x&acirc;y dựng như một khu phức hợp. Ngo&agrave;i khu vực thờ phụng, tế lễ, đền c&ograve;n c&oacute; khu nghỉ d&agrave;nh cho những đo&agrave;n h&agrave;nh hương qua sa mạc, thương l&aacute;i v&agrave; c&aacute;c nh&agrave; sư khổ hạnh từ Ấn Độ.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/http_2F2Fcdncnncom2Fcnnnext2Fdam2Fassets2F181030114806firetemple6.jpg\" style=\"height:371px; width:660px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Ng&ocirc;i đền cũng từng l&agrave; nơi c&aacute;c nh&agrave; tu khổ hạnh đạo Hindu từ gi&atilde; c&otilde;i đời bằng c&aacute;ch nằm l&ecirc;n v&ocirc;i sống.</p>\r\n\r\n<p>Azerbaijan từng l&agrave; điểm những thương l&aacute;i, người h&agrave;nh hương nghỉ ch&acirc;n khi đi qua con đường tơ lụa. V&agrave;o cuối thế kỷ 19, ng&ocirc;i đền kh&ocirc;ng c&ograve;n l&agrave; nơi thờ c&uacute;ng thần linh, đ&oacute; cũng l&agrave; thời điểm c&aacute;c mỏ dầu xung quanh khu vực n&agrave;y ph&aacute;t triển mạnh mẽ.&nbsp;</p>\r\n\r\n<p>Khu phức hợp đền Ateshgah đ&atilde; trở th&agrave;nh bảo t&agrave;ng v&agrave;o năm 1975. Năm 1998, địa điểm n&agrave;y v&agrave;o danh s&aacute;ch đề cử Di sản thế giới của UNESCO. Ng&agrave;y nay, Azerbaijan v&agrave; c&aacute;c điểm du lịch ngắm lửa nổi tiếng đ&oacute;n khoảng 15.000 lượt du kh&aacute;ch mỗi năm.</p>\r\n', 1, 0, 1),
(37, 'Định vị ngay món gà rán đủ màu đang gây sốt tại đài loan', 3, '1541422494.4.png', '<p>Đ&agrave;i Loan nổi tiếng l&agrave; thế giới ẩm thực với những m&oacute;n ăn đặc sắc v&agrave; đa dạng, đặc biệt l&agrave; c&aacute;c m&oacute;n ăn vặt. Những ai từng đi du lịch Đ&agrave;i Nam Đ&agrave;i Loan&nbsp;gần đ&acirc;y sẽ kh&ocirc;ng thể qu&ecirc;n được m&oacute;n ăn mới lạ với c&aacute;ch chế biến độc đ&aacute;o đ&oacute; l&agrave;: g&agrave; r&aacute;n đủ m&agrave;u. Đ&acirc;y cũng ch&iacute;nh l&agrave; một yếu tố thu h&uacute;t kh&aacute;ch du lịch đến đất nước n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/1.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;<em>Đ&agrave;i Loan l&agrave; điểm du lịch nổi tiếng thế giới với c&aacute;c m&oacute;n ăn vặt.</em></p>\r\n\r\n<p><strong>1/ M&oacute;n ăn g&agrave; đủ m&agrave;u c&oacute; g&igrave; đặc biệt?</strong></p>\r\n\r\n<p>C&aacute;i t&ecirc;n của m&oacute;n ăn cũng đ&atilde; n&oacute;i l&ecirc;n sự kh&aacute;c biệt của m&oacute;n g&agrave; r&aacute;n n&agrave;y, đ&oacute; l&agrave; kh&aacute;ch h&agrave;ng c&oacute; thể chọn m&agrave;u với hơn 8 &quot;sắc th&aacute;i&quot; kh&aacute;c nhau từ đỏ, cam, v&agrave;ng, xanh, đen, t&iacute;m, hồng... Chủ cửa h&agrave;ng sẽ đ&aacute;p ứng nhu cầu của kh&aacute;ch theo sở th&iacute;ch v&agrave; bạn ho&agrave;n to&agrave;n y&ecirc;n t&acirc;m về nguồn nguy&ecirc;n liệu cũng như m&agrave;u được sử dụng.</p>\r\n\r\n<p>Tất cả c&aacute;c m&agrave;u sắc tạo n&ecirc;n m&oacute;n ăn đều kh&ocirc;ng phải l&agrave; m&agrave;u nh&acirc;n tạo m&agrave; được chế biến từ những nguy&ecirc;n liệu rau củ tự nhi&ecirc;n, đảm bảo an to&agrave;n sức khỏe cho người d&ugrave;ng, chẳng hạn như: m&agrave;u v&agrave;ng của b&iacute; ng&ocirc;, đỏ của hoa mẫu đơn, m&agrave;u xanh của l&aacute; tr&agrave; xanh v&agrave; sắc t&iacute;m dịu d&agrave;ng từ khoai lang...</p>\r\n\r\n<p>Ch&iacute;nh sự nhấn nh&aacute; th&ecirc;m c&aacute;c nguy&ecirc;n liệu tạo m&agrave;u n&agrave;y m&agrave; m&oacute;n g&agrave; r&aacute;n đủ m&agrave;u kh&aacute;c biệt hẳn với m&oacute;n g&agrave; r&aacute;n truyền thống. N&oacute; kh&ocirc;ng chỉ c&oacute; độ gi&ograve;n tan, n&oacute;ng sốt m&agrave; c&ograve;n c&oacute; m&ugrave;i thơm thoang thoảng đầy tinh tế. Miếng g&agrave; d&agrave;y, mọng nước chứ kh&ocirc;ng bị kh&ocirc;, khi cắn v&agrave;o sẽ cảm nhận được vị b&eacute;o lan tỏa trong cổ họng, c&aacute;i ngậy sẽ được c&acirc;n bằng lại với ch&uacute;t ngọt ng&agrave;o, thanh đạm từ rau củ n&ecirc;n thực kh&aacute;ch sẽ kh&ocirc;ng thấy ng&aacute;n mỗi khi thưởng thức m&oacute;n n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/2.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;<em>M&oacute;n g&agrave; r&aacute;n đủ m&agrave;u xanh, đỏ, t&iacute;m, v&agrave;ng&hellip; mới lạ v&agrave; an to&agrave;n cho sức khỏe.</em></p>\r\n\r\n<p><strong>2/ M&oacute;n g&agrave; r&aacute;n đủ m&agrave;u được chế biến ra sao?</strong></p>\r\n\r\n<p>Theo chia sẻ của người chủ b&aacute;n m&oacute;n ăn n&agrave;y tại Đ&agrave;i Loan, để tạo ra m&oacute;n g&agrave; r&aacute;n đủ m&agrave;u tuyệt ngon th&igrave; cửa h&agrave;ng phải sử dụng phần thịt nạc v&agrave; th&aacute;i từng miếng c&oacute; k&iacute;ch thước khoảng 300 gram.</p>\r\n\r\n<p>B&iacute; quyết đặc biệt l&agrave; họ sẽ ng&acirc;m g&agrave; c&ugrave;ng với c&aacute;c loại thảo mộc Đ&ocirc;ng y trong 12 giờ để thịt mềm v&agrave; c&oacute; m&ugrave;i thơm rất ri&ecirc;ng. Phần bột nh&uacute;ng cũng chẳng &quot;đi theo lối m&ograve;n&quot;, bằng sự t&ocirc; điểm của nhiều gam m&agrave;u tạo n&ecirc;n một diện mạo ho&agrave;n to&agrave;n mới cho vẻ ngo&agrave;i của m&oacute;n ăn.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/3.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp;<em>Chế biến m&oacute;n g&agrave; r&aacute;n đủ m&agrave;u cần phải c&oacute; b&iacute; quyết.</em></p>\r\n\r\n<p><strong>3/ Kh&aacute;ch du lịch &ldquo;săn l&ugrave;ng&rdquo; địa điểm b&aacute;n g&agrave; r&aacute;n đủ m&agrave;u</strong></p>\r\n\r\n<p>Miếng g&agrave; c&oacute; k&iacute;ch thước to hơn cả b&agrave;n tay v&agrave; được kết hợp với những gam m&agrave;u bắt mắt, kh&aacute;c lạ l&agrave;m người ta t&ograve; m&ograve; v&agrave; hứng th&uacute;. Nhiều bạn trẻ th&iacute;ch th&uacute; v&agrave; cho rằng m&oacute;n ăn n&agrave;y tr&ocirc;ng giống như c&aacute;c m&oacute;n đồ chơi nhưng lại v&ocirc; c&ugrave;ng hấp dẫn, vừa ăn no vừa được m&atilde;n nh&atilde;n.</p>\r\n\r\n<p>Ở c&aacute;c phố to ng&otilde; nhỏ v&agrave; ở c&aacute;c điểm chợ đ&ecirc;m tại Đ&agrave;i Loan, bạn sẽ dễ d&agrave;ng t&igrave;m gặp c&aacute;c m&oacute;n ăn vặt phổ biến n&agrave;y. Một địa điểm quen thuộc v&agrave; nổi tiếng m&agrave; giới trẻ săn l&ugrave;ng khi check in tại Đ&agrave;i Loan đ&oacute; l&agrave; cửa h&agrave;ng c&oacute; t&ecirc;n Chicken Color ở chợ đ&ecirc;m Ph&ugrave;ng Gi&aacute;p. Tại đ&acirc;y, mỗi miếng g&agrave; r&aacute;n c&oacute; k&iacute;ch thước to, đủ m&agrave;u được b&aacute;n với gi&aacute; l&agrave; 75 TWD, khoảng 57.000 đồng VNĐ.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/4.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>M&oacute;n g&agrave; r&aacute;n đủ m&agrave;u đang thu h&uacute;t giới trẻ.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 0, 1),
(38, 'Những điều cần lưu ý khi đi du lịch Hồng Kông', 3, '1541423065.10.jpg', '<p>Tour du lịch Hồng K&ocirc;ng&nbsp;trong m&ugrave;a đ&ocirc;ng năm đ&atilde; ch&aacute;y h&agrave;ng. Tuy nhi&ecirc;n, mỗi đất nước sẽ c&oacute; những nền văn h&oacute;a, lối sống kh&aacute;c nhau m&agrave; bạn bắt buộc phải tu&acirc;n thủ khi đặt ch&acirc;n đến. Đ&oacute; cũng ch&iacute;nh l&agrave; l&yacute; do h&ocirc;m nay&nbsp;<strong>Tugo</strong>&nbsp;sẽ đưa ra những lưu &yacute; cho mọi người khi du h&iacute; tại Hồng K&ocirc;ng - v&ugrave;ng đất đầy quyến rũ v&agrave; xinh đẹp.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/5.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; <em>Những điều cần biết khi du lịch Hồng K&ocirc;ng.</em></p>\r\n\r\n<p><strong>1. Thời gian</strong></p>\r\n\r\n<p>Giờ ở Hồng K&ocirc;ng lệch so với giờ của Việt Nam một tiếng, v&igrave; thế bạn n&ecirc;n chỉnh đồng hồ lại khi đến Hồng K&ocirc;ng để check in kh&aacute;ch sạn hay sử dụng c&aacute;c dịch vụ c&oacute; hẹn giờ được ch&iacute;nh x&aacute;c.</p>\r\n\r\n<p><strong>2. Trang phục</strong></p>\r\n\r\n<p>Bạn n&ecirc;n mang theo trang phục ph&ugrave; hợp với m&ugrave;a v&agrave; thời tiết của Hồng K&ocirc;ng, kh&ocirc;ng n&ecirc;n mang gi&agrave;y qu&aacute; cứng, gi&agrave;y cao g&oacute;t (đối với phụ nữ) để tr&aacute;nh đau ch&acirc;n do phải đi bộ nhiều.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/6.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp; &nbsp; &nbsp;<em>Bạn n&ecirc;n chọn những trang phục thoải m&aacute;i để dễ dạo chơi</em></p>\r\n\r\n<p><strong>3. Kh&aacute;ch sạn</strong></p>\r\n\r\n<p>Tại c&aacute;c kh&aacute;ch sạn đều c&oacute; những trang thiết bị kh&aacute;c nhau n&ecirc;n khi nhận ph&ograve;ng, bạn lưu &yacute; n&ecirc;n kiểm tra tổng thể, nếu thấy hỏng hoặc thiếu phải b&aacute;o ngay cho chủ kh&aacute;ch sạn biết. Việc n&agrave;y gi&uacute;p bạn kh&ocirc;ng phải bồi thường những thiết bị hỏng hoặc thiếu m&agrave; m&igrave;nh kh&ocirc;ng g&acirc;y ra.</p>\r\n\r\n<p>Để đảm bảo cho sức khỏe, bạn cũng n&ecirc;n mang theo c&aacute;c vật dụng vệ sinh c&aacute; nh&acirc;n như kem đ&aacute;nh răng, b&agrave;n chải đ&aacute;nh răng, khăn mặt...v&igrave; ở một số kh&aacute;ch sạn sẽ kh&ocirc;ng c&oacute; những vật dụng n&agrave;y.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/7.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>H&atilde;y kiểm tra vật dụng trong ph&ograve;ng khi nhận bạn nh&eacute;.</em></p>\r\n\r\n<p>Nếu sử dụng thực phẩm trong tủ lạnh của kh&aacute;ch sạn hoặc xem c&aacute;c chương tr&igrave;nh TV phải trả tiền (pay TV), bạn sẽ phải thanh to&aacute;n chi ph&iacute; ph&aacute;t sinh khi l&agrave;m thủ tục trả ph&ograve;ng. B&ecirc;n cạnh đ&oacute;, việc tham khảo về gi&aacute; kh&aacute;ch sạn cũng như gi&aacute; tour Hồng K&ocirc;ng&nbsp;cũng l&agrave; điều m&agrave; bạn cần l&agrave;m để c&oacute; được một chuyến đi tiết kiệm, hợp t&uacute;i tiền.</p>\r\n\r\n<p><strong>4. Ăn uống</strong></p>\r\n\r\n<p>C&aacute;c bữa ăn ch&iacute;nh ở Hồng K&ocirc;ng chủ yếu theo khẩu vị người Hoa, v&igrave; vậy đồ ăn thường nhiều dầu mỡ. Do đ&oacute;, bạn n&ecirc;n mang theo những m&oacute;n ăn tại Việt Nam để tr&aacute;nh việc phải nhịn đ&oacute;i do khẩu vị kh&ocirc;ng ph&ugrave; hợp.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/8.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Ẩm thực Hồng K&ocirc;ng thường c&oacute; nhiều dầu mỡ.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>5. H&agrave;ng ho&aacute; v&agrave; tiền tệ</strong></p>\r\n\r\n<p>Đơn vị tiền tệ của Hồng K&ocirc;ng l&agrave; Đ&ocirc; la Hồng K&ocirc;ng, 1 đ&ocirc; la HK bằng khoảng 2.100 VND (t&ugrave;y thời điểm). V&igrave; thế, để thuận tiện cho việc sử dụng, bạn n&ecirc;n quy đổi tiền Việt sang Đ&ocirc; la Hồng K&ocirc;ng hoặc Đ&ocirc; la Mỹ trước khi qua đ&oacute;.</p>\r\n\r\n<p>Một điều cần lưu &yacute; l&agrave; điện thoại di động, m&aacute;y quay phim, m&aacute;y ảnh loại chuy&ecirc;n d&ugrave;ng cần khai r&otilde; k&yacute; hiệu, số m&aacute;y v&agrave;o tờ khai xuất nhập cảnh khi đến Hồng K&ocirc;ng, v&igrave; vậy bạn h&atilde;y chuẩn bị sẵn để l&agrave;m thủ tục nhanh hơn nh&eacute;.</p>\r\n\r\n<p>Ngo&agrave;i ra, khi đi mua sắm, bạn n&ecirc;n mang theo một chiếc m&aacute;y t&iacute;nh bỏ t&uacute;i để thuận tiện cho việc trả gi&aacute; v&agrave; đối chiếu cho những m&oacute;n đồ cần mua.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/9.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Đ&ocirc; la Hồng K&ocirc;ng v&agrave; Mỹ đều sử dụng được.</em></p>\r\n\r\n<p><strong>6. Những lưu &yacute; kh&aacute;c</strong></p>\r\n\r\n<p>Bạn n&ecirc;n kh&oacute;a h&agrave;nh l&yacute; cẩn thận trước khi rời khỏi kh&aacute;ch sạn, kh&ocirc;ng n&ecirc;n để lại tiền bạc, hay t&agrave;i sản c&oacute; gi&aacute; trị lớn trong h&agrave;nh l&yacute;.</p>\r\n\r\n<p>Bạn n&ecirc;n giữ danh thiếp kh&aacute;ch sạn nơi m&igrave;nh cư tr&uacute; ph&ograve;ng khi lạc đường.</p>\r\n\r\n<p>Khi tham quan theo đo&agrave;n th&igrave; trong qu&aacute; tr&igrave;nh tham quan, nếu bạn c&oacute; dấu hiệu mệt mỏi, phải b&aacute;o ngay với hướng dẫn vi&ecirc;n v&agrave; trưởng đo&agrave;n. Tuyệt đối kh&ocirc;ng được tự &yacute; ngồi nghỉ lại để chờ đo&agrave;n quay ra, v&igrave; phần lớn c&aacute;c điểm tham quan tại Trung Quốc đều v&agrave;o một cửa v&agrave; khi quay ra l&agrave; cửa kh&aacute;c.</p>\r\n\r\n<p>Nếu kh&ocirc;ng may bạn bị lạc đo&agrave;n, n&ecirc;n đứng tại điểm cuối c&ugrave;ng nh&igrave;n thấy đo&agrave;n để hướng dẫn vi&ecirc;n v&agrave; trưởng đo&agrave;n dễ d&agrave;ng t&igrave;m kiếm. Tuyệt đối kh&ocirc;ng đi t&igrave;m đo&agrave;n v&igrave; c&aacute;c điểm tham quan rất rộng n&ecirc;n c&agrave;ng dễ bị lạc.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/10.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Tận hưởng những kh&ocirc;ng gian tuyệt vời tại Hồng K&ocirc;ng.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n', 1, 0, 1),
(39, 'Khám phá kiến trúc nổi bật của nhà hát ESPLANDE', 3, '1541423933.14.png', '<p>Du lịch Singapore th&aacute;ng 12&nbsp;lu&ocirc;n l&agrave; lựa chọn ho&agrave;n hảo cho c&aacute;c du kh&aacute;ch trong nước cũng như quốc tế khi muốn gh&eacute; thăm quốc đảo n&agrave;y.</p>\r\n\r\n<p>H&ocirc;m nay ch&uacute;ng t&ocirc;i&nbsp;sẽ giới thiệu tới bạn một địa điểm du lịch v&ocirc; c&ugrave;ng độc đ&aacute;o v&agrave; cuốn h&uacute;t, đ&oacute; l&agrave; nh&agrave; h&aacute;t Esplanade - nơi nổi bật với kiến tr&uacute;c bắt mắt v&agrave; cũng l&agrave; trung t&acirc;m văn h&oacute;a nghệ thuật hoành tráng tại đảo quốc sư tử Singapore.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/11.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Kh&aacute;m ph&aacute; kiến tr&uacute;c nổi bật của nh&agrave; h&aacute;t Esplande.</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p><strong>1. Giới thiệu về nh&agrave; h&aacute;t Esplande</strong></p>\r\n\r\n<p>Bạn c&oacute; biết trong c&aacute;c tour du lịch Singapore trọn g&oacute;i&nbsp;kh&ocirc;ng thể n&agrave;o thiếu địa điểm v&ocirc; c&ugrave;ng ấn tượng n&agrave;y. Nh&agrave; h&aacute;t Esplanade&nbsp;l&agrave; một tổ hợp trung t&acirc;m biểu diễn nghệ thuật ở Singapore. Tổ hợp n&agrave;y nằm tr&ecirc;n một diện t&iacute;ch rộng 6 ha, tọa lạc tại khu bờ cảng dọc theo Vịnh Marina. Tổng kinh ph&iacute; x&acirc;y dựng c&ocirc;ng tr&igrave;nh n&agrave;y l&agrave; 600 triệu đ&ocirc; la Singapore.&nbsp;</p>\r\n\r\n<p><strong>Nh&agrave; h&aacute;t Esplanade</strong>&nbsp;được ch&iacute;nh thức khai trương v&agrave;o năm 2002. Trong bản thiết kế nh&agrave; h&aacute;t đầu ti&ecirc;n được giới thiệu trước c&ocirc;ng ch&uacute;ng năm 1994, t&ograve;a nh&agrave; c&oacute; những lớp k&iacute;nh trang tr&iacute; xung quanh. Thiết kế n&agrave;y đ&atilde; bị ch&ecirc; bai v&igrave; dễ g&acirc;y hiệu ứng nh&agrave; k&iacute;nh, nhất l&agrave; trong điều kiện kh&iacute; hậu nhiệt đới ở Singapore. V&igrave; vậy, sau đ&oacute; n&oacute; đ&atilde; c&oacute; v&agrave;i thay đổi, giải ph&aacute;p tạo &quot;b&oacute;ng m&aacute;t&quot; đ&atilde; được sử dụng, đ&oacute; l&agrave; d&ugrave;ng vật liệu nh&ocirc;m c&oacute; sơn phủ, c&aacute;ch điệu th&agrave;nh những hệ m&aacute;i nhỏ. Nhờ thế, nh&agrave; h&aacute;t c&oacute; d&aacute;ng vẻ tương tự như quả sầu ri&ecirc;ng, một kiến tr&uacute;c lạ mắt v&agrave; độc đ&aacute;o c&oacute; thể dễ d&agrave;ng nhận biết từ xa.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/12.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>Ấn tượng trong từng đường n&eacute;t.</em></p>\r\n\r\n<p><strong>2. Kiến tr&uacute;c của nh&agrave; h&aacute;t</strong></p>\r\n\r\n<p>Kh&ocirc;ng gian tại nh&agrave; h&aacute;t bao gồm: rạp h&aacute;t, ph&ograve;ng h&ograve;a nhạc, rạp chiếu phim, trung t&acirc;m triển l&atilde;m và m&ocirc;̣t phòng h&ocirc;̣i nghị rộng lớn.&nbsp;<strong>Nh&agrave; h&aacute;t trung t&acirc;m của Esplanade</strong>&nbsp;c&oacute; sức chứa 2.000 chỗ, c&oacute; s&acirc;n khấu lớn nhất Singapore, ph&ograve;ng h&ograve;a nhạc c&oacute; sức chứa 1.600 chỗ. Ngoài ra, b&ecirc;n trong tổ hợp n&agrave;y c&ograve;n c&oacute; trung t&acirc;m mua sắm và thư viện ở tầng 3, khiến cho&nbsp;<strong>nh&agrave; h&aacute;t Esplanade&nbsp;</strong>trở th&agrave;nh một trung t&acirc;m văn h&oacute;a nghệ thuật hoành tráng tại đảo quốc sư tử.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/13.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Nh&agrave; h&aacute;t tại đ&acirc;y c&oacute; sức chưa l&ecirc;n đến 2000 người.</em></p>\r\n\r\n<p>Nh&agrave; h&aacute;t Esplanade c&oacute; hiệu suất biểu diễn đứng v&agrave;o h&agrave;ng đẳng cấp thế giới với những dịch vụ hỗ trợ v&agrave; tiện nghi chuy&ecirc;n nghiệp. Ngo&agrave;i những buổi tr&igrave;nh diễn nghệ thuật th&igrave; nh&agrave; h&aacute;t c&ograve;n l&agrave; nơi tổ chức những cuộc gặp gỡ, những buổi hội họp cũng như c&aacute;c chủ đề li&ecirc;n quan đến phong c&aacute;ch sống v&agrave; nghệ thuật.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/14.png\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Thiết kế g&acirc;y ấn tượng mạnh với kh&aacute;ch du lịch.</em></p>\r\n\r\n<p>Diện mạo của Singapore đ&atilde; c&oacute; nhiều thay đổi trong những thập kỷ qua, nhưng g&acirc;y ấn tượng nhất vẫn l&agrave; sự xuất hiện của&nbsp;<strong>Nh&agrave; h&aacute;t tr&aacute;i sầu ri&ecirc;ng Esplanade</strong>.&nbsp;</p>\r\n', 1, 0, 1),
(40, 'Khám phá phật Nha Tự - điểm đến thú hút tại SINGAPORE', 3, '1541424672.15.jpg', '<p>Singapore l&agrave; một đất nước du lịch y&ecirc;u th&iacute;ch của nhiều người du kh&aacute;ch bởi n&oacute; sở hữu cho m&igrave;nh một số lượng lớn đia điểm tham quan nổi tiếng. Trong số đ&oacute; kh&ocirc;ng thể kh&ocirc;ng kể đến Phật Nha Tự - một đền Phật gi&aacute;o thu h&uacute;t được lượng lớn kh&aacute;ch tham quan mỗi năm. Nếu bạn chuẩn bị c&oacute; một tour du lịch th&aacute;ng 12&nbsp;n&agrave;y th&igrave; h&atilde;y c&ugrave;ng&nbsp;ch&uacute;ng t&ocirc;i&nbsp;kh&aacute;m ph&aacute; nơi n&agrave;y nh&eacute;!&nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/15.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Phật Nha Tự l&agrave; một điểm đến thu h&uacute;t được nhiều kh&aacute;ch du lịch tại Singapore.</em></p>\r\n\r\n<h3><strong>1.&nbsp;Tổng quan về Phật Nha Tự</strong></h3>\r\n\r\n<p>Phật Nha Tự hay c&ograve;n được gọi l&agrave; ch&ugrave;a Răng Phật l&agrave; một c&ocirc;ng tr&igrave;nh ch&ugrave;a v&agrave; bảo t&agrave;ng nghệ thuật Phật gi&aacute;o tọa lạc tại 288 đường South Bridge. Đ&acirc;y l&agrave; điểm độc đ&aacute;o thu h&uacute;t được nhiều du kh&aacute;ch tại khu phố T&agrave;u &ndash; Chinatown.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/16.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>Phật Nha Tự nổi bật ở khu phố T&agrave;u.</em></p>\r\n\r\n<p>Ng&ocirc;i ch&ugrave;a được thiết kế v&agrave; b&agrave;i tr&iacute; theo kiểu Mandala, thể hiện quan niệm &ldquo;vũ trụ vạn vật&rdquo; của nh&agrave; Phật v&agrave; mang nhiều n&eacute;t kiến tr&uacute;c gợi nhớ đến lịch sử nh&agrave; Đường (Trung Quốc). Được thiết kế bởi Ho&agrave; thượng Shi Fa Zhao với sự cố vấn của c&aacute;c chuy&ecirc;n gia trong, ngo&agrave;i nước, Phật Nha Tự đ&atilde; được khởi c&ocirc;ng từ ng&agrave;y 13/3/2005 v&agrave; kh&aacute;nh th&agrave;nh ng&agrave;y 31/05/2007. Với tổng ch&iacute; ph&iacute; l&ecirc;n đến 62 tri&ecirc;u SGD, c&ocirc;ng tr&igrave;nh gồm 5 tầng nổi v&agrave; một tầng hầm, ng&ocirc;i đền dễ d&agrave;ng g&acirc;y ấn tượng mạnh với mọi người bởi h&igrave;nh d&aacute;ng tr&aacute;ng lệ v&agrave; m&agrave;u sắc nổi bật.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/17.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>C&ocirc;ng tr&igrave;nh c&oacute; tổng chi ph&iacute; l&ecirc;n đến 62 tri&ecirc;u SGD</em></p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3><strong>2. Nguồn gốc của t&ecirc;n gọi Phật Nha Tự</strong></h3>\r\n\r\n<p>Trong qu&aacute; tr&igrave;nh kh&ocirc;i phục lại một bảo th&aacute;p bị đổ ở ngọn đồi Bagan, ho&agrave; thượng Cakkapala v&agrave; c&aacute;c m&ocirc;n đồ của m&igrave;nh đ&atilde; t&igrave;m thấy một chiếc răng v&agrave; được cho l&agrave; răng phật. Ph&aacute;t hiện của &ocirc;ng đ&atilde; kh&ocirc;ng được c&ocirc;ng bố rộng r&atilde;i v&agrave; chiếc răng được đem về cất giữ trong tu viện Bandula. Năm 2002, ho&agrave; thượng Cakkapala đ&atilde; đem x&aacute; lợi răng Phật trong tu viện tham gia triển l&atilde;m tại ch&ugrave;a V&agrave;ng ở Singapore. Cũng trong năm đ&oacute;, ho&agrave; thượng Cakkapala đ&atilde; trao tặng x&aacute; lợi răng Phật cho đại sư Shi Fa Zhao với y&ecirc;u cầu phải x&acirc;y dựng một ng&ocirc;i ch&ugrave;a để cất giữ x&aacute; lợi v&agrave; cho du kh&aacute;ch h&agrave;nh hương. Từ đ&oacute; t&ecirc;n gọi Phật Nha Tự đ&atilde; ra đời.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/18.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>T&ecirc;n gọi của ng&ocirc;i ch&ugrave;a xuất ph&aacute;t từ việc được x&acirc;y dựng để cất giữ x&aacute; lợi v&agrave; cho du kh&aacute;ch h&agrave;ng hương.</em></p>\r\n\r\n<h3><strong>3.&nbsp;Kiến tr&uacute;c độc đ&aacute;o của ng&ocirc;i ch&ugrave;a</strong></h3>\r\n\r\n<p>Nh&igrave;n v&agrave;o c&ocirc;ng tr&igrave;nh Phật gi&aacute;o n&agrave;y bạn sẽ cảm thấy rất ấn tượng bởi vẻ nguy nga tr&aacute;ng lệ của nơi n&agrave;y. Để tham quan hết c&aacute;c tầng của Phật Nha Tự, du kh&aacute;ch c&oacute; thể sử dụng thang bộ hoặc thang m&aacute;y. Ngo&agrave;i ra, nơi n&agrave;y c&ograve;n c&oacute; hệ thống ghế điện hỗ trợ di chuyển cho người khuyết tật l&ecirc;n c&aacute;c tầng. Nếu bạn sắp c&oacute; một&nbsp;<a href=\"https://dulichthiennhien.vn/du-lich-singapore-malaysia\">tour Singapore Malaysia 5 ng&agrave;y 4 đ&ecirc;m</a>&nbsp;th&igrave; đừng bỏ qua kiến tr&uacute;c tuyệt vời của nơi n&agrave;y nh&eacute;!</p>\r\n\r\n<ul>\r\n	<li>Tầng 1 l&agrave; Bảo điện thờ đức Dược sư Lưu Ly Quang Như Lai v&agrave; nhị vị Bồ t&aacute;t Nhật Quang Biến Chiếu, Nguyệt Quang Biến Chiếu.&nbsp;Từ tầng một l&ecirc;n tầng hai sẽ qua một tầng lửng, được bố tr&iacute; Bảo t&agrave;ng về Bồ t&aacute;t Địa Tạng v&agrave; nhiều tượng s&aacute;p của c&aacute;c vị cao tăng nổi tiếng Singapore lẫn thế giới.&nbsp;</li>\r\n	<li>Tầng hai l&agrave; Ph&aacute;p Bảo điện hay c&ograve;n gọi l&agrave; T&agrave;ng Kinh C&aacute;c. Nơi đ&acirc;y lưu trữ Tam Tạng Kinh Điển của Phật gi&aacute;o. Khu vực n&agrave;y như một thư viện, d&agrave;nh cho những người say m&ecirc; đọc s&aacute;ch v&agrave; ham th&iacute;ch nghi&ecirc;n cứu cho việc tu học Phật ph&aacute;p cũng như t&igrave;m hiểu Triết l&yacute; Phật gi&aacute;o.</li>\r\n	<li>Tầng ba l&agrave; Bảo t&agrave;ng Văn h&oacute;a Phật gi&aacute;o. Tầng n&agrave;y sưu tầm v&agrave; trưng b&agrave;y hơn 300 bảo vật li&ecirc;n quan đến cuộc đời của Đức Phật Th&iacute;ch Ca M&acirc;u Ni, từ l&uacute;c Đản sinh cho đến nhập Niết b&agrave;n. Nơi đ&acirc;y c&ograve;n trưng b&agrave;y X&aacute; lợi của Đức Phật v&agrave; c&aacute;c vị Th&aacute;nh tăng, Cao tăng từ nhiều quốc gia tr&ecirc;n thế giới.</li>\r\n	<li>Tầng bốn l&agrave; nơi trưng b&agrave;y X&aacute; lợi Răng Phật. Bảo vật được đặt trong ng&ocirc;i Bảo th&aacute;p cao 3,6m đ&uacute;c bằng 420kg v&agrave;ng r&ograve;ng, nơi t&ocirc;n nghi&ecirc;m cao qu&yacute; nhất trong ng&ocirc;i ch&ugrave;a n&agrave;y. Khu tầng n&agrave;y c&oacute; h&agrave;ng trăm ngọn đ&egrave;n lồng v&agrave;ng lung linh treo tr&ecirc;n trần tạo n&ecirc;n &aacute;nh s&aacute;ng huyền diệu linh thi&ecirc;ng.</li>\r\n	<li>Tầng năm của Phật Nha Tự l&agrave; s&acirc;n thượng. Khu vực n&agrave;y được bố tr&iacute; như một Khu vườn y&ecirc;n tĩnh, nhiều c&acirc;y xanh qu&yacute; gi&aacute;, phong lan tươi đẹp. Nổi bật giữa khu vườn l&agrave; Bảo th&aacute;p chứa Kinh lu&acirc;n, bao quanh c&ograve;n b&agrave;i tr&iacute; h&agrave;ng trăm pho tượng Phật. Kinh lu&acirc;n l&agrave; một ph&aacute;p tu, gi&uacute;p tịnh h&oacute;a th&acirc;n t&acirc;m v&agrave; t&iacute;ch lũy v&ocirc; lượng c&ocirc;ng đức.</li>\r\n	<li>Tầng hầm của Phật Nha Tự l&agrave; một Giảng đường, một hội trường đa năng, nơi thường diễn ra những buổi Ph&aacute;p thoại, c&aacute;c hoạt động như triển l&atilde;m, biểu diễn nghệ thuật hay tội thảo, tọa đ&agrave;m về phật ph&aacute;p&hellip;</li>\r\n</ul>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/19.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>B&ecirc;n trong ng&ocirc;i ch&ugrave;a gồm nhiều tượng phật.</em></p>\r\n\r\n<p>D&ugrave; chỉ mới được đưa v&agrave;o hoạt động từ năm 2007 nhưng Phật Nha Tự đ&atilde; trở n&ecirc;n nổi tiếng v&agrave; thu h&uacute;t được nhiều người đến tham quan cũng như t&igrave;m hiểu về Phật gi&aacute;o. Đ&acirc;y thật sự l&agrave; một điểm đến t&acirc;m linh m&agrave; bạn kh&ocirc;ng n&ecirc;n bỏ qua tại Singapore.</p>\r\n', 1, 0, 1),
(41, 'Top 3 địa điểm vui chơi khi du lịch ở SEOUL', 3, '1541425311.20.jpg', '<p>Nếu bạn muốn c&oacute; chuyến du lịch H&agrave;n Quốc Seoul&nbsp;th&uacute; vị th&igrave; ngo&agrave;i việc đến c&aacute;c khu trung t&acirc;m mua sắm, h&atilde;y kh&aacute;m ph&aacute; c&aacute;c khu vui chơi giải tr&iacute; hấp dẫn tại đ&acirc;y.</p>\r\n\r\n<p>Xứ sở Kim chi c&oacute; nhiều c&ocirc;ng vi&ecirc;n chủ đề rất nổi tiếng sẽ l&agrave; nơi tham quan tuyệt vời cho cả người lớn v&agrave; trẻ em.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/20.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>Du lịch H&agrave;n Quốc lu&ocirc;n thu h&uacute;t du kh&aacute;ch bởi nhiều địa điểm vui chơi.</em></p>\r\n\r\n<p><strong>1. C&ocirc;ng vi&ecirc;n Everland lộng lẫy</strong></p>\r\n\r\n<p>Everland l&agrave; c&ocirc;ng vi&ecirc;n chủ đề nổi tiếng ở H&agrave;n Quốc gi&uacute;p du kh&aacute;ch kh&aacute;m ph&aacute; lịch sử, văn h&oacute;a v&agrave; đặc biệt l&agrave; c&aacute;c lễ hội đặc sắc. Đ&acirc;y cũng l&agrave; một trong những c&ocirc;ng vi&ecirc;n giải tr&iacute; được kh&aacute;ch du lịch gh&eacute; thăm nhiều nhất tr&ecirc;n thế giới.</p>\r\n\r\n<p>Đến đ&acirc;y, du kh&aacute;ch sẽ bị &ldquo;cho&aacute;ng ngợp&rdquo; bởi một d&atilde;y c&aacute;c l&acirc;u đ&agrave;i lộng lẫy m&ocirc; phỏng kiến tr&uacute;c c&aacute;c nước Trung Đ&ocirc;ng, T&acirc;y Ban Nha, Ấn Độ v&agrave; Nga. Nơi n&agrave;y quy tụ đầy đủ c&aacute;c địa điểm ăn uống v&agrave; vui chơi như quầy ẩm thực, quầy b&aacute;n đồ lưu niệm, khu tr&ograve; chơi với c&aacute;c nh&acirc;n vật hoạt h&igrave;nh 3D tr&igrave;nh diễn s&ocirc;i động.</p>\r\n\r\n<p>Hơn thế, c&ocirc;ng vi&ecirc;n cũng kh&ocirc;ng thiếu c&aacute;c tr&ograve; chơi cảm gi&aacute;c mạnh rất th&uacute; vị như t&agrave;u lượn, v&ograve;ng quay, ph&aacute;o đ&agrave;i bay&hellip; Ngo&agrave;i ra, c&ograve;n c&oacute; vườn th&uacute; Topia hoang d&atilde; v&agrave; c&ocirc;ng vi&ecirc;n nước Caribbean Bay m&aacute;t mẻ nữa.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/21.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<em>Bạn sẽ cho&aacute;ng ngợp với những t&ograve;a l&acirc;u đ&agrave;i lộng lẫy của Everland.</em></p>\r\n\r\n<p><strong>2. C&ocirc;ng vi&ecirc;n Seoul Land vui nhộn</strong></p>\r\n\r\n<p>Nhắc đến du lịch Seoul&nbsp;th&igrave; Seoul Land l&agrave; một điểm đến kh&ocirc;ng thể bỏ lỡ v&igrave; đ&acirc;y l&agrave; nơi vui chơi l&yacute; tưởng cho trẻ nhỏ. Người d&acirc;n địa phương thường dẫn trẻ đến vui chơi v&agrave;o dịp cuối tuần v&igrave; ngo&agrave;i c&aacute;c tr&ograve; chơi hấp dẫn như t&agrave;u lượn th&igrave; nơi đ&acirc;y c&ograve;n c&oacute; vườn b&aacute;ch thảo v&agrave; vườn b&aacute;ch th&uacute; lớn nhất H&agrave;n Quốc. Hơn thế, nhiều lễ hội hay sự kiện cũng được tổ chức thường xuy&ecirc;n ở c&ocirc;ng vi&ecirc;n Seoul Land.</p>\r\n\r\n<p>C&ocirc;ng vi&ecirc;n Seoul Land chia l&agrave;m 5 khu vực, nếu kh&aacute;ch du lịch v&agrave;o m&ugrave;a đ&ocirc;ng th&igrave; c&oacute; thể trải nghiệm tại đồi trượt tuyết, nếu v&agrave;o m&ugrave;a h&egrave; th&igrave; cũng kh&ocirc;ng lo n&oacute;ng v&igrave; đ&atilde; c&oacute; bể bơi m&aacute;t mẻ.</p>\r\n\r\n<p>Từ c&ocirc;ng vi&ecirc;n n&agrave;y, du kh&aacute;ch c&oacute; thể di chuyển nhanh ch&oacute;ng để tham quan một số địa điểm l&acirc;n cận kh&aacute;c như vườn th&uacute; Seoul Grand Park, bảo t&agrave;ng nghệ thuật đương đại, trường đua ngựa Seoul Race Park.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/22.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>C&ocirc;ng vi&ecirc;n Seoul Land vui nhộn l&agrave; nơi đến l&yacute; tưởng của trẻ em.</em></p>\r\n\r\n<p><strong>3. C&ocirc;ng vi&ecirc;n Lotte World nhộn nhịp</strong></p>\r\n\r\n<p>Lotte World sẽ l&agrave; một địa điểm kh&ocirc;ng thể bỏ qua cho chuyến&nbsp;<a href=\"https://dulichthiennhien.vn/du-lich-han-quoc\">du lịch Seoul H&agrave;n Quốc</a>&nbsp;bởi nơi đ&acirc;y l&agrave; sự kết hợp tuyệt vời của kh&ocirc;ng gian giải tr&iacute; trong nh&agrave; v&agrave; ngo&agrave;i trời. Bạn c&oacute; thể thoải m&aacute;i ăn uống, mua sắm, vui chơi tại c&aacute;c địa điểm trong khu&ocirc;n vi&ecirc;n Lotte World m&agrave; kh&ocirc;ng cần phải ra ngo&agrave;i với Lotte Department, Lotte Hotel World, s&acirc;n trượt băng, bể bơi...</p>\r\n\r\n<p>Kh&ocirc;ng chỉ vậy, bạn c&ograve;n c&oacute; thể gh&eacute; c&ocirc;ng vi&ecirc;n ngo&agrave;i trời Magic Island. Đ&acirc;y c&ograve;n gọi l&agrave; một đảo nh&acirc;n tạo của c&ocirc;ng vi&ecirc;n. Với đầy đủ c&aacute;c tr&ograve; chơi th&uacute; vị v&agrave; chương tr&igrave;nh nghệ thuật biểu diễn, diễu h&agrave;nh đường phố. Đặc biệt hơn nơi n&agrave;y được thiết kế x&acirc;y dựng dựa tr&ecirc;n cốt l&otilde;i của truyện ngụ ng&ocirc;n Aesop n&ecirc;n sẽ khiến c&aacute;c bạn trẻ m&ecirc; mẩn như lạc v&agrave;o thế giới cổ t&iacute;ch.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/29.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<em>C&ocirc;ng vi&ecirc;n ngo&agrave;i trời Magic Island như xứ sở cổ t&iacute;ch.</em></p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; gợi &yacute; một số c&ocirc;ng vi&ecirc;n th&uacute; vị ở H&agrave;n Quốc v&agrave; thu h&uacute;t nhiều kh&aacute;ch du lịch hiện nay. H&atilde;y c&ugrave;ng gia đ&igrave;nh bạn trải nghiệm chuyến đi tuyệt vời n&agrave;y nh&eacute;!</p>\r\n', 1, 0, 1),
(42, 'Top 3 khu chợ không nên bỏ qua tại HỒNG KÔNG', 3, '1541426229.36.jpg', '<p>Hồng K&ocirc;ng l&agrave; một trong những điểm du lịch được khai th&aacute;c mạnh bởi kh&ocirc;ng chỉ c&ocirc;ng ty du lịch nước ngo&agrave;i&nbsp;m&agrave; c&ograve;n cả c&ocirc;ng ty du lịch trong nước. Đến với Hồng K&ocirc;ng ngo&agrave;i c&aacute;c địa danh nổi tiếng hay m&oacute;n ăn mang đậm hương vị &Aacute; Ch&acirc;u, du kh&aacute;ch c&ograve;n c&oacute; thể nh&igrave;n thấy Hồng K&ocirc;ng kh&ocirc;ng ngủ qua những khu chợ đ&ecirc;m. H&ocirc;m nay, ch&uacute;ng t&ocirc;i&nbsp;sẽ c&ugrave;ng du kh&aacute;ch gh&eacute; qua 5 khu chợ đ&ecirc;m nổi tiếng ở Hồng K&ocirc;ng.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/30.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Khung cảnh tại chợ đ&ecirc;m Qu&yacute; B&agrave;, Hong Kong.</p>\r\n\r\n<p><strong>1.Những khu phức hợp chợ đ&ecirc;m nổi tiếng</strong></p>\r\n\r\n<p>Tour Hồng K&ocirc;ng của c&aacute;c&nbsp;<a href=\"https://www.dulichthiennhien.vn/du-lich-nuoc-ngoai\">c</a>&ocirc;ng ty du lịch nước ngo&agrave;i&nbsp;bất k&igrave; đều kh&ocirc;ng qu&ecirc;n dẫn du kh&aacute;ch đến tham quan chợ Qu&yacute; B&agrave;. Với quy m&ocirc; h&agrave;ng trăm gian h&agrave;ng cũng như sự đa dạng về c&aacute;c mặt h&agrave;ng như quần &aacute;o, qu&agrave; lưu niệm hay trang sức&hellip;</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/31.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Chợ Qu&yacute; B&agrave;, điếm đến hứa hẹn tại Hong Kong.</p>\r\n\r\n<p>C&ugrave;ng mặt h&agrave;ng với chợ Qu&yacute; B&agrave;, du kh&aacute;ch cũng c&oacute; thể tham quan chợ&nbsp;Temple Street, với n&eacute;t đặc biệt l&agrave; những show h&aacute;t kịch kiểu Trung Hoa xưa tại chợ. Hay đến với khu mưa sắm Police Married Quarters, với c&aacute;c gian h&agrave;ng nổi bật c&ugrave;ng m&agrave;u sắc của c&aacute;c m&oacute;n qu&agrave; lưu niệm.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/32.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Cổng v&agrave;o chợ đ&ecirc;m Temple Street.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/33.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Biểu tượng cổng ch&agrave;o &ldquo;chợ đ&ecirc;m&rdquo; Police Married Quarters.</p>\r\n\r\n<p><strong>2. Chợ trang sức Jade</strong></p>\r\n\r\n<p>Tiếp theo, tour du lịch Hồng K&ocirc;ng&nbsp;của&nbsp;ch&uacute;ng t&ocirc;i&nbsp;sẽ c&ugrave;ng du kh&aacute;ch dừng ch&acirc;n tại chợ trang sức Jade. Đến với chợ Jade du kh&aacute;ch sẽ cho&aacute;ng ngợp trước sự lấp l&aacute;nh của những m&oacute;n trang sức đa dạng từ kiểu d&aacute;ng, k&iacute;ch thước v&agrave; m&agrave;u sắc. Trang sức ở đ&acirc;y chắc hẳn l&agrave; m&oacute;n qu&agrave; tuyệt vời tặng cho bạn b&egrave;, người th&acirc;n, đặc biệt l&agrave; ph&aacute;i đẹp. Điểm lưu &yacute; đối với du kh&aacute;ch l&agrave; cần xem kĩ h&agrave;ng trước khi mua v&igrave; nơi đ&acirc;y v&igrave; sẽ dễ mua nhầm đồ giả nếu kh&ocirc;ng kiểm tra m&oacute;n trang sức bạn chọn.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/34.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Những quầy h&agrave;ng lấp l&aacute;nh tại chợ trang sức Jade, Hong Kong.</p>\r\n\r\n<p><strong>3. Chợ d&agrave;nh cho t&iacute;n đồ c&ocirc;ng nghệ -&nbsp;chợ ApLiu</strong></p>\r\n\r\n<p>Điểm đến tiếp theo trong top 5 khu chợ đ&ecirc;m nổi tiếng của tour du lịch Hồng K&ocirc;ng&nbsp;l&agrave; chợ ApLiu. Với những t&iacute;n đồ y&ecirc;u c&ocirc;ng nghệ Apliu l&agrave; thi&ecirc;n đường mua sắm. &nbsp;</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/35.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Một quầy h&agrave;ng chuy&ecirc;n về m&aacute;y ảnh tại chợ đ&ecirc;m c&ocirc;ng nghệ ApLiu.</p>\r\n\r\n<p>Đến với chợ Apliu, du kh&aacute;ch c&oacute; thể mua c&aacute;c sản phẩm điện tử như điện thoại, m&aacute;y ảnh, m&aacute;y t&iacute;nh, m&aacute;y t&iacute;nh bảng, DVD&hellip; từ h&agrave;ng mới cho đến h&agrave;ng đ&atilde; qua sử dụng. Cũng ch&iacute;nh v&igrave; sự đa dạng về mặt h&agrave;ng cũng nhưu nguồn gốc, du kh&aacute;ch n&ecirc;n c&oacute; sự t&igrave;m hiểu kĩ trước cũng như mức gi&aacute; định cho m&oacute;n h&agrave;ng muốn mu atrước khi v&agrave;o chợ để tr&aacute;nh mua phải h&agrave;ng kh&ocirc;ng như &yacute;.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/36.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Note lại c&aacute;c khu chợ n&agrave;y khi đi Hồng K&ocirc;ng bạn nh&eacute;!</p>\r\n\r\n<p>Tr&ecirc;n đ&acirc;y l&agrave; top c&aacute;c khu chợ nổi tiếng d&agrave;nh cho việc tham quan v&agrave; mua sắm m&agrave;&nbsp;ch&uacute;ng t&ocirc;i&nbsp;gợi &yacute; cho bạn nếu c&oacute; cơ hội đi Hong Kong.</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 0, 1);
INSERT INTO `tin_tuc` (`id_tin`, `tieu_de`, `danh_muc_id`, `hinh_anh`, `noi_dung`, `luot_xem`, `id_nguoi_dang`, `hien_thi`) VALUES
(43, 'Thả hồn theo những ngọn đèn của lễ hội đẹp nhất ở Hàn Quốc', 3, '1541426913.42.jpg', '<p>Nếu bạn y&ecirc;u th&iacute;ch những chiếc đ&egrave;n lồng, những m&agrave;u sắc rực rỡ tr&ecirc;n bầu trời đ&ecirc;m th&igrave; chắc chắn kh&ocirc;ng n&ecirc;n bỏ qua việc t&igrave;m hiểu lễ hội n&agrave;y.</p>\r\n\r\n<p>Chắc hẳn nhiều người đ&atilde; biết đến c&aacute;c lễ hội đ&egrave;n lồng tại Th&aacute;i Lan th&igrave; h&ocirc;m nay ch&uacute;ng t&ocirc;i&nbsp;sẽ giới thiệu cho bạn một lễ hội tương tự tại H&agrave;n Quốc. Sau khi t&igrave;m hiểu, h&atilde;y t&igrave;m ng&agrave;y cho m&igrave;nh một tour du lịch H&agrave;n Quốc&nbsp;để tham gia&nbsp;nh&eacute;. Với t&ecirc;n gọi lễ hội đ&egrave;n lồng Dalgubeaol, h&atilde;y c&ugrave;ng ch&uacute;ng t&ocirc;i&nbsp;t&igrave;m hiểu về n&oacute; nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/37.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Lễ hội đ&egrave;n lồng ở H&agrave;n Quốc.</p>\r\n\r\n<p><strong>1/ Đ&ocirc;i n&eacute;t về lễ hội đ&egrave;n lồng Dalgubeol</strong></p>\r\n\r\n<p>Lễ hội Dalgubeol (Dalgubeol Lantern Festival) được tổ chức h&agrave;ng năm để kỷ niệm ng&agrave;y sinh của Đức Phật. Đ&acirc;y l&agrave; lễ hội c&oacute; truyền thống kh&aacute; l&acirc;u đời tại H&agrave;n Quốc, bắt nguồn từ thời T&acirc;n La Thống Nhất (668 &ndash; 935) v&agrave; từ thời Cao Ly (918 &ndash; 1932).</p>\r\n\r\n<p>Lễ hội n&agrave;y được tổ chức tại rất nhiều tỉnh, th&agrave;nh phố tr&ecirc;n đất nước H&agrave;n Quốc, nhưng nổi tiếng v&agrave; cũng th&uacute; vị nhất l&agrave; Dalgubeol ở Daegu. Thời gian diễn ra lễ hội ở Daegu v&agrave;o th&aacute;ng 4 h&agrave;ng năm, thu h&uacute;t h&agrave;ng ng&agrave;n người d&acirc;n Deagu cũng như du kh&aacute;ch. Mọi người sẽ tập trung tại s&acirc;n b&oacute;ng ch&agrave;y Duryu nằm trong quần thể c&ocirc;ng vi&ecirc;n Duryu để thả đ&egrave;n trời, mang theo ước nguyện b&igrave;nh an v&agrave; hạnh ph&uacute;c. Nếu bạn đ&atilde; lỡ chọn tour H&agrave;n Quốc m&ugrave;a thu&nbsp;th&igrave; cũng đừng qu&aacute; tiếc nuối khi bỏ lỡ lễ hội n&agrave;y nh&eacute;.</p>\r\n\r\n<p>Cứ v&agrave;o khoảng thời gian tổ chức lễ hội, nhiều người d&acirc;n c&ugrave;ng kh&aacute;ch du lịch sẽ hội tụ về s&acirc;n b&oacute;ng ch&agrave;y Duryu thả đ&egrave;n trời. H&agrave;ng ng&agrave;n chiếc đ&egrave;n c&ugrave;ng được thả l&ecirc;n trời đ&ecirc;m mang theo những ước nguyện b&igrave;nh an v&agrave; mu&ocirc;n điều hạnh ph&uacute;c.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/38.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Lễ hội Dalgubeol đ&atilde; dược tiến h&agrave;nh từ kh&aacute; l&acirc;u.</p>\r\n\r\n<p><strong>2/ Th&ocirc;ng tin về lễ hội Dalgubeol</strong></p>\r\n\r\n<p>Nếu muốn tham gia lễ hội n&agrave;y th&igrave; bạn cần phải mua v&eacute;. Hiện tại c&oacute; hai h&igrave;nh thức để bạn c&oacute; thể sở hữu được v&eacute; v&agrave;o l&agrave; mua online v&agrave; mua tại địa điểm diễn ra lễ hội. Tuy nhi&ecirc;n, việc mua v&eacute; online sẽ rất kh&oacute; khăn v&igrave; v&eacute; hết rất nhanh v&agrave; số lượng cũng rất &iacute;t.</p>\r\n\r\n<p>Nếu kh&ocirc;ng thể mua được v&eacute; online th&igrave; bạn phải đến s&acirc;n vận động v&agrave; xếp h&agrave;ng thật sớm để c&oacute; thể t&igrave;m cho m&igrave;nh một chiếc v&eacute; trong 6000 v&eacute; được ph&aacute;t miễn ph&iacute;. Với ti&ecirc;u ch&iacute; &ldquo;first come first serve&rdquo; th&igrave; bạn kh&ocirc;ng c&oacute; c&aacute;ch n&agrave;o để đặt cọc cho chiếc v&eacute; n&agrave;y đ&acirc;u. V&igrave; thế, nếu cả đến việc xếp h&agrave;ng m&agrave; bạn cũng kh&ocirc;ng mua được v&eacute; th&igrave; kh&ocirc;ng c&ograve;n c&aacute;ch n&agrave;o v&agrave;o được lễ hội nữa cả. V&igrave; thời gian xếp h&agrave;ng c&oacute; thể k&eacute;o d&agrave;i n&ecirc;n bạn h&atilde;y đem đồ ăn đến hoặc mua đồ ăn nhanh tại c&ocirc;ng vi&ecirc;n nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/39.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;V&eacute; l&agrave; dạng v&ograve;ng tay v&agrave; sẽ được b&aacute;n hết trong thời gian ngắn.</p>\r\n\r\n<p><strong>3/ C&aacute;c hoạt động trong lễ hội Dalgubeol</strong></p>\r\n\r\n<p>Th&ocirc;ng thường, lễ hội Dalgubeol sẽ được tiến h&agrave;nh trong bốn ng&agrave;y với nhiều hoạt động th&uacute; vị. Bạn sẽ được trải nghiệm c&aacute;c hoạt động như thả đ&egrave;n trời, thả đ&egrave;n hoa đăng tr&ecirc;n s&ocirc;ng&hellip; v&agrave; c&oacute; lẽ đ&aacute;ng mong chờ nhất l&agrave; lễ diễu h&agrave;nh đ&egrave;n lồng Dalgubeol.</p>\r\n\r\n<p>Ban tổ chức sẽ mở cửa đ&oacute;n kh&aacute;ch v&agrave;o 13 giờ 30 ph&uacute;t hoặc sớm hơn l&agrave; 12 giờ v&agrave; sẽ mở cho đến 17 giờ. Lễ hội sẽ bắt đầu v&agrave;o khoảng 18 giờ 30 ph&uacute;t v&agrave; kết th&uacute;c l&uacute;c 21 giờ. Tham gia lễ hội, bạn sẽ được tận mắt chứng kiến h&agrave;ng ng&agrave;n chiếc đ&egrave;n lồng được thả l&ecirc;n bầu trời đ&ecirc;m. Một khung cảnh tuyệt đẹp với nền đen v&agrave; điểm nhất l&agrave; những chiếc đ&egrave;n k&egrave;m theo những lời ước, những mong muốn cho năm mới. Sau khi kết th&uacute;c hội đ&egrave;n trời th&igrave; sẽ c&oacute; tiết mục ph&aacute;o hoa hấp dẫn, v&igrave; thế đừng vội ra về nh&eacute;!</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/40.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; Thả đ&egrave;n trời sẽ l&agrave; hoạt động ch&iacute;nh của lễ hội.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/41.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Tham gia lễ hội, bạn sẽ được chi&ecirc;m ngưỡng khung cảnh tuyệt vời n&agrave;y.</p>\r\n\r\n<p>B&ecirc;n cạnh lễ hội thả đ&egrave;n trời th&igrave; vẫn c&oacute; rất nhiều hoạt động hấp dẫn kh&aacute;c như lễ diễu h&agrave;nh đ&egrave;n lồng Dalgubeol diễn ra v&agrave;o l&uacute;c 18 giờ. Với c&aacute;c xe đ&egrave;n lồng được trang tr&iacute; c&ocirc;ng phu c&ugrave;ng c&aacute;c đo&agrave;n biểu diễn nghệ thuật, hoạt động n&agrave;y bắt đầu từ c&ocirc;ng vi&ecirc;n Duryu v&agrave; đi dọc c&aacute;c tuyến đường ở phố Bangogae, phố Shinnam, ng&atilde; năm phố Gyesan v&agrave; kết th&uacute;c ở phố Banwoldang.</p>\r\n\r\n<p><img alt=\"\" src=\"/banhkeo/public/uploads/images/42.jpg\" style=\"height:534px; width:800px\" />&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Ngo&agrave;i thả đ&egrave;n th&igrave; lễ hội c&ograve;n rất nhiều hoạt động th&uacute; vị kh&aacute;c.</p>\r\n\r\n<p>Đ&acirc;y quả l&agrave; một lễ hội tuyệt vời đ&uacute;ng kh&ocirc;ng n&agrave;o, nếu bạn đang c&oacute; dự định đến H&agrave;n quốc vi vu th&igrave; đừng bỏ qua cơ hội tham gia n&oacute; nh&eacute;.</p>\r\n', 0, 0, 1);
INSERT INTO `tin_tuc` (`id_tin`, `tieu_de`, `danh_muc_id`, `hinh_anh`, `noi_dung`, `luot_xem`, `id_nguoi_dang`, `hien_thi`) VALUES
(44, 'Đến nhà cổ Bình Thủy – tham quan ngôi nhà cổ đẹp nhất xứ Tây Đô', 1, '1540407463.nha-co-binh-thuy1-min_1540371371.jpg', '<p>Cuối thế kỉ 18. V&ugrave;ng ph&iacute;a T&acirc;y Nam v&agrave;m s&ocirc;ng Cần Thơ ph&ugrave; sa nước ngọt tưới m&aacute;t quanh năm. Đ&acirc;y cũng l&agrave; nơi thu h&uacute;t nhiều người đổ về chọn đất an cư lập nghiệp. Trong đ&oacute; c&oacute; gia tộc Họ Dương.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Gia tộc Họ Dương đến đất Nam Bộ lập nghiệp, đ&atilde; sống qua nhiều thế hệ tại đ&acirc;y. Ở thế hệ thứ 3, &ocirc;ng Dương Văn Vị cũng l&agrave; người kh&aacute; gi&agrave;u c&oacute; của d&ograve;ng họ, đ&atilde; chọn v&ugrave;ng đất gần v&agrave;m rạch B&igrave;nh Thủy l&agrave;m nơi dựng nh&agrave;.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy1-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Nh&agrave; cổ B&igrave;nh Thủy nh&igrave;n từ ch&iacute;nh diện</em></p>\r\n\r\n<p><br />\r\nL&uacute;c mới đầu, nh&agrave; được &ocirc;ng Vị dựng bằng gỗ v&agrave; lợp ng&oacute;i, d&ugrave;ng để thờ tổ ti&ecirc;n. M&atilde;i đến 30 năm sau đ&oacute; mới bắt đầu được t&ocirc;n tạo lại. Sau khi &ocirc;ng Vị mất, con trai &uacute;t của &ocirc;ng l&agrave; Dương Chấn Kỷ (l&uacute;c đ&oacute; l&agrave; một điền chủ rất gi&agrave;u c&oacute;) tiếp tục x&acirc;y dựng v&agrave; ho&agrave;n thiện ng&ocirc;i nh&agrave; v&agrave;o năm 1911.<br />\r\n&nbsp;</p>\r\n\r\n<p>&Ocirc;ng Kỷ l&agrave; một thương gia rất th&iacute;ch t&igrave;m t&ograve;i c&aacute;i mới lạ, nhất l&agrave; về kiến tr&uacute;c v&agrave; nghệ thuật. Thời bấy giờ đang thịnh h&agrave;nh tr&agrave;o lưu T&acirc;y phương, &ocirc;ng liền cho người x&acirc;y dựng ng&ocirc;i nh&agrave; theo kiến tr&uacute;c ch&acirc;u &Acirc;u.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Những năm 1980, hậu duệ đời thứ 5 của gia tộc họ Dương l&agrave; &ocirc;ng Dương Văn Ng&ocirc;n c&oacute; th&uacute; chơi hoa kiểng. &Ocirc;ng sưu tầm kh&aacute; nhiều giống lan qu&yacute; v&agrave; hay tổ chức giao lưu, chơi lan. Do đ&oacute;, thời n&agrave;y ng&ocirc;i nh&agrave; c&ograve;n c&oacute; thể l&agrave; &ldquo;Vườn lan B&igrave;nh Thủy&rdquo;<br />\r\n&nbsp;</p>\r\n\r\n<h3>Du lịch Cần Thơ nhớ gh&eacute; thăm nh&agrave; cổ B&igrave;nh Thủy &ndash; nh&agrave; cổ đẹp nhất xứ T&acirc;y Đ&ocirc;<br />\r\n&nbsp;</h3>\r\n\r\n<p>Trong số 70 ng&ocirc;i nh&agrave; cổ ở Cần thơ th&igrave; nh&agrave; cổ B&igrave;nh Thủy l&agrave; đẹp nhất. Ng&ocirc;i nh&agrave; cho đến ngay vẫn giữ nguy&ecirc;n được sự bề thế v&agrave; đẹp đẽ như thời điểm mới x&acirc;y do được tr&ugrave;ng tu kĩ lưỡng. Thoạt nh&igrave;n kiến tr&uacute;c c&oacute; vẻ cổ k&iacute;nh, trang nghi&ecirc;m, nhưng đường n&eacute;t đi&ecirc;u khắc lại to&aacute;t n&ecirc;n vẻ ph&oacute;ng kho&aacute;ng như t&iacute;nh c&aacute;ch con người miền T&acirc;y.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Cổng r&agrave;o l&agrave;m bằng b&ecirc; t&ocirc;ng v&agrave; sắt ki&ecirc;n cố theo kiểu dinh thự Ph&aacute;p. Cổng phụ được x&acirc;y theo kiến tr&uacute;c &Aacute; Đ&ocirc;ng với 4 cột tr&ograve;n vững chắc.<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-7-min.jpg\" style=\"height:532px; width:800px\" /></p>\r\n\r\n<p><em>Cổng lớn được x&acirc;y dựng như cổng một biệt thự thời nay<br />\r\n<img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-9-min.jpg\" style=\"height:533px; width:800px\" /></em></p>\r\n\r\n<p><em>Cổng phụ mang n&eacute;t kiến tr&uacute;c phương Đ&ocirc;ng</em></p>\r\n\r\n<p><br />\r\nNh&agrave; cổ nằm tr&ecirc;n một diện t&iacute;ch lớn khoảng 8.000 m&eacute;t vu&ocirc;ng, bao gồm cả vườn tược. Ri&ecirc;ng khu nh&agrave; được bao bọc bởi c&acirc;y cảnh được cắt tỉa chu đ&aacute;o, hoa nở rộ bốn m&ugrave;a. Khiến cho ng&ocirc;i nh&agrave; vừa mang n&eacute;t ho&agrave;i cổ k&iacute;nh đ&aacute;o vừa sống động, thơ mộng v&agrave; tươi mới.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy2-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Xung quanh nh&agrave; trồng kh&aacute; nhiều hoa v&agrave; c&acirc;y cảnh được cắt tỉa chu đ&aacute;o</em><br />\r\n<br />\r\n<br />\r\nTrước khi v&agrave;o được b&ecirc;n trong, du kh&aacute;ch sẽ nh&igrave;n thấy một cầu thang c&aacute;ch điệu h&igrave;nh c&aacute;nh cung tao nh&atilde;, trang tr&iacute; hoa văn tinh tế. Cầu thang n&agrave;y l&agrave; điểm nối từ khoảng s&acirc;n đến t&ograve;a nh&agrave;. Nh&agrave; c&oacute; 5 gian 2 m&aacute;i, chia th&agrave;nh nh&agrave; trước, giữa v&agrave; sau. Nền nh&agrave; được l&aacute;t gạch nhập khẩu từ Ph&aacute;, đ&oacute;ng trần platfond, tr&ecirc;n c&aacute;c cửa lớn đều được chạm khắc rất tinh tế.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-15-min.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><em>Cầu thang xoắn nối liền nh&agrave; với s&acirc;n vườn</em></p>\r\n\r\n<p><br />\r\nB&ecirc;n trong, nh&agrave; cổ B&igrave;nh Thủy nổi bật với 3 bộ b&agrave;n ghế cổ đẹp v&agrave; 1 sập gụ được trang tr&iacute; khảm ngọ trai tinh xảo, đường n&eacute;t hoa văn chạm khắc mềm mại, xinh đẹp. Ở gian giữa b&agrave;y b&agrave;n thờ tổ ti&ecirc;n đ&uacute;ng kiểu của người Nam Bộ. Gian thờ được sơn son thếp v&agrave;ng, giường thờ, sập gụ được chạm khắc tinh xảo Mai, lan, c&uacute;c, tr&uacute;c, Ph&uacute;c &ndash; Lộc &ndash; Thọ, long, l&acirc;n, quy, phụng&hellip;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy4-min.jpg\" style=\"height:531px; width:800px\" /></p>\r\n\r\n<p><em>Bộ b&agrave;n ghế cổ<br />\r\n<br />\r\n<strong><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-6-min.jpg\" style=\"height:534px; width:800px\" /></strong></em></p>\r\n\r\n<p><br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://tour.dulichvietnam.com.vn/du-lich-trong-nuoc/du-lich-mien-tay.html/63\"><strong>Tour lịch miền T&acirc;y gi&aacute; rẻ</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Nh&agrave; cổ B&igrave;nh Thủy</strong>&nbsp;c&ograve;n c&oacute; cả một kho đồ cổ đ&atilde; được g&igrave;n giữ suốt nhiều thế kỉ qua. Trong đ&oacute; c&oacute; hai bộ b&agrave;n ghế xuất xứ từ V&acirc;n Nam, Trung Quốc, mặt b&agrave;n l&agrave;m bằng đ&aacute; cẩm thạch, v&acirc;n xanh; bộ salon kiểu Ph&aacute;p đời Louis XV, cặp đ&egrave;n treo thế kỉ 19&hellip; Những hiện vật n&agrave;y cho ta biết được mức độ gi&agrave;u c&oacute; v&agrave; th&uacute; chơi đồ cao cấp của mấy đời gia đ&igrave;nh họ Dương đất B&igrave;nh Thủy, Cần Thơ.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-16-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Đồ cổ trong nh&agrave; cổ B&igrave;nh Thủy vẫn c&ograve;n đến ng&agrave;y nay</em><br />\r\n&nbsp;</p>\r\n\r\n<p>Khu vườn xung quanh nh&agrave; được trang tr&iacute; h&ograve;n non bộ, c&acirc;y cảnh được cắt tỉa xinh đẹp, tinh tế.&nbsp;Nh&agrave; cổ B&igrave;nh Thủy với kiến tr&uacute;c ho&agrave;i cổ độc đ&aacute;o đ&atilde; trở th&agrave;nh địa điểm quay phim cho c&aacute;c bộ phim như &ldquo;Người đẹp T&acirc;y Đ&ocirc;&rdquo;, &ldquo;Những nẻo đường xa&rdquo;, &ldquo;Nợ đời&rdquo;, &ldquo;Người t&igrave;nh&rdquo;.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Năm 2009, nh&agrave; cổ B&igrave;nh Thủy đ&atilde; được Bộ Văn h&oacute;a - Thể thao &amp; Du lịch c&ocirc;ng nhận l&agrave; Di t&iacute;ch kiến tr&uacute;c nghệ thuật cấp quốc gia.<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Nh&agrave; cổ B&igrave;nh Thủy</strong>&nbsp;hiện nay l&agrave; một trong những điểm tham quan thu h&uacute;t du kh&aacute;ch khi du lịch Cần Thơ. Nh&agrave; cổ mở cửa tiếp đ&oacute;n du kh&aacute;ch hằng ng&agrave;y. Đến đ&acirc;y l&agrave; đến với kh&ocirc;ng gian thơ mộng, hơi ho&agrave;i cổ nhưng cũng rất ph&oacute;ng kho&aacute;ng. Giới trẻ cũng thường t&igrave;m đến đ&acirc;y để chụp ảnh lưu niệm bởi khung cảnh nơi đ&acirc;y l&ecirc;n ảnh rất đẹp.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"nhà cổ Bình Thủy\" src=\"https://www.dulichvietnam.com.vn/data/nha-co-binh-thuy-8-min.jpg\" style=\"height:533px; width:800px\" /></p>\r\n\r\n<p><br />\r\nNếu c&oacute; dịp&nbsp;<strong>du lịch Cần Thơ</strong>&nbsp;hoặc&nbsp;<strong>du lịch miền T&acirc;y</strong>, đừng qu&ecirc;n gh&eacute; thăm nh&agrave; cổ B&igrave;nh Thủy &ndash; ng&ocirc;i nh&agrave; cổ đẹp nhất xứ T&acirc;y Đ&ocirc;. Du kh&aacute;ch c&oacute; thể đăng k&iacute; tour du lịch để được thiết kế lộ tr&igrave;nh tham quan l&iacute; tưởng nhất, tiết kiệm thời gian v&agrave; chi ph&iacute; nhất.&nbsp;</p>\r\n\r\n<p><strong><em>Cẩm Luyến</em></strong></p>\r\n', 3, 0, 1),
(45, 'Khám phá vẻ đẹp Côn Đảo dưới sắc trời mùa thu', 1, '1540407610.con-dao-20-min_1539855807.jpg', '<p><strong>C&ocirc;n Đảo</strong>&nbsp;(B&agrave; Rịa &ndash; Vũng T&agrave;u) l&agrave; một quần đảo bao gồm 16 h&ograve;n đảo lớn nhỏ, diện t&iacute;ch khoảng 76 km vu&ocirc;ng. C&ocirc;n Đảo nằm c&aacute;ch Vũng T&agrave;u 97 hải l&yacute; v&agrave; c&aacute;ch s&ocirc;ng Hậu 45 hải l&yacute;.<br />\r\nTầm th&aacute;ng 10 - 11 l&agrave; thời điểm C&ocirc;n Đảo bước v&agrave;o m&ugrave;a kh&ocirc;, cũng l&agrave; l&uacute;c đẹp nhất khi trời v&agrave;o thu. Đ&acirc;y ch&iacute;nh l&agrave; thời điểm l&iacute; tưởng để đi du lịch C&ocirc;n Đảo. Ngo&agrave;i biển &iacute;t nhiều sẽ c&oacute; s&oacute;ng lớn, nhưng điều n&agrave;y chẳng g&acirc;y ảnh hưởng g&igrave; mấy, nếu bạn đến c&aacute;c h&ograve;n đảo v&agrave; ở trong b&atilde;i biển.&nbsp;<br />\r\nC&ocirc;n Đảo qua thời gian vẫn giữ được n&eacute;t đẹp thi&ecirc;n nhi&ecirc;n hoang sơ, dịu d&agrave;ng. Khi &aacute;nh nắng trời thu chiếu rọi, nước biển chuyển sang ngọc b&iacute;ch đẹp đẽ. B&atilde;i c&aacute;t trải d&agrave;i mịn m&agrave;ng, b&atilde;i tắm sạch sẽ, nước trong veo. &Aacute;nh nắng m&ugrave;a thu cũng kh&ocirc;ng qu&aacute; gắt như nắng hạ, du kh&aacute;ch c&oacute; thể tắm biển tại b&atilde;i biển Đầm Trầu.&nbsp;</p>\r\n\r\n<p>Người ta đến C&ocirc;n Đảo đầu ti&ecirc;n l&agrave; để thăm Nh&agrave; t&ugrave; C&ocirc;n Đảo. Nơi địa ngục trần gian n&agrave;y đ&atilde; từng giam giữ v&agrave; tra tấn d&atilde; man những người t&ugrave; c&aacute;ch mạng, người y&ecirc;u nước. Ngo&agrave;i nh&agrave; t&ugrave; C&ocirc;n Đảo c&ograve;n c&oacute; nghĩa trang H&agrave;ng Dương cũng thu h&uacute;t nhiều du kh&aacute;ch đến tưởng nhớ v&agrave; b&agrave;y tỏ l&ograve;ng biết ơn với c&aacute;c chiến sĩ c&aacute;ch mạng ng&agrave;y xưa.&nbsp;</p>\r\n\r\n<p>V&agrave;o m&ugrave;a n&agrave;y, nếu muốn c&oacute; một chuyến đi tham quan nhẹ nh&agrave;ng cũng đừng ngại chọn C&ocirc;n Đảo. Sắc trời m&ugrave;a thu tạo ra cho nơi đ&acirc;y khung cảnh đẹp tuyệt điệu. Du kh&aacute;ch c&oacute; thể tham quan c&aacute;c địa điểm như Dinh Ch&uacute;a Đảo, Hệ thống chuồng cọp, Miếu b&agrave; Phi Yến, B&atilde;i Nh&aacute;t, Cảng Bến Đầm, H&ograve;n B&agrave;, Ch&ugrave;a N&uacute;i Một, B&atilde;i Suối N&oacute;ng, H&ograve;n Bảy Cạnh, H&ograve;n T&agrave;i, H&ograve;n Cau, H&ograve;n Tre Lớn&hellip;</p>\r\n\r\n<p>Nếu đ&atilde; du lịch đến C&ocirc;n Đảo v&agrave;o m&ugrave;a n&agrave;y, h&atilde;y thử trải nghiệm lặn ngắm san h&ocirc; một lần nh&eacute;. Cũng đừng qu&ecirc;n thưởng thức qua những m&oacute;n ăn hấp dẫn của nơi n&agrave;y. C&aacute; thu một nắng, ch&aacute;o h&agrave;u, cua mặt trăng, mắm nhum, t&ocirc;m h&ugrave;m đỏ&hellip;đều l&agrave; đặc sản C&ocirc;n Đảo độc đ&aacute;o, hấp dẫn</p>\r\n\r\n<p>Kh&aacute;c với c&aacute;c khu du lịch đ&ocirc;ng đ&uacute;c, ồn &agrave;o kh&aacute;c,&nbsp;<strong>C&ocirc;n Đảo</strong>&nbsp;cho đến nay vẫn giữ được vẹn nguy&ecirc;n n&eacute;t đẹp tự nhi&ecirc;n đ&aacute;ng tr&acirc;n qu&yacute;. Người d&acirc;n nơi đ&acirc;y nhiệt t&igrave;nh, th&acirc;n thiện, ch&acirc;n chất, dễ mến. Kh&ocirc;ng kh&iacute; th&igrave; lu&ocirc;n thanh b&igrave;nh y&ecirc;n ả. Nếu như đ&atilde; qu&aacute; mỏi mệt với những bon chen nơi th&agrave;nh phố, h&atilde;y đến C&ocirc;n Đảo m&ugrave;a thu để nghỉ ngơi nh&eacute;</p>\r\n', 4, 0, 1),
(46, 'Khu nghỉ dưỡng thân thiện với thiên nhiên nhất châu Á', 1, '1540412990.22-10-201857-740388268-w500-5136-1540206579_180x108.png', '<p>Đến nay, InterContinental Danang Sun Peninsula Resort trở th&agrave;nh một minh chứng điển h&igrave;nh cho triết l&yacute; kiến tr&uacute;c n&agrave;y của &ocirc;ng.<strong>Mỗi ng&agrave;y một... xanh hơn</strong></p>\r\n\r\n<p>Bậc thầy kiến tr&uacute;c Bill Bensley từng chia sẻ cảm gi&aacute;c &ldquo;giống như được về nh&agrave;&rdquo; mỗi khi trở lại khu nghỉ dưỡng b&ecirc;n b&aacute;n đảo Sơn Tr&agrave;. Điều &ocirc;ng t&acirc;m đắc nhất ở &ldquo;ng&ocirc;i nh&agrave;&rdquo; n&agrave;y l&agrave; n&oacute; mỗi ng&agrave;y một xanh hơn.</p>\r\n\r\n<p>&ldquo;Ng&agrave;y ấy, t&ocirc;i đ&atilde; t&igrave;m c&aacute;ch hạn chế tối đa việc động chạm m&ocirc;i trường trong qu&aacute; tr&igrave;nh thi c&ocirc;ng. Giờ đ&acirc;y, mọi thứ đ&atilde; xanh tươi trở lại v&agrave; thậm ch&iacute; tr&ocirc;ng c&ograve;n tự nhi&ecirc;n hơn trước kia. T&ocirc;i thật sự rất y&ecirc;u nơi n&agrave;y, tất cả những g&igrave; t&ocirc;i l&agrave;m l&agrave; cố gắng x&acirc;y n&ecirc;n một c&ocirc;ng tr&igrave;nh cho ph&eacute;p mọi người sống c&ugrave;ng với thi&ecirc;n nhi&ecirc;n&rdquo;, Bill Bensley chia sẻ.</p>\r\n\r\n<p>Thật vậy, nối từ tầng Biển cả đến tầng Thi&ecirc;n đường của khu nghỉ dưỡng l&agrave; chiếc cầu thang gỗ uốn lượn quanh co như mọc giữa rừng gi&agrave;. Bill Bensley đ&atilde; thống nhất với chủ đầu tư khu nghỉ dưỡng n&agrave;y - Tập đo&agrave;n Sun Group, rằng tr&ecirc;n con đường đi của c&aacute;i cầu thang gỗ đ&oacute;, kh&ocirc;ng c&oacute; chuyện chặt c&acirc;y l&agrave;m đường, chỉ c&oacute; thể l&agrave; uốn đường để nhường c&acirc;y. Nguy&ecirc;n tắc đ&oacute; được t&ocirc;n trọng tối đa, từ l&uacute;c x&acirc;y dựng đến khi chỉnh trang để đ&oacute;n đo&agrave;n thượng kh&aacute;ch APEC năm 2017.</p>\r\n\r\n<p>Cũng từ cầu thang gỗ, du kh&aacute;ch sẽ thấy những c&aacute;nh rừng nguy&ecirc;n sinh, ngọn đồi huyền thoại, b&atilde;i c&aacute;t trắng muốt ven theo đại dương xanh ngọc. Tất cả c&ograve;n đ&oacute;, vẻ đẹp nguy&ecirc;n vẹn của thi&ecirc;n tạo.</p>\r\n\r\n<p>Mỗi căn biệt thự trong khu nghỉ dưỡng được đặt men theo c&aacute;c ngọn đồi, h&agrave;i ho&agrave; với n&uacute;i rừng. Du kh&aacute;ch vừa c&oacute; thể đứng ở ban c&ocirc;ng ngắm nh&igrave;n khung cảnh kỳ vĩ của b&aacute;n đảo Sơn Tr&agrave;, vừa nghe tiếng g&aacute;y của g&agrave; mặt đỏ, xem những ch&uacute; s&oacute;c nhảy nh&oacute;t, v&agrave; theo d&otilde;i lo&agrave;i voọc ch&agrave; v&aacute; ch&acirc;n n&acirc;u qu&yacute; hiếm t&igrave;m kiếm l&aacute; non tr&ecirc;n t&aacute;n c&acirc;y gần đ&oacute;. &ldquo;Nghỉ dưỡng giữa rừng nguy&ecirc;n sinh&rdquo;, đ&oacute; l&agrave; trải nghiệm đang chờ đ&oacute;n du kh&aacute;ch tại InterContinental Danang Sun Peninsula Resort.</p>\r\n\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td><img alt=\"polyad\" src=\"https://i-dulich.vnecdn.net/2018/10/22/22-10-201856-276787815-w500-6557-1540206579.png\" /></td>\r\n		</tr>\r\n		<tr>\r\n			<td>\r\n			<p>Voọc ch&agrave; v&aacute; ch&acirc;n n&acirc;u nổi tiếng ở b&aacute;n đảo Sơn Tr&agrave;.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n\r\n<p>Năm 2018, &ldquo;khu nghỉ dưỡng sang trọng bậc nhất thế giới&rdquo; n&agrave;y lại tiếp tục được World Travel Awards khu vực ch&acirc;u &Aacute;, ch&acirc;u Đại Dương trao tặng c&ugrave;ng l&uacute;c 5 giải thưởng, trong đ&oacute; c&oacute; danh hiệu &ldquo;Khu nghỉ dưỡng th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n nhất ch&acirc;u &Aacute;&quot;.</p>\r\n\r\n<p><strong>Tầm nh&igrave;n cho tương lai</strong></p>\r\n\r\n<p>Khu nghỉ c&oacute; ri&ecirc;ng một nh&agrave; động vật học l&agrave; điều hiếm c&oacute; kh&ocirc;ng chỉ ở Việt Nam m&agrave; tr&ecirc;n cả thế giới. Tuy nhi&ecirc;n, đ&oacute; l&agrave; điều đ&oacute; đ&atilde; trở th&agrave;nh hiện thực khi James Morrison l&agrave; nh&agrave; động vật học của ri&ecirc;ng InterContinental Danang Sun Peninsula Resort.</p>\r\n\r\n<p>James Morrison bắt đầu l&agrave;m việc trong lĩnh vực bảo tồn từ năm 2011 tại Vườn quốc gia Kruger, Nam Phi. Đam m&ecirc; bảo tồn thi&ecirc;n nhi&ecirc;n, &ocirc;ng tới Việt Nam để giảng dạy v&agrave; nghi&ecirc;n cứu m&ocirc;i trường tại Đại học Quốc gia H&agrave; Nội. Sau nhiều h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; v&agrave; nghi&ecirc;n cứu c&aacute;c c&ocirc;ng vi&ecirc;n quốc gia ở Myanmar, Bangladesh, Nepal... &ocirc;ng quyết định trở về Việt Nam, trong vai tr&ograve; Trưởng ph&ograve;ng Bảo tồn, C&ocirc;ng vi&ecirc;n quốc gia P&ugrave; M&aacute;t cho tổ chức Bảo vệ Động vật hoang d&atilde; của Việt Nam v&agrave; hiện l&agrave;m việc cho resort ở Sơn Tr&agrave;.</p>\r\n\r\n<p>&ldquo;Chỉ c&oacute; một v&agrave;i nơi ở Việt Nam sở hữu đa dạng sinh học phong ph&uacute; như b&aacute;n đảo Sơn Tr&agrave;&rdquo;, James chia sẻ, &ldquo;Đ&oacute; l&agrave; l&yacute; do việc bảo vệ hệ sinh th&aacute;i l&agrave; rất quan trọng&quot;. Theo đ&oacute;, &ocirc;ng đảm nhận việc nghi&ecirc;n cứu, tư vấn v&agrave; chia sẻ kiến thức về động vật hoang d&atilde; của khu resort v&agrave; c&aacute;c hệ sinh th&aacute;i xung quanh, để giảm thiểu t&aacute;c động đến m&ocirc;i trường tự nhi&ecirc;n bằng c&aacute;ch thực hiện c&aacute;c hoạt động kinh doanh một c&aacute;ch c&oacute; tr&aacute;ch nhiệm. &quot;C&ugrave;ng nhau, ch&uacute;ng ta c&oacute; thể x&acirc;y dựng sự ph&aacute;t triển bền vững kh&ocirc;ng chỉ cho khu dưỡng m&agrave; c&ograve;n cho to&agrave;n bộ v&ugrave;ng sinh th&aacute;i - đ&oacute; l&agrave; tầm nh&igrave;n cho tương lai&rdquo;, James khẳng định.</p>\r\n\r\n<p>Để từng bước thực hiện sứ mệnh của m&igrave;nh tại khu nghỉ dưỡng, James Morrison v&agrave; đồng sự ở resort đ&atilde; c&oacute; một loạt hoạt động, từ n&acirc;ng cấp hệ thống đ&egrave;n LED để giảm đến 75% năng lượng ti&ecirc;u thụ nhằm giảm lượng kh&iacute; thải ra m&ocirc;i trường, đến d&agrave;nh 100% lợi nhuận b&aacute;n đồ lưu niệm tại khu nghỉ được quy&ecirc;n g&oacute;p cho một tổ chức phi ch&iacute;nh phủ nhằm bảo vệ voọc ch&acirc;n n&acirc;u.</p>\r\n\r\n<p>Du kh&aacute;ch đến đ&acirc;y cũng sẽ được James Morrison v&agrave; c&aacute;c nh&acirc;n vi&ecirc;n chia sẻ kiến thức về động vật hoang d&atilde;, như một c&aacute;ch truyền th&ocirc;ng về &yacute; thức bảo vệ m&ocirc;i trường sống. Theo chủ đầu tư, c&aacute;c hoạt động nhằm từng bước đưa resort trở th&agrave;nh một biểu tượng mới của nghỉ dưỡng xanh, xứng đ&aacute;ng với danh hiệu Khu nghỉ dưỡng th&acirc;n thiện với thi&ecirc;n nhi&ecirc;n nhất ch&acirc;u &Aacute; 2018.</p>\r\n', 7, 0, 1),
(7, 'Độc đáo lễ hội Ok Om Bok của người Khmer', 1, '1540414790.le-hoi-ok-om-bok-4-min_1540290643.jpg', '<p>Truyền thuyết&nbsp;<strong>người Khmer&nbsp;</strong>cho rằng thỏ ch&iacute;nh l&agrave; tiền kiếp của Phật Th&iacute;ch Ca. Xưa kia sống ở bờ s&ocirc;ng Hằng. Một h&ocirc;m, thần Sakah giả l&agrave;m ăn xin thử l&ograve;ng thỏ. Kh&ocirc;ng c&oacute; g&igrave; cho thần n&ecirc;n thỏ đốt lửa, tự nhảy v&agrave;o để mời người ăn thịt m&igrave;nh. Lửa bỗng dưng tắt v&agrave; người ăn xin biến mất, thần Sakah hiện ra. Thần muốn khen ngợi l&ograve;ng hy sinh của thỏ n&ecirc;n vẽ h&igrave;nh thỏ l&ecirc;n Mặt trăng. Trong t&iacute;n ngưỡng của người Khmer, lễ c&uacute;ng trăng l&agrave; để tưởng nhớ tiền kiếp của đức Phật Th&iacute;ch Ca. Đồng thời cũng l&agrave; dịp tạ ơn thần mặt trăng đ&atilde; bảo vệ m&ugrave;a m&agrave;ng suốt một năm qua.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-2-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<h2><br />\r\nLễ c&uacute;ng mặt trăng Ok Om Bok</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>V&agrave;i ng&agrave;y trước ng&agrave;y rằm th&aacute;ng 10 &acirc;m lịch, người Khmer đ&atilde; bắt đầu chuẩn bị mọi thứ để l&agrave;m lễ. Đến ng&agrave;y, người Khmer sẽ b&agrave;y biện b&agrave;n c&uacute;ng trong s&acirc;n nh&agrave;, s&acirc;n ch&ugrave;a để quan s&aacute;t mặt trăng.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Trước khi trăng l&ecirc;n, người ta cắm hai thanh tre l&ecirc;n mặt đất, c&aacute;ch nhau 3 m&eacute;t, g&aacute;c ngang một thanh tre kh&aacute;c như h&igrave;nh c&aacute;i cổng. Đặt một c&aacute;i b&agrave;n dưới cổng đ&oacute;. Tr&ecirc;n b&agrave;n b&agrave;y cốm dẹp, khoai lan, khoai m&ocirc;n, dừa tươi, chuối, b&aacute;nh kẹo, tr&agrave;&hellip; Cứ mỗi lần r&oacute;t tr&agrave; l&agrave; họ lại một lần khấn v&aacute;i đức Phật.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-5-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>M&acirc;m c&uacute;ng đầy đủ mọi thứ của người Khmer</em><br />\r\n<br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://www.dulichvietnam.com.vn/tra-vinh/\" target=\"_blank\"><strong>Cẩm nang du lịch Tr&agrave; Vinh</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p>Khi trăng l&ecirc;n đỉnh đầu, người lớn tuổi được người Khmer tin tưởng sẽ đại diện ra c&uacute;ng tạ mặt trăng. R&oacute;t tr&agrave;, thắp nhang rồi khấn nguyện. Trong qu&aacute; tr&igrave;nh c&uacute;ng, trẻ con sẽ tập trung lại chờ được ăn b&aacute;nh. C&uacute;ng xong, người ta sẽ ph&aacute;t b&aacute;nh cho c&aacute;c em nhỏ.&nbsp; Người lớn sẽ đ&uacute;t b&aacute;nh, kẹo cho c&aacute;c em cho đến khi đầy miệng, sau đ&oacute; vỗ lưng c&aacute;c em v&agrave; hỏi c&aacute;c em về ước muốn của m&igrave;nh. Người khmer tin rằng, ước muốn của c&aacute;c em nhỏ cũng l&agrave; động lực của người lớn trong năm tới.<br />\r\n<br />\r\n<img alt=\"lễ hội Ok Om Bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-1-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Người l&agrave;m lễ đ&uacute;t đồ ăn cho trẻ em v&agrave; hỏi c&aacute;c em về ước muốn của m&igrave;nh</em><br />\r\n&nbsp;</p>\r\n\r\n<h2>Trong dịp lễ n&agrave;y c&oacute; 2 sự kiện l&agrave; thả đ&egrave;n gi&oacute; v&agrave; đua ghe Ngo</h2>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<h3>Thả đ&egrave;n gi&oacute;</h3>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đ&egrave;n gi&oacute; được l&agrave;m từ tre, giấy quyến v&agrave; d&acirc;y kẽm. Đ&aacute;y đ&egrave;n để trống c&oacute; lớp g&ograve;n b&ecirc;n trong. L&uacute;c thả th&igrave; đốt lớp g&ograve;n, nhiều người c&ugrave;ng n&acirc;ng đ&egrave;n l&ecirc;n cao. Nhiệt độ l&agrave;m giấy căng phồng, tạo ra lực đẩy để đ&egrave;n bay l&ecirc;n. Đ&egrave;n gi&oacute; bay l&ecirc;n trời mang theo nguyện ước của người Khmer.&nbsp;</p>\r\n\r\n<p><img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-4-min.jpg\" style=\"height:500px; width:800px\" /><br />\r\n&nbsp;</p>\r\n\r\n<h3>Đua ghe Ngo</h3>\r\n\r\n<p><br />\r\nĐua ghe Ngo l&agrave; sự kiện được chờ đợi thứ hai của lễ hội Ok Om Bok. Ghe Ngo l&agrave; t&ecirc;n của một loại thuyền độc mộc. Ghe d&agrave;i 25 - 30 m&eacute;t, rộng khoảng 1 - 1,4 m&eacute;t. Tr&ecirc;n ghe đ&oacute;ng nhiều thanh gỗ g&aacute;c ngang vừa cho hai tay bơi ngồi theo cặp. Mỗi ghe đua c&oacute; thể chở từ 40 - 60 người ch&egrave;o. Người lớn tuổi, c&oacute; chức sắc, được nể trọng sẽ điều khiển nhịp ch&egrave;o v&agrave; ngồi ở mũi ghe. Người cầm l&aacute;i ngồi ở đu&ocirc;i ghe. Mỗi ghe Ngo t&ocirc; vẽ những biểu tượng như cọp, rồng, sư tử&hellip; Trước khi đua, ghe Ngo lu&ocirc;n được tu bổ, hạ thủy tập dượt, l&agrave;m lễ c&uacute;ng b&aacute;i trước rất kĩ c&agrave;ng. C&uacute;ng b&aacute;i trước l&agrave; để cầu c&aacute;c vị thần ph&ugrave; hộ cho qu&aacute; tr&igrave;nh diễn ra cuộc đua được thuận lợi, an to&agrave;n.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p>Đến trưa ng&agrave;y rằm, người d&acirc;n ra hai b&ecirc;n bờ s&ocirc;ng h&aacute;o hức chờ đợi cuộc đua bắt đầu. Sau hồi c&ograve;i hiệu lệnh xuất ph&aacute;t, từng đội ghe đua nhau ch&egrave;o trong tiếng trống, tiếng loa, tiếng reo h&ograve; của người cỗ vũ.&nbsp;<br />\r\n<br />\r\n<img alt=\"lễ hội ok-om-bok\" src=\"https://www.dulichvietnam.com.vn/data/le-hoi-ok-om-bok-min.jpg\" style=\"height:500px; width:800px\" /></p>\r\n\r\n<p><em>Cuộc đua ghe Ngo v&igrave; danh dự của mỗi phum sốc Khmer diễn ra s&ocirc;i nổi</em><br />\r\n<br />\r\n<strong>Xem th&ecirc;m:&nbsp;</strong><a href=\"https://tour.dulichvietnam.com.vn/du-lich-trong-nuoc/du-lich-mien-tay.html/63\" target=\"_blank\"><strong>Tour du lịch miền T&acirc;y</strong></a><br />\r\n&nbsp;</p>\r\n\r\n<p>Ghe Ngo l&agrave; một sản phẩm văn h&oacute;a độc đ&aacute;o của người Khmer Nam Bộ. Ghe cũng l&agrave; biểu tượng của sự no ấm, sung t&uacute;c, l&agrave; đại diện cho mỗi phum s&oacute;c, x&atilde;, huyện. Do đ&oacute;, cuộc đua ghe thường diễn ra quyết liệt kh&ocirc;ng chỉ v&igrave; giải thưởng, m&agrave; c&ograve;n v&igrave; danh dự v&agrave; vinh quang của phum s&oacute;c. Đua ghe Ngo cũng l&agrave; m&ocirc;n thể thao hấp dẫn v&agrave; h&agrave;o hứng.&nbsp;<br />\r\n<br />\r\nNếu du kh&aacute;ch&nbsp;<strong>du lịch miền T&acirc;y Nam Bộ</strong>&nbsp;trong dịp n&agrave;y, được h&ograve;a m&igrave;nh v&agrave;o lễ hội Ok Om Bok, sẽ cảm thấy trải nghiệm n&agrave;y v&ocirc; c&ugrave;ng đ&aacute;ng gi&aacute;. Kh&ocirc;ng kh&iacute; rộn r&agrave;ng, vui nhộn của lễ hỗi sẽ khiến bạn qu&ecirc;n hết mọi phiền muộn, h&ograve;a m&igrave;nh v&agrave;o lễ hội mỗi năm chỉ c&oacute; một lần n&agrave;y.&nbsp;<br />\r\n&nbsp;</p>\r\n\r\n<p><strong>Lễ hội Ok Om Bok&nbsp;</strong>mỗi năm chỉ c&oacute; một lần, mang &yacute; nghĩa v&ocirc; c&ugrave;ng s&acirc;u sắc trong đời sống tinh thần người Khmer. Thể hiện kh&aacute;t vọng v&agrave; t&acirc;m hồn người Khmer, thể hiện sự biết ơn của người Khmer với đấng bề tr&ecirc;n. Việc giữ truyền thống tổ chức lễ hội Ok Om Bok cũng g&oacute;p phần bảo tồn, ph&aacute;t huy n&eacute;t đẹp văn h&oacute;a gi&agrave;u t&iacute;nh nh&acirc;n văn của người Khmer Nam Bộ. Kh&ocirc;ng chỉ vậy, lễ hội c&ograve;n l&agrave; một sản phẩm du lịch thu h&uacute;t du kh&aacute;ch đến với v&ugrave;ng đất n&agrave;y.&nbsp;</p>\r\n', 7, 0, 1),
(11, 'Đà Nẵng sắp mở tuyến phố đi bộ kết hợp chợ đêm', 1, '1540527062.du-lich-da-nang_1540365374.jpg', '<p>Ng&agrave;y 23/10, Sở Du lịch TP Đ&agrave; Nẵng th&ocirc;ng tin về việc tr&igrave;nh UBND th&agrave;nh phố xem x&eacute;t mở tuyến phố đi bộ tr&ecirc;n đường H&ugrave;ng Vương. Tuyến đường sầm uất thường xuy&ecirc;n đ&oacute;n lượng kh&aacute;ch lớn đến tham quan v&agrave; mua sắm.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đường H&ugrave;ng Vương song song với nhiều con đường s&ocirc;i động của Đ&agrave; Nẵng như đường L&ecirc; Duẩn, Nguyễn Văn Linh. Đ&acirc;y được xem l&agrave; mạch giao th&ocirc;ng xuy&ecirc;n suốt, kết nối quận Hải Ch&acirc;u - trung t&acirc;m kinh tế, ch&iacute;nh trị, văn h&oacute;a của to&agrave;n th&agrave;nh phố với c&aacute;c khu vực l&acirc;n cận.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Tuyến giao th&ocirc;ng n&agrave;y cũng gắn liền với c&aacute;c hoạt động thương mại. Chợ H&agrave;n v&agrave; chợ Cồn l&agrave; hai khu chợ nhộn nhịp bậc nhất nơi đ&acirc;y. Ngo&agrave;i ra, c&aacute;c c&ocirc;ng tr&igrave;nh hiện đại như si&ecirc;u thị Big C, nh&agrave; h&aacute;t Trưng Vương v&agrave; quảng trường cũng nằm trong khu vực.</p>\r\n\r\n<p><img alt=\"Chợ Cồn, thiên đường ăn vặt nổi tiếng của Đà Nẵng. Ảnh: Kiểu Sơn.\" src=\"https://www.dulichvietnam.com.vn/data/image/0hoi-cho-du-lich/tau%20thong%20nhat/cho-con-da-nang.jpg\" style=\"height:480px; width:800px\" /><br />\r\nChợ Cồn, thi&ecirc;n đường ăn vặt nổi tiếng của Đ&agrave; Nẵng. Ảnh: Kiểu Sơn.<br />\r\n&nbsp;</p>\r\n\r\n<p>Theo Sở Du lịch Đ&agrave; Nẵng, những khu xung quanh như phố ẩm thực đ&ecirc;m tr&ecirc;n đường Phạm Hồng Th&aacute;i, &ldquo;con đường c&agrave; ph&ecirc;&rdquo; Y&ecirc;n B&aacute;i hay nh&agrave; thờ ch&iacute;nh t&ograve;a Đ&agrave; Nẵng tr&ecirc;n đường Trần Ph&uacute; cũng sẽ hỗ trợ cho việc h&igrave;nh th&agrave;nh khu phố đi bộ.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Đầu th&aacute;ng 9 năm nay, th&agrave;nh phố Đ&agrave; Nẵng cũng đ&atilde; mở chợ đ&ecirc;m kết hợp phố đi bộ tại đoạn đường L&yacute; Nam Đế - Mai Hắc Đế thuộc phường An Hải T&acirc;y, quận Sơn Tr&agrave;.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Theo l&atilde;nh đạo Sở Du lịch Đ&agrave; Nẵng, l&acirc;u nay, hoạt động vui chơi giải tr&iacute; về đ&ecirc;m của Đ&agrave; Nẵng c&ograve;n hạn chế. Th&agrave;nh phố sẽ ưu ti&ecirc;n bố tr&iacute; c&aacute;c h&agrave;ng h&oacute;a, sản phẩm của địa phương, xen kẽ c&aacute;c dịch vụ ăn uống, giải tr&iacute; ở khu phố đi bộ, chợ v&agrave;o buổi tối.</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>Trong 10 th&aacute;ng năm 2018, tổng kh&aacute;ch du lịch đến Đ&agrave; Nẵng ước đạt hơn 6,8 triệu lượt, tăng 21,5% so với c&ugrave;ng kỳ 2017. Trong đ&oacute;, lượng kh&aacute;ch quốc tế hơn 2,5 triệu lượt, tăng 31% so với c&ugrave;ng kỳ 2017. Số kh&aacute;ch nội địa hơn 4,3 triệu lượt, tăng 16,7% so với c&ugrave;ng kỳ 2017. Tổng thu du lịch ước đạt 23.661 tỷ đồng, tăng 43% so với c&ugrave;ng kỳ năm 2017.</p>\r\n', 0, 1, 1),
(8, 'Ngắm hoa tím đẹp lung linh trên đỉnh Tà Chì Nhù ở Yên Bái', 1, '1541406536.cotim1.jpg', '<p><strong>Kh&ocirc;ng phải trong phim, cũng kh&ocirc;ng phải trong những khung h&igrave;nh mộng mơ ở v&ugrave;ng thảo nguy&ecirc;n M&ocirc;ng Cổ hay Ch&acirc;u &Acirc;u n&agrave;o đ&oacute; m&agrave; c&oacute; cả một v&ugrave;ng đồi hoa t&iacute;m đẹp &ldquo;lịm tim&rdquo; nằm dọc tr&ecirc;n qu&atilde;ng đường chinh phục T&agrave; Ch&igrave; Nh&ugrave; - đỉnh n&uacute;i cao thứ 6 của Việt Nam.</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/__thumbs/cotim1.jpg/cotim1__600x338.jpg\" style=\"height:338px; width:600px\" /></strong></p>\r\n\r\n<p>T&agrave; Ch&igrave; Nh&ugrave;, c&ograve;n gọi l&agrave; Phu Song Sung theo c&aacute;ch gọi của người d&acirc;n tộc Th&aacute;i hay Chung Chua Nh&agrave; theo c&aacute;ch gọi của người d&acirc;n tộc M&ocirc;ng. Nếu x&eacute;t về độ cao th&igrave; đ&acirc;y&nbsp;l&agrave; đỉnh n&uacute;i nằm trong top 10 ngọn n&uacute;i cao nhất Việt Nam, đứng ở vị tr&iacute; thứ 6&nbsp;với độ cao 2.979m trong địa b&agrave;n huyện Trạm Tấu, tỉnh Y&ecirc;n B&aacute;i. C&ograve;n nếu&nbsp;x&eacute;t về độ kh&oacute; th&igrave; T&agrave; Ch&igrave; Nh&ugrave; được xếp v&agrave;o h&agrave;ng đầu&nbsp;bởi đường đi đa số l&agrave; dốc cao, &iacute;t b&oacute;ng c&acirc;y, đ&aacute; sỏi nhiều.</p>\r\n\r\n<p>Th&ocirc;ng thường, T&agrave; Ch&igrave; Nh&ugrave; nổi tiếng l&agrave; một ngọn đồi trọc chỉ ph&ugrave; hợp để săn m&acirc;y. &Iacute;t ai biết, v&agrave;o cuối thu, khoảng&nbsp;từ đầu đến cuối th&aacute;ng 10, những b&ocirc;ng hoa t&iacute;m ẩn m&igrave;nh bấy l&acirc;u bỗng nở bung biến T&agrave; Ch&igrave; Nh&ugrave; th&agrave;nh ngọn đồi m&agrave;u t&iacute;m, một kh&ocirc;ng gian cực kỳ tuyệt vời cho những bức ảnh sống ảo đẹp ngỡ ng&agrave;ng kh&ocirc;ng thua k&eacute;m g&igrave; những c&aacute;nh đồng violet ở trời &Acirc;u.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/cotim2.jpg\" style=\"height:250px; width:600px\" /></p>\r\n\r\n<p>Chưa bao giờ bạn cảm thấy m&acirc;y v&agrave; trời gần đến thế, như thể&nbsp;chỉ cần với tay l&agrave; c&oacute; thể chạm được m&acirc;y. Những &aacute;ng m&acirc;y l&uacute;c n&agrave;y kh&ocirc;ng ở tr&ecirc;n đầu m&agrave; nằm ngang tầm mắt, khi th&igrave; lập lờ, mềm mại như dải lụa, l&uacute;c lại b&ocirc;ng tơi, trắng x&oacute;a như kem b&ocirc;ng. Theo đường chạy mặt trời những đ&aacute;m m&acirc;y hiện l&ecirc;n r&otilde; n&eacute;t, tạo n&ecirc;n một khung cảnh thi&ecirc;n nhi&ecirc;n tuyệt đẹp./.</p>\r\n', 1, 0, 1),
(9, 'Đà Nẵng - Điểm hẹn của hạnh phúc', 1, '1541407102.danangdiemhen.jpg', '<p><strong>Đ&agrave; Nẵng thật đẹp, thật lộng lẫy trong từng shoot h&igrave;nh v&agrave; xứng đ&aacute;ng cho sự lựa chọn của kh&ocirc;ng &iacute;t đ&ocirc;i lứa trong ng&agrave;y hạnh ph&uacute;c của m&igrave;nh.</strong></p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/__thumbs/danangdiemhen.jpg/danangdiemhen__480x282.jpg\" style=\"height:282px; width:479px\" /></p>\r\n\r\n<p>Những yếu tố tự nhi&ecirc;n với b&atilde;i biển c&aacute;t trắng nắng v&agrave;ng, hệ sinh th&aacute;i hoang sơ tr&ecirc;n b&aacute;n đảo Sơn Tr&agrave; c&ugrave;ng với những điểm đến hấp dẫn kh&aacute;c đ&atilde; được c&aacute;c bạn trẻ khai th&aacute;c tối đa l&agrave;m bối cảnh cho bộ ảnh cưới của m&igrave;nh. Đ&acirc;u đ&acirc;u cũng bắt gặp h&igrave;nh ảnh c&aacute;c cặp đ&ocirc;i đang thực hiện bộ ảnh cưới, đ&aacute;nh dấu cột mốc v&agrave;ng son để họ ch&iacute;nh thức th&agrave;nh vợ, th&agrave;nh chồng.</p>\r\n\r\n<p>Li&ecirc;n tục được b&igrave;nh chọn l&agrave; một trong những điểm đến hấp dẫn nhất h&agrave;nh tinh, top 10 điểm du lịch mới, top những th&agrave;nh phố du lịch n&ecirc;n đến nhất thế giới, c&ugrave;ng với thi&ecirc;n nhi&ecirc;n ưu đ&atilde;i, Đ&agrave; Nẵng đ&atilde; khẳng định được vị tr&iacute; của m&igrave;nh so với những điểm du lịch trong nước. Chỉ ri&ecirc;ng những điểm cộng đ&oacute; cũng đủ th&ocirc;i th&uacute;c c&aacute;c cặp đ&ocirc;i nung nấu &yacute; tưởng thực hiện bộ ảnh cưới tại th&agrave;nh phố xinh đẹp n&agrave;y.</p>\r\n\r\n<p>Kh&ocirc;ng những thế, h&agrave;ng loạt c&aacute;c triển l&atilde;m cưới cũng chọn Đ&agrave; Nẵng triển khai. Cũng bởi l&yacute; do Đ&agrave; Nẵng đang dần trở th&agrave;nh điểm đến l&yacute; tưởng để c&aacute;c đ&ocirc;i uy&ecirc;n ương thực hiện album cưới. Tại đ&acirc;y, họ được trải nghiệm kh&ocirc;ng gian tiệc cưới, c&aacute;c gian h&agrave;ng triển l&atilde;m của c&aacute;c thương hiệu v&aacute;y v&agrave; trang sức nổi tiếng, được thử l&agrave;m c&ocirc; d&acirc;u v&agrave; c&aacute;i ch&iacute;nh l&agrave; để c&aacute;c cặp đ&ocirc;i c&oacute; dịp kết nối với c&aacute;c thương hiệu cưới cao cấp, c&oacute; cơ hội chuẩn bị tốt hơn cho ng&agrave;y trọng đại của m&igrave;nh.</p>\r\n\r\n<p>Hiện nay, ngo&agrave;i những điểm quen thuộc, c&aacute;c bạn trẻ c&ograve;n săn l&ugrave;ng nhiều địa điểm hấp dẫn kh&aacute;c. Thậm ch&iacute; c&oacute; bạn c&ograve;n đưa ra xu hướng lựa m&ugrave;a xu&acirc;n hạ thu đ&ocirc;ng để chụp ảnh cưới. Song, chung quy lại, nếu đến với Đ&agrave; Nẵng, bạn muốn s&ocirc;ng nước hay n&uacute;i rừng, n&ocirc;ng th&ocirc;n hay th&agrave;nh thị, hiện đại hay cổ xưa đều đ&aacute;p ứng bối cảnh để thực hiện trọn vẹn bộ ảnh cưới kỷ niệm cho m&igrave;nh. V&agrave; c&oacute; lẽ Đ&agrave; Nẵng dần x&acirc;y dựng th&ecirc;m cho m&igrave;nh thương hiệu th&agrave;nh phố của thi&ecirc;n đường ảnh cưới để l&agrave; điểm hẹn cho hạnh ph&uacute;c lứa đ&ocirc;i./.&nbsp;&nbsp;</p>\r\n', 0, 0, 1),
(10, 'Đến Mộc Châu khám phá Điểm hẹn hồ Bản Mòn - Homestay và Ẩm thực Tây Bắc', 1, '1541407665khamphadienhenbanhotaybac.jpg', '<p>Bạn v&agrave; gia đ&igrave;nh c&oacute; &yacute; định du lịch Mộc Ch&acirc;u v&agrave; đang ph&acirc;n v&acirc;n kh&ocirc;ng biết ăn ngủ nghỉ ở đ&acirc;u? Điểm hẹn hồ Bản M&ograve;n - Homestay &amp; Ẩm thực T&acirc;y Bắc chắc chắn sẽ l&agrave; lựa chọn tốt của du kh&aacute;ch. &nbsp;</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/khamphadienhenbanhotaybac.jpg\" style=\"height:400px; width:680px\" /></p>\r\n\r\n<p>Nằm tại ng&atilde; 3 bản M&ograve;n, ngay gần quốc lộ 6&nbsp;Điểm hẹn hồ Bản M&ograve;n - Homestay &amp; Ẩm thực T&acirc;y Bắc&nbsp;rất tiện cho việc di chuyển của du kh&aacute;ch tới c&aacute;c điểm tham quan tại thị trấn Mộc Ch&acirc;u như hồ Rừng th&ocirc;ng bản &Aacute;ng, bia tưởng niệm T&acirc;y Tiến, vườn hoa Happy Land, th&aacute;c Dải Yếm v&agrave; c&aacute;c điểm tham quan kh&aacute;c ở thị trấn N&ocirc;ng trường như đồi ch&egrave;, trang trại b&ograve; sữa Mộc Ch&acirc;u&hellip;</p>\r\n\r\n<p>Đến với&nbsp;Điểm hẹn hồ Bản M&ograve;n - Homestay &amp; Ẩm thực T&acirc;y Bắc,&nbsp;du kh&aacute;ch sẽ được h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n nhi&ecirc;n, sinh th&aacute;i với nh&agrave; s&agrave;n gỗ, v&agrave;o vườn c&acirc;y h&aacute;i quả, hay ra ao c&acirc;u c&aacute;&hellip; 1 homestay chất lượng&nbsp; với đầy đủ tiện nghi, rộng r&atilde;i, tho&aacute;ng m&aacute;t, vệ sinh sạch sẽ, c&oacute; b&atilde;i đỗ xe v&agrave; s&acirc;n khấu rộng để phục vụ giao lưu văn nghệ. Với kh&ocirc;ng gian trong l&agrave;nh, y&ecirc;n tĩnh sẽ mang lại cho du kh&aacute;ch 1 kỳ nghỉ dưỡng thật &yacute; nghĩa v&agrave; đong đầy cảm x&uacute;c b&ecirc;n gia đ&igrave;nh c&ugrave;ng người th&acirc;n.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/__thumbs/khamphadienhenbanhotaybac1.jpg/khamphadienhenbanhotaybac1__360x480.jpg\" style=\"height:480px; width:360px\" /></p>\r\n\r\n<p>Kh&ocirc;ng chỉ l&agrave; 1 khu nghỉ dưỡng&nbsp;Điểm hẹn hồ Bản M&ograve;n - Homestay &amp; Ẩm thực T&acirc;y Bắc&nbsp;c&ograve;n c&oacute; đội đầu bếp chuy&ecirc;n nghiệp phục vụ du kh&aacute;ch với những m&oacute;n ăn đặc sản, mang đậm bản sắc của b&agrave; con đồng b&agrave;o d&acirc;n tộc Th&aacute;i v&agrave; đặc biệt l&agrave; những m&oacute;n gỏi c&aacute; rất hấp dẫn.</p>\r\n\r\n<p>Ngo&agrave;i ra, tại&nbsp;Điểm hẹn hồ Bản M&ograve;n - Homestay &amp; Ẩm thực T&acirc;y Bắc cũng c&oacute; 1 kh&ocirc;ng gian đang rất hot với c&aacute;c bạn trẻ địa phương, cũng như du kh&aacute;ch khi tới đ&acirc;y đ&oacute; ch&iacute;nh l&agrave; c&acirc;y cầu v&agrave; m&oacute;c kh&oacute;a t&igrave;nh y&ecirc;u. Đ&acirc;y ch&iacute;nh l&agrave; địa điểm l&yacute; tưởng d&agrave;nh cho c&aacute;c cặp đ&ocirc;i đang y&ecirc;u nhau muốn b&agrave;y tỏ t&igrave;nh cảm v&agrave; hướng tới 1 t&igrave;nh y&ecirc;u vĩnh cửu.</p>\r\n\r\n<p>H&atilde;y đến với&nbsp;&nbsp;Điểm hẹn hồ Bản M&ograve;n - Homestay &amp; Ẩm thực T&acirc;y Bắc để được tận hưởng những dịch vụ tốt nhất./</p>\r\n', 0, 0, 1),
(47, 'Thác Voi – thác nước đẹp hùng vĩ của Lâm Đồng', 1, '1541407646.thacvoi2JPG.jpg', '<p><strong>(TITC) - C&aacute;ch trung t&acirc;m th&agrave;nh phố Đ&agrave; Lạt khoảng 25 km về hướng T&acirc;y Nam, th&aacute;c Voi nằm ở thị trấn Nam Ban, huyện L&acirc;m H&agrave;, tỉnh L&acirc;m Đồng. Th&aacute;c Voi l&agrave; một trong những th&aacute;c nước đẹp của n&uacute;i rừng T&acirc;y Nguy&ecirc;n h&ugrave;ng vĩ với chiều cao hơn 30m.&nbsp;</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/thacvoi1JPG.jpg\" style=\"height:400px; width:680px\" /></strong></p>\r\n\r\n<p>Điều l&yacute; th&uacute; khi đến th&aacute;c Voi l&agrave; kh&aacute;ch kh&ocirc;ng phải chinh phục đỉnh th&aacute;c m&agrave; l&agrave; chinh phục... ch&acirc;n th&aacute;c.&nbsp;Muốn chi&ecirc;m ngưỡng đầy đủ vẻ đẹp của th&aacute;c, du kh&aacute;ch qua hơn 140 bậc tam cấp v&ograve;ng v&egrave;o v&agrave; lắm gian nan, mỗi b&acirc;c l&agrave; một g&oacute;c độ, l&uacute;c l&agrave; c&aacute;c tấm v&aacute;n của chiếc cầu gỗ , khi l&agrave; bậc đ&aacute; thi&ecirc;n tạo. Khi xuống đến ch&acirc;n th&aacute;c, du kh&aacute;ch sẽ tận hưởng những d&ograve;ng th&aacute;c trắng x&oacute;a đang tung bụi nước mịt m&ugrave;, nghe tiếng th&aacute;c đổ ầm ầm tưởng như một đ&agrave;n voi chạy đua hoặc tung v&ograve;i phun nước đ&ugrave;a nghịch với nhau.&nbsp;Điều đặc biệt, kh&ocirc;ng như những suối th&aacute;c kh&aacute;c, v&agrave;o m&ugrave;a kh&ocirc;, lượng nước ở th&aacute;c Voi kh&ocirc;ng hề thuy&ecirc;n giảm, n&oacute; vẫn ng&agrave;y đ&ecirc;m ầm ầm tu&ocirc;n đổ v&agrave; l&agrave; nguồn thuỷ lợi ch&iacute;nh cho c&aacute;c bu&ocirc;n l&agrave;ng.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/thacvoi2JPG.jpg\" style=\"height:378px; width:567px\" /></p>\r\n\r\n<p>Ấn tượng l&agrave; những khối đ&aacute; l&ocirc; nh&ocirc; nơi ch&acirc;n th&aacute;c cũng tạo ra những hang động l&agrave; nơi tr&uacute; ngụ của những đ&agrave;n dơi lớn, c&oacute; hang s&acirc;u đến 50m với v&aacute;ch đ&aacute; sừng sững c&oacute; c&aacute;c h&igrave;nh th&uacute;, m&agrave;u sắc rất lạ mắt&hellip;&nbsp;Th&aacute;c Voi l&agrave; như vậy đ&oacute;, th&aacute;c c&oacute; một vẻ đẹp hoang sơ kỳ vĩ như ch&iacute;nh những tr&ugrave;ng điệp của v&ugrave;ng n&uacute;i cao nguy&ecirc;n./</p>\r\n\r\n<p>&nbsp;</p>\r\n\r\n<p>&nbsp;</p>\r\n', 0, 0, 1),
(48, 'Lung linh Hòn Yến – Phú Yên', 1, '1541407855.lunglinhdaoyen1.jpg', '<p><strong>H&ograve;n Yến - thuộc th&ocirc;n Nhơn Hội, x&atilde; An H&ograve;a (huyện Tuy An) l&agrave; một quần thể gồm c&aacute;c h&ograve;n đảo nhỏ nằm gần bờ, c&aacute;c b&atilde;i c&aacute;t, g&agrave;nh đ&aacute; với mu&ocirc;n ng&agrave;n vi&ecirc;n đ&aacute; tr&ograve;n lớn, nhỏ nằm xen kẽ với san h&ocirc;. Ng&agrave;y biển cạn, nước biển l&ugrave;i ra xa, lộ ra một b&atilde;i đ&aacute; rộng c&oacute; thể lội từ trong bờ ra tận H&ograve;n Đụn v&agrave; H&ograve;n Yến.</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/lunglinhdaoyen1.jpg\" style=\"height:400px; width:680px\" /></strong></p>\r\n\r\n<p>H&ograve;n Yến c&oacute; h&igrave;nh ch&oacute;p vung, được cấu tạo bởi c&aacute;c khối đ&aacute; bazan h&igrave;nh trụ hoặc h&igrave;nh lục gi&aacute;c gh&eacute;p liền nhau nhỏ dần từ dưới l&ecirc;n. H&ograve;n Đụn c&oacute; cấu tạo của một khối đ&aacute; m&agrave;u đỏ n&uacute;i lửa. S&oacute;ng vỗ quanh năm đ&atilde; khiến cho khối đ&aacute; bị x&acirc;m thực nhiều chỗ, tạo n&ecirc;n những hang, những l&otilde;m c&oacute; h&igrave;nh d&aacute;ng độc đ&aacute;o. Dưới ch&acirc;n H&ograve;n Đụn, nước biển luồn s&acirc;u v&agrave;o b&ecirc;n trong, chuyển động theo chiều l&ecirc;n xuống tạo n&ecirc;n những &acirc;m thanh nghe &oacute;c &aacute;ch, &oacute;c &aacute;ch&hellip; C&aacute;c lo&agrave;i hải sản quần tụ nơi đ&acirc;y rất đa dạng. Phong ph&uacute; nhất l&agrave; c&aacute;c lo&agrave;i san h&ocirc;. Kh&ocirc;ng cần phải l&ecirc;n thuyền hoặc l&ecirc;n t&agrave;u đ&aacute;y k&iacute;nh, ng&agrave;y biển cạn, du kh&aacute;ch c&oacute; thể đứng tr&ecirc;n g&agrave;nh hoặc thỏa th&iacute;ch lội xuống nước ngắm san h&ocirc; v&agrave; th&ograve; tay bắt những ch&uacute; c&aacute; con tung tăng lội trong những hốc nước nhỏ.&nbsp;</p>\r\n\r\n<p>Đ&ocirc;ng đảo du kh&aacute;ch đ&atilde; t&igrave;m đến đ&acirc;y, nhất l&agrave; v&agrave;o những ng&agrave;y đầu th&aacute;ng v&agrave; giữa th&aacute;ng &acirc;m lịch khi thủy triều xuống. L&uacute;c n&agrave;y cả một v&ugrave;ng san h&ocirc; rộng lớn nổi bật l&ecirc;n tr&ecirc;n mặt nước lung linh những m&agrave;u sắc v&agrave; h&igrave;nh d&aacute;ng độc đ&aacute;o./.</p>\r\n', 0, 0, 1),
(49, 'Ngắm nét bình yên, thơ mộng của sông Hương', 1, '1541417132.netbinhyendongsongxuhue2.jpg', '<p><strong>S&ocirc;ng Hương từng l&agrave; nguồn cảm x&uacute;c của du kh&aacute;ch khi đi thuyền dọc theo d&ograve;ng s&ocirc;ng để nh&igrave;n ngắm phong cảnh v&agrave; lắng nghe những điệu ca Huế truyền thống. Từ xa xưa, d&ograve;ng Hương giang &ecirc;m đềm đ&atilde; tạo n&ecirc;n những cảm hứng cho c&aacute;c t&aacute;c giả, nhất l&agrave; thi sĩ v&agrave; nhạc sĩ.</strong></p>\r\n\r\n<p><strong>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/hinh/netbinhyendongsongxuhue.jpg\" style=\"height:345px; width:460px\" /></strong></p>\r\n\r\n<p>S&ocirc;ng Hương (Hương giang) l&agrave; con s&ocirc;ng chảy qua th&agrave;nh phố Huế v&agrave; c&aacute;c huyện, thị x&atilde; Hương Tr&agrave;, Hương Thủy, Ph&uacute; Vang (tỉnh Thừa Thi&ecirc;n-Huế).</p>\r\n\r\n<p>D&ograve;ng ch&iacute;nh của Tả Trạch d&agrave;i khoảng 67 km, bắt nguồn từ d&atilde;y Trường Sơn Đ&ocirc;ng, ven khu vực vườn quốc gia Bạch M&atilde; chảy theo hướng T&acirc;y Bắc với 55 th&aacute;c nước h&ugrave;ng vĩ, qua thị trấn Nam Đ&ocirc;ng rồi sau đ&oacute; hợp lưu với d&ograve;ng Hữu Trạch tại ng&atilde; ba Bằng L&atilde;ng (khoảng 3 km về ph&iacute;a Bắc khu vực lăng Minh Mạng).</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/hinh/netbinhyendongsongxuhue1.jpg\" style=\"height:232px; width:460px\" /></p>\r\n\r\n<p>Hữu Trạch d&agrave;i khoảng 60 km l&agrave; nh&aacute;nh phụ, chảy theo hướng Bắc, qua 14 th&aacute;c v&agrave; vượt qua ph&agrave; Tuần để tới ng&atilde; ba Bằng L&atilde;ng, nơi hai d&ograve;ng n&agrave;y gặp nhau v&agrave; tạo n&ecirc;n s&ocirc;ng Hương.</p>\r\n\r\n<p>S&ocirc;ng Hương rất đẹp khi chi&ecirc;m ngưỡng n&oacute; từ nguồn v&agrave; khi n&oacute; chảy quanh c&aacute;c ch&acirc;n n&uacute;i, xuy&ecirc;n qua c&aacute;c c&aacute;nh rừng rậm của hệ thực vật nhiệt đới.</p>\r\n\r\n<p>Con s&ocirc;ng chảy chậm qua c&aacute;c l&agrave;ng mạc như Kim Long, Nguyệt Biều, Vỹ Dạ, Đ&ocirc;ng Ba, Gia Hội, chợ Dinh, Nam Phổ, Bao Vinh.</p>\r\n\r\n<p>C&aacute;c c&ocirc;ng tr&igrave;nh kiến tr&uacute;c hai b&ecirc;n bờ s&ocirc;ng gồm th&agrave;nh qu&aacute;ch, thị tứ, vườn tược, ch&ugrave;a chiền, th&aacute;p v&agrave; đền đ&agrave;i... &aacute;nh phản chiếu của ch&uacute;ng tr&ecirc;n d&ograve;ng nước khiến con s&ocirc;ng mang theo nhiều chất thơ v&agrave; t&iacute;nh nhạc.</p>\r\n\r\n<p>&nbsp; &nbsp; &nbsp; &nbsp; &nbsp;&nbsp;<img alt=\"\" src=\"/banhkeo/public/uploads/images/hinh/netbinhyendongsongxuhue2.jpg\" style=\"height:400px; width:680px\" /></p>\r\n\r\n<p>Nhiều người lu&ocirc;n gắn liền sự thanh b&igrave;nh, thanh lịch v&agrave; cảnh vật đẹp đẽ của Huế với d&ograve;ng S&ocirc;ng Hương. Hằng năm, v&agrave;o m&ugrave;a lụt, nước s&ocirc;ng Hương d&acirc;ng cao c&oacute; thể g&acirc;y ngập &uacute;ng cho th&agrave;nh phố Huế v&agrave; c&aacute;c v&ugrave;ng l&acirc;n cận.</p>\r\n\r\n<p>Nhưng nhờ ph&ugrave; sa sau mỗi trận lụt, c&aacute;c miệt vườn như Nguyệt Biểu với đặc sản l&agrave; quả Thanh Tr&agrave;; Kim Long với măng cụt, c&aacute;c triền ven s&ocirc;ng với bắp... sẽ tốt tươi hơn. D&ograve;ng s&ocirc;ng như một dải lụa đ&agrave;o trải d&agrave;i tr&ecirc;n v&ugrave;ng đất Huế./.</p>\r\n', 0, 0, 1);

